import Emitter from 'events';
import { StatusCodes, ClientSession } from 'node-opcua';
import Logger from 'gulplog';
import Client from './Client';
import ProjectConfig from '../../config/ProjectConfig';

const emitter = new Emitter();
const openSessions = [];
let openingSessions = 0;

/**
 * A wrapper around {@link node-opcua~ClientSession} used to connect to atvise server.
 */
export default class Session {

  /**
   * An {@link events~Emitter} that emits events when creating / closing sessions.
   * @type {events~Emitter}
   */
  static get emitter() {
    return emitter;
  }

  /**
   * Creates and opens a new {@link node-opcua~ClientSession}.
   * @return {Promise<node-opcua~ClientSession, Error>} Fulfilled with an already opened
   * {@link node-opcua~ClientSession}.
   * @emits {undefined} Emits an `all-open` event once all opening sessions are open.
   */
  static create() {
    openingSessions++;

    return Client.create()
      .then(client => new Promise((resolve, reject) => {
        client.createSession({
          userName: ProjectConfig.login.username,
          password: ProjectConfig.login.password,
        }, (err, session) => {
          if (err) {
            if (
              [
                'userName === null || typeof userName === "string"',
                'password === null || typeof password === "string"',
              ].includes(err.message) ||
              (err.response &&
              err.response.responseHeader.serviceResult === StatusCodes.BadUserAccessDenied)
            ) {
              reject(new Error('Unable to create session: Invalid login'));
            } else {
              reject(new Error(`Unable to create session: ${err.message}`));
            }
          } else {
            Object.assign(session, { _emitter: new Emitter() });

            openSessions.push(session);
            resolve(session);
          }

          openingSessions--;
          if (openingSessions === 0) {
            emitter.emit('all-open');
          }
        });
      }));
  }

  /**
   * Closes the given session.
   * @param {node-opcua~ClientSession} session The session to close.
   * @param {Boolean} [deleteSubscriptions=true] If active subscriptions should be closed as well.
   * @return {Promise<node-opcua~ClientSession, Error>} Fulfilled with the (now closed!) session or
   * rejected with the error that occured while closing.
   */
  static close(session, deleteSubscriptions = true) {
    if (!session || !(session instanceof ClientSession)) {
      return Promise.reject(new Error('session is required'));
    }

    if (session._closed) {
      return Promise.resolve();
    }

    if (session._closing) {
      return new Promise(resolve => {
        session._emitter.on('fully-closed', () => resolve(session));
      });
    }

    Object.assign(session, { _closing: true });

    return new Promise((resolve, reject) => {
      function markAsClosed() {
        openSessions.splice(openSessions.indexOf(session), 1);
        Object.assign(session, { _closed: true });

        resolve(session);
        session._emitter.emit('fully-closed');
      }

      session.close(deleteSubscriptions, err => {
        if (err) {
          if (err.response &&
            err.response.responseHeader.serviceResult === StatusCodes.BadSessionIdInvalid
          ) {
            Logger.debug('Attempted to close a session that does not exist');
            markAsClosed(session);
          } else if (err.message === 'no channel') {
            // Client already disconnected
            markAsClosed(session);
          } else {
            reject(new Error(`Unable to close session: ${err.message}`));
          }
        } else {
          session._client.disconnect(clientErr => {
            if (clientErr) {
              reject(new Error(`Unable to disconnect client: ${clientErr.message}`));
            } else {
              markAsClosed(session);
            }
          });
        }
      });
    });
  }

  /**
   * The sessions currently open.
   * @type {Session[]}
   */
  static get open() {
    return openSessions;
  }

  /**
   * Closes all open sessions.
   * @return {Promise<Error, Session[]>} Rejected with the error that occurred while closing the
   * sessions or fulfilled with the (now closed) sessions affected.
   */
  static closeOpen() {
    function closeSessions(sessions) {
      return Promise.all(
        sessions.map(session => Session.close(session))
      );
    }

    if (openingSessions === 0) {
      if (openSessions.length === 0) {
        return Promise.resolve([]);
      }

      return closeSessions(openSessions);
    }

    return new Promise((resolve, reject) => {
      emitter.once('all-open', () => {
        closeSessions(openSessions)
          .then(resolve, reject);
      });
    });
  }

}

import { ctor as throughStreamClass } from 'through2';
import Session from './Session';

/**
 * An object transform stream connected to atvise server.
 */
export default class Stream extends throughStreamClass({ objectMode: true }) {

  /**
   * Creates a new Stream and starts opening a new session to atvise server.
   * @param {Object} [options] The options to use. See
   * [through2 documentation](https://github.com/rvagg/through2#options) for details.
   * @param {Boolean} [options.keepSessionAlive=false] If the ativse server session should be closed
   * one the stream ends.
   * @emits {Session} Emits an `session-open` event once the session is open, passing the Session
   * instance.
   */
  constructor(options = {}) {
    super(options);

    /**
     * `true` if the stream's atvise server session should be kept alive once the stream ends.
     * @type {Boolean}
     */
    this._keepSessionAlive = options.keepSessionAlive || false;

    Session.create()
      .then(session => (this.session = session))
      .then(session => this.emit('session-open', session))
      .catch(err => this.emit('error', err));
  }

  /**
   * Called just before the stream is closed: Closes the open session.
   * @param {function(err: ?Error, data: Object)} callback Called once the session is closed.
   */
  _flush(callback) {
    if (this.session && !this._keepSessionAlive) {
      Session.close(this.session)
        .then(() => callback())
        .catch(err => callback(err));
    } else {
      callback();
    }
  }

}

import { DataType } from 'node-opcua';
import NodeId from '../server/NodeId';

class AtviseType {

  constructor(nodeIdValue, identifier, dataType, fileExtensionOrKeep) {
    this.typeDefinition = new NodeId(`VariableTypes.ATVISE.${nodeIdValue}`);
    this.identifier = identifier;
    this.dataType = dataType;
    if (fileExtensionOrKeep !== undefined) {
      if (typeof fileExtensionOrKeep === 'string') {
        this.fileExtension = fileExtensionOrKeep;
      } else {
        this.keepExtension = fileExtensionOrKeep;
      }
    }
  }

}

class AtviseResourceType extends AtviseType {

  constructor(name, identifier) {
    super(`Resource.${name}`, identifier, DataType.ByteString, true);
  }

}

/**
 * The atvise types to handle. **Ordering matters:** The {@link MappingTransformer} takes the first
 * match, therefore **plain types should always come before resource types!**
 * @type {AtviseType[]}
 */
const AtviseTypes = [
  new AtviseType('HtmlHelp', 'help', DataType.ByteString, 'html'),
  new AtviseType('QuickDynamic', 'qd', DataType.XmlElement),
  new AtviseType('ScriptCode', 'script', DataType.XmlElement),
  new AtviseType('Display', 'display', DataType.XmlElement),
  new AtviseType('TranslationTable', 'locs', DataType.XmlElement),
  new AtviseResourceType('Pdf', 'pdf'),
  new AtviseResourceType('Html', 'html'),
  new AtviseResourceType('Javascript', 'js'),
  new AtviseResourceType('Wave', 'wav'),
  new AtviseResourceType('Gif', 'gif'),
  new AtviseResourceType('Png', 'png'),
  new AtviseResourceType('Aac', 'm4a'),
  new AtviseResourceType('Ogg', 'ogg'),
  new AtviseResourceType('Icon', 'ico'),
  new AtviseResourceType('Css', 'css'),
  new AtviseResourceType('Svg', 'svg'),
  new AtviseResourceType('Jpeg', 'jpg'),
  new AtviseResourceType('OctetStream', '*'),
];

export default AtviseTypes;

import Emitter from 'events';
import {
  ClientSubscription,
  AttributeIds,
  subscription_service as SubscriptionService,
  StatusCodes,
} from 'node-opcua';
import ProjectConfig from '../../config/ProjectConfig';
import NodeStream from './NodeStream';
import QueueStream from './QueueStream';
import Session from './Session';

/**
 * A stream that monitors changes in the read nodes.
 */
export class SubscribeStream extends QueueStream {

  /**
   * Creates a new SubscribeStream based on some options.
   * @param {Object} options The stream options to apply.
   */
  constructor(options = {}) {
    super(Object.assign(options, { keepSessionAlive: true })); // FIXME: Option not handled.

    /**
     * Set to true once all nodes are monitored.
     * @type {Boolean}
     */
    this._trackChanges = false;

    this.once('session-open', () => this.createSubscription());
  }

  /**
   * Creates the server subscription to monitor nodes with.
   * @emit {node-opcua~ClientSubscription} Emits a `subscription-started` event once the
   * subscription started.
   */
  createSubscription() {
    const subscription = new ClientSubscription(this.session, {
      requestedPublishingInterval: 100,
      requestedLifetimeCount: 1000,
      requestedMaxKeepAliveCount: 12,
      maxNotificationsPerPublish: 10,
      publishingEnabled: true,
      priority: 10,
    });

    subscription.on('started', () => {
      /**
       * The {@link node-opcua~ClientSubscription} to use to monitor nodes.
       * @type {node-opcua~ClientSubscription}
       */
      this.subscription = subscription;
      this.emit('subscription-started', subscription);
    });

    subscription.on('failure', err => this.emit('error', err));
  }

  /**
   * Returns an error message specifically for the given reference description.
   * @param {node-opcua~ReferenceDescription} referenceDescription The reference description to get
   * the error message for.
   * @return {String} The specific error message.
   */
  processErrorMessage(referenceDescription) {
    return `Error reading node ${referenceDescription.nodeId.toString()}`;
  }

  /**
   * Monitors the nodes specified by a {@link node-opcua~ReferenceDescription}.
   * @param {node-opcua~ReferenceDescription} referenceDescription The refernce description to use.
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  processChunk(referenceDescription, handleErrors) {
    const nodeId = referenceDescription.nodeId;

    const item = this.subscription.monitor({
      nodeId,
      attributeId: AttributeIds.Value,
    }, {
      clientHandle: 13,
      samplingInterval: 250,
      queueSize: 1,
      discardOldest: true,
    }, SubscriptionService.TimestampsToReturn.Both);

    item.on('changed', dataValue => {
      if (!this._trackChanges) {
        handleErrors(null, StatusCodes.Good, done => done()); // Ignore first notification
      } else {
        this.emit('change', {
          nodeId,
          value: dataValue.value,
          referenceDescription,
          mtime: dataValue.serverTimestamp,
        });
      }
    });

    item.on('err', err => {
      /*
       This works around a bug in node-opcua:
       Instead of a error a string is emitted
       FIXME: Remove once bug is fixed
       */
      if (err instanceof Error) {
        handleErrors(err);
      } else {
        handleErrors(new Error(err));
      }
    });
  }

  /**
   * Buffers all read node descriptions until the subscription started, then calls
   * {@link QueueStream#_enqueueChunk} with them.
   * @param {node-opcua~ReferenceDescription} desc Reference description of the node to transform.
   * @param {String} enc The encoding used.
   * @param {function(err: ?Error)} callback Called with the error that occured while trying to
   * monitor the given node.
   */
  _transform(desc, enc, callback) {
    if (this.subscription) {
      this._enqueueChunk(desc);
      callback();
    } else {
      this.once('subscription-started', () => {
        this._enqueueChunk(desc);
        callback();
      });
    }
  }

  /**
   * Starts tracking node changes.
   * @param {function(err: ?Error)} callback Called once flushing is complete.
   */
  _flush(callback) {
    super._flush(err => {
      this._trackChanges = true;
      callback(err);
    });
  }

}

/**
 * Watches the given nodes for value changes.
 * @emit {ReadStream.ReadResult} Emits `change` events when a watched node changes.
 */
export default class Watcher extends Emitter {

  /**
   * Creates a new Watcher with the given nodes.
   * @param {NodeId[]} nodes The nodes to watch (recursively).
   */
  constructor(nodes = ProjectConfig.nodesToWatch) {
    super();

    /**
     * The node stream that discovers the nodes to watch.
     * @type {NodeStream}
     */
    this._nodeStream = new NodeStream(nodes)
      .on('error', err => this.emit('error', err));

    /**
     * The stream that starts monitoring the nodes to watch.
     * @type {SubscribeStream}
     */
    this._subscribeStream = new SubscribeStream()
      .on('error', err => this.emit('error', err));

    this._nodeStream.pipe(this._subscribeStream);

    this._subscribeStream.on('finish', () => this.emit('ready'));
    this._subscribeStream.on('change', event => this.emit('change', event));
  }

  /**
   * Ends monitoring nodes.
   */
  close() {
    if (this._subscribeStream.session) {
      Session.close(this._subscribeStream.session)
        .catch(err => this.emit('error', err));
    }
  }

}

import Logger from 'gulplog';
import { StatusCodes } from 'node-opcua';
import QueueStream from './QueueStream';

/**
 * A stream that writes all read {@link AtviseFile}s to their corresponding nodes on atvise server.
 */
export default class WriteStream extends QueueStream {

  /**
   * The error message to use when writing a file fails.
   * @param {AtviseFile} file The file being processed.
   * @return {String} The error message to use.
   */
  processErrorMessage(file) {
    return `Error writing ${file.nodeId.toString()}`;
  }

  /**
   * Writes an {@link AtviseFile} to it's corresponding node on atvise server.
   * @param {AtviseFile} file The file to write.
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  processChunk(file, handleErrors) {
    try {
      this.session.writeSingleNode(file.nodeId.toString(), {
        dataType: file.dataType,
        arrayType: file.arrayType,
        value: file.value,
      }, (err, statusCode) => {
        if (statusCode === StatusCodes.BadUserAccessDenied) {
          Logger.warn(`Error writing node ${
            file.nodeId.toString()
          }: Make sure it is not opened in atvise builder`);
          handleErrors(err, StatusCodes.Good, done => done());
        } else {
          handleErrors(err, statusCode, done => {
            this.push(file);
            done();
          });
        }
      });
    } catch (e) {
      handleErrors(e);
    }
  }

}

import filter from 'gulp-filter';
import Transformer from './Transformer';

/**
 * A transformer that transforms only some of the files read.
 * @abstract
 */
export default class PartialTransformer extends Transformer {

  /**
   * Creates a new partial transformer with the specified options.
   * @param {Object} options The options to use. See {@link Transformer#constructor} for available
   * options.
   */
  constructor(options = {}) {
    super(options);

    /**
     * The filter stream used.
     * @type {Stream}
     */
    this.filter = filter(file => this.shouldBeTransformed(file), { restore: true });
  }

  /**
   * `true` if `file` should be transformed.
   * @param {AtviseFile} file The file to transform or not.
   * @abstract
   */
  shouldBeTransformed(file) { // eslint-disable-line no-unused-vars
    throw new Error('PartialTransformer#shouldBeTransformed must be implemented by all subclasses');
  }

  /**
   * Transforms a file if {@link PartialTransformer#shouldBeTransformed} returns `true`.
   * @param {AtviseFile} file The file to transform.
   * @param {String} enc The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error that occured
   * while transforming or (optionally) the transformed file.
   */
  _transform(file, enc, callback) {
    if (this.shouldBeTransformed(file)) {
      super._transform(file, enc, callback);
    } else {
      callback(null, file);
    }
  }

  /**
   * Applies the transformer to the given stream. It does so by running the following steps:
   *  - Pipe the {@link PartialTransformer#filter} stream
   *  - If PartialTransformer#applyToFilteredStream is overrridden, apply it.
   *  - Otherwise pipe {@link Transformer#withDirection}.
   *  - Restore the filter stream
   * @param {Stream} stream The stream to apply the transformer to.
   * @param {TransformDirection} direction The direction to use.
   * @return {Stream} The resulting stream.
   */
  applyToStream(stream, direction) {
    const filteredStream = stream.pipe(this.filter);

    return (this.applyToFilteredStream(filteredStream, direction) ||
      filteredStream.pipe(this.withDirection(direction)))
      .pipe(this.filter.restore);
  }

  /**
   * Applies a stream transformer to the given, already filtered stream. Override this method if you
   * want to pipe streams directly. Returning a falsy value (e.g. false, null, undefined) invokes
   * {@link Transformer#transformFromDB} or {@link Transformer#transformFromFilesystem} instead.
   * @param {Stream} stream The stream to apply the transformer to.
   * @param {TransformDirection} direction The direction to use.
   * @return {?Stream} The resulting stream.
   */
  applyToFilteredStream(stream, direction) { // eslint-disable-line no-unused-vars
    return false;
  }

}

import { readdir } from 'fs';
import { extname, basename, join } from 'path';
import Logger from 'gulplog';
import PartialTransformer from './PartialTransformer';
import AtviseFile from '../server/AtviseFile';

/**
 * Determines which files are needed to create a combined file and stores these files as long as
 * some of them are missing.
 */
export class CombineFilesCache {

  /**
   * Creates a new DisplayCache.
   */
  constructor() {
    /**
     * The files caches for the given path.
     * @type {Map<String, vinyl~File>}
     */
    this._files = {};

    /**
     * The extensions of the files required.
     * @type {String[]}
     */
    this._required = {};
  }

  /**
   * Returns the extensions of the missing files for the given `dirname`.
   * @param {String} dirname The cache key to look for.
   * @return {String[]} Extensions of the missing files.
   */
  missingExtensions(dirname) {
    const required = this._required[dirname];
    const files = this._files[dirname];

    return required.filter(ext => files[ext] === undefined);
  }

  /**
   * Checks if, when `file` is added, all required files are cached.
   * @param {vinyl~File} file The file to add before checking.
   * @param {function(err: ?Error, files: ?Map<String, vinyl~File>)} callback Called with the error
   * that occured while checking or all files related to `file` if all required files are already
   * cached.
   */
  gotAllFiles(file, callback) {
    const dirname = file.dirname;

    if (!this._required[dirname]) {
      readdir(dirname, (err, files) => {
        if (err) {
          callback(err);
        } else {
          this._files[dirname] = {};
          this._required[dirname] = files
            .filter(name => name[0] !== '.')
            .map(name => extname(name));

          this.gotAllFiles(file, callback);
        }
      });
    } else {
      this._files[dirname][file.extname] = file;

      if (this.missingExtensions(dirname).length === 0) {
        const files = this._files[dirname];
        callback(null, files);

        delete this._files[dirname];
        delete this._required[dirname];
      } else {
        callback(null);
      }
    }
  }

}

/**
 * A transformer that splits files into multiple others.
 * @abstract
 */
export default class SplittingTransformer extends PartialTransformer {

  /**
   * Creates a new SplittingTransformer.
   * @param {Object} options The options to apply.
   */
  constructor(options) {
    super(options);

    /**
     * The cache used when collecting files to combine.
     * @type {CombineFilesCache}
     */
    this._combineFilesCache = new CombineFilesCache();
  }

  /**
   * Creates a combined file from the cached split files.
   * @param {Map<String, AtviseFile>} files The cached files stored against their extensions.
   * @param {AtviseFile} lastFile The last file collected. This is the only file guaranteed to be
   * set, therefore us if for error messages, etc.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Should be called with any errors
   * that occur while combining the files, or optionally the resulting file.
   * @abstract
   */
  createCombinedFile(files, lastFile, callback) { // eslint-disable-line no-unused-vars
    throw new Error(
      'SplittingTransformer#createCombinedFile must be implemented by all subclasses'
    );
  }

  /**
   * Calls {@link SplittingTransformer#createCombinedFile} as soon as all dependencies are
   * required files are cached.
   * @param {AtviseFile} file The read file.
   * @param {String} enc The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error occured while
   * caching files or creating the combined file or optionally the resulting combined file.
   */
  transformFromFilesystem(file, enc, callback) {
    this._combineFilesCache.gotAllFiles(file, (err, allFiles) => {
      if (err) {
        callback(err);
      } else if (allFiles) {
        this.createCombinedFile(allFiles, file, callback);
      } else {
        callback(null);
      }
    });
  }

  /**
   * Splits a {@link vinyl~File}: The resulting is a clone of the input file, with a different path.
   * @param {vinyl~File} file The file to split.
   * @param {?String} newExtension The extension the resulting file gets.
   * @return {vinyl~File} The resulting file.
   * @example
   * // Assuming that `original` is a File with the path "path/to/file.type.xml":
   * const result = SplittingTransformer.splitFile(original, '.another');
   * // `result` is a new File, with the contents of `original` and the path
   * // "path/to/file.type/file.another"
   */
  static splitFile(file, newExtension) {
    const newFile = file.clone();

    newFile.basename = `${newFile.stem}/${newFile.stem}`;
    newFile.extname = newExtension;

    return newFile;
  }

  /**
   * Combines split files to a single one.
   * @param {vinyl~File[]} files The files to combine.
   * @param {String} newExtension The extension the resulting file gets.
   * @return {vinyl~File} The resulting file.
   */
  static combineFiles(files, newExtension) {
    const newFile = files[0].clone();

    newFile.path = `${newFile.dirname}${newExtension}`;

    return newFile;
  }

  /**
   * If there are any missing files this method loads these files and calls
   * {SplittingTransformer#createCombinedFile} with them.
   * @param {function(err: ?Error)} callback Called with the error that occurred while loading
   * missing files.
   */
  _flush(callback) {
    const missingDirnames = Object.keys(this._combineFilesCache._files);

    if (missingDirnames.length > 0) {
      Promise.all(
        missingDirnames.map(dirname => {
          const files = this._combineFilesCache._files[dirname];
          const firstFile = files[Object.keys(files)[0]];

          const missing = this._combineFilesCache.missingExtensions(dirname);
          const stem = basename(dirname, extname(dirname));
          const paths = missing.map(ext => join(dirname, '/', `${stem}${ext}`));

          delete this._combineFilesCache._files[dirname];
          delete this._combineFilesCache._required[dirname];

          Logger.debug('Loading', paths.length, 'required file(s)');

          return Promise.all(
            paths.map((path, i) => AtviseFile.read({
              cwd: firstFile.cwd,
              base: firstFile.base,
              path,
            })
              .then(file => {
                files[missing[i]] = file;
              })
            )
          )
            .then(() => new Promise((resolve, reject) => {
              this.createCombinedFile(files, firstFile, (err, file) => {
                if (err) {
                  reject(err);
                } else {
                  this.push(file);

                  resolve(file);
                }
              });
            }));
        })
      )
        .then(files => Logger.debug('Created', files.length, 'additional file(s)'))
        .then(() => callback())
        .catch(err => callback(err));
    } else {
      callback();
    }
  }

}

