import { join } from 'path';
import { src } from 'gulp';
import browserSync from 'browser-sync';
import Logger from 'gulplog';
import { obj as createStream } from 'through2';
import watchForFileChanges from '../lib/gulp/watchForFileChanges';
import watchForServerChanges from '../lib/gulp/watchForServerChanges';
import PushStream from '../lib/gulp/PushStream';
import PullStream from '../lib/gulp/PullStream';
import AtviseFile from '../lib/server/AtviseFile';
import ProjectConfig from '../config/ProjectConfig';

/**
 * Watches local files and atvise server nodes to trigger pull/push on change.
 */
export default function watch() {
  let fileWatcher;
  let serverWatcher;
  const browserSyncInstance = browserSync.create();

  let pulling = false;
  let pushing = false;
  let lastPull = 0;
  let lastPushed = null;

  function initBrowserSync() {
    return new Promise((resolve) => {
      let ready = 0;

      function initIfReady() {
        ready++;

        if (ready === 2) {
          browserSyncInstance.init({
            proxy: {
              target: `${ProjectConfig.host}:${ProjectConfig.port.http}`,
              ws: true,
            },
          }, () => resolve());
        }
      }

      fileWatcher.once('ready', () => initIfReady());
      serverWatcher.once('ready', () => initIfReady());
    });
  }

  function rejectOnError(err, resolve, reject) {
    if (err) {
      reject(err);
    } else {
      resolve();
    }
  }

  function startFileWatcher() {
    return new Promise((resolve, reject) => {
      fileWatcher = watchForFileChanges((path, root, stat) => {
        if (!pulling && AtviseFile.normalizeMtime(stat.mtime) > lastPull) {
          pushing = true;
          Logger.info(path, 'changed');

          const source = src(join(root, path), { base: root });

          (new PushStream(source))
            .on('data', file => (lastPushed = file.nodeId.toString()))
            .on('end', () => {
              pushing = false;
              browserSyncInstance.reload();
              fileWatcher.emit('push');
            });
        }
      })(err => rejectOnError(err, resolve, reject));
    });
  }

  function startServerWatcher() {
    return new Promise((resolve, reject) => {
      serverWatcher = watchForServerChanges(readResult => {
        if (!pushing) {
          if (readResult.nodeId.toString() !== lastPushed) {
            pulling = true;
            Logger.info(readResult.nodeId.toString(), 'changed');

            const readStream = createStream();
            readStream.write(readResult);
            readStream.end();

            (new PullStream(readStream))
              .on('end', () => {
                pulling = false;
                lastPull = AtviseFile.normalizeMtime(readResult.mtime);
                browserSyncInstance.reload();
                fileWatcher.emit('pull');
              });
          } else {
            lastPushed = null;
          }
        }
      })(err => rejectOnError(err, resolve, reject));
    });
  }

  const promise = Promise.all([
    startFileWatcher(),
    startServerWatcher(),
  ]);

  initBrowserSync();

  promise.browserSync = browserSyncInstance;
  promise.fileWatcher = fileWatcher;
  promise.serverWatcher = serverWatcher;

  return promise;
}

watch.description = 'Watch local files and atvise server nodes to trigger pull/push on change';

import { Buffer } from 'buffer';
import XMLTransformer from '../lib/transform/XMLTransformer';

/**
 * Splits read atvise display XML nodes into their SVG and JavaScript sources,
 * alongside with a .json file containing the display's parameters.
 */
export default class DisplayTransformer extends XMLTransformer {

  /**
   * Returns true for all files containing atvise displays.
   * @param {AtviseFile} file The file to check.
   * @return {Boolean} `true` for all atvise display files.
   */
  shouldBeTransformed(file) {
    return file.isDisplay;
  }

  /**
   * Splits any read files containing atvise displays into their SVG and JavaScript sources,
   * alongside with a .json file containing the display's parameters.
   * @param {AtviseFile} file The display file to split.
   * @param {String} enc The encoding used.
   * @param {function(err: Error, file: AtviseFile)} callback Called with the error that occured
   * while transforming the display, or the file passed through.
   */
  transformFromDB(file, enc, callback) {
    this.decodeContents(file, (err, results) => {
      if (err) {
        callback(err);
      } else if (!results || results.svg === undefined) {
        callback(new Error('Error parsing display: No `svg` tag'));
      } else {
        const xml = results;
        const document = results.svg;

        const config = {};

        // Extract JavaScript
        if (this.tagNotEmpty(document.script)) {
          document.script.forEach(script => {
            if (script.$ && (script.$.src || script.$['xlink:href'])) {
              if (!config.dependencies) {
                config.dependencies = [];
              }

              config.dependencies.push(script.$.src || script.$['xlink:href']);
            } else {
              // TODO: Warn on multiple inline scripts

              const scriptFile = DisplayTransformer.splitFile(file, '.js');
              const scriptText = (typeof script === 'string') ?
                script : script._ || '';

              scriptFile.contents = Buffer.from(scriptText);
              this.push(scriptFile);
            }
          });

          delete xml.svg.script;
        }

        // Extract metadata
        if (this.tagNotEmpty(document.metadata)) {
          // TODO: Warn on multiple metadata tags

          const meta = document.metadata[0];

          // - Parameters
          if (this.tagNotEmpty(meta['atv:parameter'])) {
            config.parameters = [];
            meta['atv:parameter'].forEach(param => config.parameters.push(param.$));

            delete xml.svg.metadata[0]['atv:parameter'];
          }
        }

        const configFile = DisplayTransformer.splitFile(file, '.json');

        configFile.contents = Buffer.from(JSON.stringify(config, null, '  '));
        this.push(configFile);

        const svgFile = DisplayTransformer.splitFile(file, '.svg');

        this.encodeContents(xml, (encodeErr, stringValue) => {
          if (encodeErr) {
            callback(encodeErr);
          } else {
            svgFile.contents = Buffer.from(stringValue);
            this.push(svgFile);

            callback(null);
          }
        });
      }
    });
  }

  /**
   * Creates a display from the collected files.
   * @param {Map<String, vinyl~File>} files The collected files, stored against their extension.
   * @param {vinyl~File} lastFile The last file read. *Used for error messages only*.
   * @param {function(err: ?Error, data: vinyl~File)} callback Called with the error that occured
   * while creating the display or the resulting file.
   */
  createCombinedFile(files, lastFile, callback) {
    const configFile = files['.json'];
    let config = {};

    if (configFile) {
      try {
        config = JSON.parse(configFile.contents.toString());
      } catch (e) {
        callback(new Error(`Error parsing JSON in ${configFile.relative}: ${e.message}`));
        return;
      }
    }

    const svgFile = files['.svg'];
    if (!svgFile) {
      callback(new Error(`No display SVG in ${lastFile.dirname}`));
      return;
    }

    const scriptFile = files['.js'];
    let inlineScript = '';
    if (scriptFile) {
      inlineScript = scriptFile.contents.toString();
    }

    this.decodeContents(svgFile, (err, xml) => {
      if (err) {
        callback(err);
      } else if (!xml || xml.svg === undefined) {
        callback(new Error('Error parsing display SVG: No `svg` tag'));
      } else {
        const result = xml;

        // Handle empty svg tag
        if (typeof result.svg === 'string') {
          result.svg = {};
        }

        // Insert dependencies
        result.svg.script = [];
        if (config.dependencies) {
          config.dependencies.forEach(p => result.svg.script.push({
            $: { 'xlink:href': p },
          }));
        }

        // Insert script
        // FIXME: Import order is not preserved!
        if (scriptFile) {
          result.svg.script.push({
            $: { type: 'text/ecmascript' },
            _: XMLTransformer.forceCData(inlineScript),
          });
        }

        // Insert metadata
        // - Parameters
        if (config.parameters && config.parameters.length > 0) {
          if (!result.svg.metadata || !result.svg.metadata[0]) {
            result.svg.metadata = [{}];
          }
          if (!result.svg.metadata[0]['atv:parameter']) {
            result.svg.metadata[0]['atv:parameter'] = [];
          }

          // FIXME: Parameters should come before `atv:gridconfig` and `atv:snapconfig`
          config.parameters
            .forEach(param => result.svg.metadata[0]['atv:parameter'].push({ $: param }));
        }

        const display = DisplayTransformer.combineFiles(
          Object.keys(files).map(ext => files[ext]),
          '.xml'
        );

        this.encodeContents(result, (encodeErr, xmlString) => {
          if (encodeErr) {
            callback(encodeErr);
          } else {
            display.contents = Buffer.from(xmlString);

            callback(null, display);
          }
        });
      }
    });
  }

}

import { Buffer } from 'buffer';
import { readFile } from 'fs';
import Logger from 'gulplog';
import Transformer from '../lib/transform/Transformer';
import AtviseFile from '../lib/server/AtviseFile';
import NodeId from '../lib/server/NodeId';

/**
 * A Transformer that maps {@link ReadStream.ReadResult}s to {@link AtviseFile}s.
 */
export default class MappingTransformer extends Transformer {

  /**
   * Writes an {@link AtviseFile} for each {@link ReadStream.ReadResult} read. If a read file has a
   * non-standard type (definition) an additional `.rc` file is pushed holding this type.
   * @param {ReadStream.ReadResult} readResult The read result to create the file for.
   * @param {String} encoding The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error that occurred
   * while transforming the read result or the resulting file.
   */
  transformFromDB(readResult, encoding, callback) {
    try {
      const file = AtviseFile.fromReadResult(readResult);

      if (file.relative.match(/\.var\./)) {
        const rc = file.clone();

        rc.extname = '';
        rc.basename = `.${rc.stem}.rc`;

        rc.contents = Buffer.from(JSON.stringify({
          typeDefinition: file.typeDefinition,
        }, null, '  '));

        this.push(rc);
      }

      callback(null, file);
    } catch (e) {
      Logger[e.message === 'no value' ? 'debug' : 'warn'](
        `Unable to map ${readResult.nodeId.toString()}: ${e.message}`
      );
      Logger.debug(e);

      callback(null);
    }
  }

  /**
   * Writes an {@link AtviseFile} for each {@link vinyl~File} read.
   * @param {vinyl~File} file The raw file.
   * @param {String} encoding The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error that occurred
   * while transforming the read result or the resulting file.
   */
  transformFromFilesystem(file, encoding, callback) {
    if (file.isDirectory()) {
      callback(null);
    } else {
      const atFile = new AtviseFile({
        cwd: file.cwd,
        base: file.base,
        path: file.path,
        contents: file.contents,
      });

      if (file.relative.match(/\.var\./)) {
        const rcFile = file.clone({ contents: false });
        rcFile.extname = '';
        rcFile.basename = `.${rcFile.stem}.rc`;

        readFile(rcFile.path, 'utf8', (err, data) => {
          try {
            const rc = JSON.parse(data);
            atFile._typeDefinition = new NodeId(rc.typeDefinition);

            callback(null, atFile);
          } catch (e) {
            Logger.error(`Unable to get runtime configuration for ${file.relative}`);
            callback(err || e);
          }
        });
      } else {
        callback(null, atFile);
      }
    }
  }

}

import Logger from 'gulplog';
import XMLTransformer from '../lib/transform/XMLTransformer';

/**
 * A transformer that splits atvise scripts and quick dynamics into a code file and a .json file
 * containing parameters and metadata.
 */
export default class ScriptTransformer extends XMLTransformer {

  /**
   * Returns `true` for all files containing script code or quick dynamics.
   * @param {AtviseFile} file The file to check.
   * @return {Boolean} `true` for all files containing script code or quick dynamics.
   */
  shouldBeTransformed(file) {
    return file.isScript || file.isQuickDynamic;
  }

  /**
   * Splits any read files containing scripts or quick dynamics into their JavaScript sources,
   * alongside with a .json file containing parameters and metadata.
   * @param {AtviseFile} file The script file to split.
   * @param {String} enc The encoding used.
   * @param {function(err: Error, file: AtviseFile)} callback Called with the error that occured
   * while transforming the script, or the file passed through.
   */
  transformFromDB(file, enc, callback) {
    this.decodeContents(file, (err, results) => {
      if (err) {
        callback(err);
      } else {
        if (!results || results.script === undefined) {
          Logger.warn(`Empty document at ${file.relative}`);
        }

        const document = results && results.script ? results.script : {};

        const config = {};
        let code = '';

        // Extract metadata
        if (this.tagNotEmpty(document.metadata)) {
          // TODO: Warn on multiple metadata tags

          const meta = document.metadata[0];

          // - Icon
          if (this.tagNotEmpty(meta.icon)) {
            const icon = meta.icon[0];
            config.icon = icon.$ || {};
            config.icon.content = icon._ || '';
          }

          // - Visible
          if (this.tagNotEmpty(meta.visible)) {
            config.visible = Boolean(meta.visible[0]);
          }

          // - Title
          if (this.tagNotEmpty(meta.title)) {
            config.title = meta.title[0];
          }

          // - Description
          if (this.tagNotEmpty(meta.description)) {
            config.description = meta.description[0];
          }
        }

        // Extract Parameters
        if (this.tagNotEmpty(document.parameter)) {
          config.parameters = [];
          document.parameter.forEach(param => config.parameters.push(param.$));
        }

        // Extract JavaScript
        if (this.tagNotEmpty(document.code)) {
          code = document.code[0];
        }

        // Write config file
        const configFile = ScriptTransformer.splitFile(file, '.json');
        configFile.contents = Buffer.from(JSON.stringify(config, null, '  '));
        this.push(configFile);

        // Write script file
        const scriptFile = ScriptTransformer.splitFile(file, '.js');
        scriptFile.contents = Buffer.from(code);
        this.push(scriptFile);

        callback(null);
      }
    });
  }

  /**
   * Creates a script from the collected files.
   * @param {Map<String, AtviseFile>} files The collected files, stored against their extension.
   * @param {AtviseFile} lastFile The last file read. *Used for error messages only*.
   * @param {function(err: ?Error, data: vinyl~File)} callback Called with the error that occured
   * while creating the script or the resulting file.
   */
  createCombinedFile(files, lastFile, callback) {
    const configFile = files['.json'];
    let config = {};

    if (configFile) {
      try {
        config = JSON.parse(configFile.contents.toString());
      } catch (e) {
        callback(new Error(`Error parsing JSON in ${configFile.relative}: ${e.message}`));
        return;
      }
    }

    const scriptFile = files['.js'];
    let code = '';

    if (scriptFile) {
      code = scriptFile.contents.toString();
    }

    const result = {
      script: { },
    };

    // Insert metadata
    if (lastFile.isQuickDynamic) {
      const meta = {};

      // - Icon
      if (config.icon) {
        const icon = config.icon.content;
        delete config.icon.content;

        meta.icon = {
          $: config.icon,
          _: icon,
        };
      }

      // - Other fields
      if (config.visible !== undefined) {
        meta.visible = config.visible ? 1 : 0;
      }

      if (config.title !== undefined) {
        meta.title = config.title;
      }

      if (config.description !== undefined) {
        meta.description = config.description;
      }

      result.script.metadata = meta;
    }

    // Insert parameters
    result.script.parameter = config.parameters ?
      config.parameters.map(param => ({ $: param })) :
      [];

    result.script.code = ScriptTransformer.forceCData(code);

    const script = ScriptTransformer.combineFiles(
      Object.keys(files).map(ext => files[ext]),
      '.xml'
    );

    this.encodeContents(result, (encodeErr, xmlString) => {
      if (encodeErr) {
        callback(encodeErr);
      } else {
        script.contents = Buffer.from(xmlString);

        callback(null, script);
      }
    });
  }

}

