import { join } from 'path';
import expect from 'unexpected';
import { spy } from 'sinon';

import { NodeId as OpcNodeId } from 'node-opcua';
import NodeId from '../../../../src/lib/server/NodeId';

/** @test {NodeId} */
describe('NodeId', function() {
  const path = 'AGENT/DISPLAYS/Main';
  const id = 'AGENT.DISPLAYS.Main';

  /** @test {NodeId#constructor} */
  describe('#constructor', function() {
    function expectType(type, ...args) {
      expect((new NodeId(...args)).identifierType, 'to equal', type);
    }

    context('when called with value only', function() {
      it('should work', function() {
        expect(() => new NodeId('AGENT.DISPLAYS'), 'not to throw');
      });

      it('should set namespace to 1', function() {
        expect((new NodeId('AGENT.DISPLAYS')).namespace, 'to equal', 1);
      });

      it('should set indentifierType to string if string is passed', function() {
        expectType(NodeId.NodeIdType.STRING, 'AGENT.DISPLAYS');
      });

      it('should set indentifierType to number if number is passed', function() {
        expectType(NodeId.NodeIdType.NUMERIC, 123);
      });
    });

    context('when called with a nodeid string', function() {
      it('should work', function() {
        expect(() => new NodeId('ns=1;s=AGENT.DISPLAYS'), 'not to throw');
      });

      it('should get namespace from string', function() {
        expect((new NodeId('ns=13;s=AGENT.DISPLAYS')).namespace, 'to equal', 13);
      });

      it('should get identifierType from string', function() {
        expectType(NodeId.NodeIdType.STRING, 'ns=1;s=AGENT.DISPLAYS');
        expectType(NodeId.NodeIdType.NUMERIC, 'ns=1;i=13');
        // FIXME: Missing tests for type GUID
        expectType(NodeId.NodeIdType.BYTESTRING, 'ns=1;b=13Ac');
      });
    });

    context('when called with type, value, namespace', function() {
      it('should work', function() {
        expect(() => new NodeId(NodeId.NodeIdType.STRING, 'AGENT.DISPLAYS', 1), 'not to throw');
      });

      it('should set namespace to 1 if omitted', function() {
        expect(() => new NodeId(NodeId.NodeIdType.STRING, 'AGENT.DISPLAYS'), 'not to throw');
        expect((new NodeId(NodeId.NodeIdType.STRING, 'AGENT.DISPLAYS')).namespace, 'to equal', 1);
      });
    });

    it('should extend node-opcua\'s NodeId', function() {
      expect((new NodeId(NodeId.NodeIdType.NUMERIC, 123, 1)), 'to be a', OpcNodeId);
    });
  });

  /** @test {NodeId#fromFilePath} */
  describe('#fromFilePath', function() {
    it('should return a NodeId', function() {
      expect(NodeId.fromFilePath(path), 'to be a', NodeId);
    });

    it('should return file path again', function() {
      const nodeId = NodeId.fromFilePath(path);
      expect(nodeId.filePath, 'to be a', 'string');
      expect(nodeId.filePath, 'to equal', path);
    });

    it('should work with resource paths', function() {
      expect(NodeId.fromFilePath(
        join('SYSTEM/LIBRARY/ATVISE/RESOURCES/timer/imgs_glossy/top-separator.gif')
      ).value,
        'to equal', 'SYSTEM.LIBRARY.ATVISE.RESOURCES/timer/imgs_glossy/top-separator.gif');
    });

    it('should work with multi extension resource paths', function() {
      expect(NodeId.fromFilePath(
        join('SYSTEM/LIBRARY/PROJECT/RESOURCES/styles/bootstrap.min.css')
        ).value,
        'to equal', 'SYSTEM.LIBRARY.PROJECT.RESOURCES/styles/bootstrap.min.css');
    });
  });

  /** @test {NodeId#filePath} */
  describe('#filePath', function() {
    it('should return a valid file path', function() {
      const nodeId = new NodeId(NodeId.NodeIdType.STRING, id, 1);

      expect(nodeId.filePath, 'to be a', 'string');
      expect(nodeId.filePath, 'to equal', path);
    });

    it('should handle resource paths', function() {
      const nodeId = new NodeId(NodeId.NodeIdType.STRING, 'SYSTEM.LIBRARY.RESOURCES/dir/test.e', 1);

      expect(nodeId.filePath, 'to be a', 'string');
      expect(nodeId.filePath, 'to equal', 'SYSTEM/LIBRARY/RESOURCES/dir/test.e');
    });
  });

  /** @test {NodeId#inspect} */
  describe('#inspect', function() {
    const opts = {
      stylize: spy(t => t),
    };

    beforeEach(() => opts.stylize.reset());

    it('should return "namespace value"', function() {
      const nodeId = new NodeId(NodeId.NodeIdType.STRING, 'AGENT.DISPLAYS', 1);

      expect(nodeId.inspect(0, opts), 'to match', /1 AGENT\.DISPLAYS/);
    });

    it('should style string id as string', function() {
      (new NodeId(NodeId.NodeIdType.STRING, 'AGENT.DISPLAYS', 1)).inspect(0, opts);

      expect(opts.stylize.calledTwice, 'to be true');
      expect(opts.stylize.lastCall.args, 'to equal', ['AGENT.DISPLAYS', 'string']);
    });

    it('should style numeric id as number', function() {
      (new NodeId(NodeId.NodeIdType.NUMERIC, 123, 1)).inspect(0, opts);

      expect(opts.stylize.calledTwice, 'to be true');
      expect(opts.stylize.lastCall.args, 'to equal', [123, 'number']);
    });
  });
});

import { resolveNodeId, StatusCodes, NodeClass } from 'node-opcua';
import { spy } from 'sinon';
import expect from '../../../expect';
import NodeStream from '../../../../src/lib/server/NodeStream';
import NodeId from '../../../../src/lib/server/NodeId';

class StubNodeStream extends NodeStream {
  _transform(chunk, enc, callback) {
    callback(null, chunk);
  }
}

/** @test {NodeStream} */
describe('NodeStream', function() {
  const testNodes = [new NodeId('ns=1;s=AGENT.DISPLAYS')];

  /** @test {NodeStream#constructor} */
  describe('#constructor', function() {
    it('should fail without nodesToBrowse', function() {
      expect(() => new NodeStream(), 'to throw', /nodesToBrowse is required/);
    });

    it('should throw with invalid ignoreNodes', function() {
      expect(() => new NodeStream(testNodes, { ignoreNodes: 'test' }),
        'to throw', /ignoreNodes must be an array/);
    });

    it('should store "recursive" option', function() {
      expect((new StubNodeStream(testNodes, { recursive: false })).recursive, 'to be', false);
    });

    it('should create ignoredRexExp', function() {
      expect((new StubNodeStream(testNodes, { ignoreNodes: testNodes })).ignoredRegExp,
        'to equal', /^(ns=1;s=AGENT.DISPLAYS)/);
    });

    it('should listen to drained events', function() {
      expect((new StubNodeStream(testNodes)).listenerCount('drained'), 'to equal', 1);
    });
  });

  /** @test {NodeStream#processErrorMessage} */
  describe('#processErrorMessage', function() {
    const nodeId = resolveNodeId('ns=1;s=AGENT.DISPLAYS.Main');
    expect(NodeStream.prototype.processErrorMessage(nodeId),
      'to contain', nodeId.toString());
  });

  /** @test {NodeStream#processChunk} */
  describe('#processChunk', function() {
    it('should forward browse errors', function() {
      const stream = new NodeStream(testNodes)
        .prependOnceListener('session-open', () => {
          stream.session.browse = (options, callback) => {
            callback(new Error('Browse error'));
          };
        });

      return expect(stream, 'to error with', /Browse error/);
    });

    it('should emit error without results', function() {
      const stream = new NodeStream(testNodes)
        .prependOnceListener('session-open', () => {
          stream.session.browse = (options, callback) => {
            callback(null, null);
          };
        });

      return expect(stream, 'to error with', /No results/);
    });

    it('should emit error with empty results', function() {
      const stream = new NodeStream(testNodes)
        .prependOnceListener('session-open', () => {
          stream.session.browse = (options, callback) => {
            callback(null, []);
          };
        });

      return expect(stream, 'to error with', /No results/);
    });

    it('should not push parent nodes', function() {
      const stream = new NodeStream(testNodes, { recursive: false })
        .prependOnceListener('session-open', () => {
          stream.session.browse = (options, callback) => {
            callback(null, [{
              statusCode: StatusCodes.Good,
              references: [
                { nodeId: new NodeId('ns=1;s=AGENT') },
              ],
            }]);
          };
        });

      return expect(stream, 'to yield objects satisfying', 'to have length', 0);
    });

    it('should not push ignored nodes', function() {
      const stream = new NodeStream(testNodes, {
        recursive: false,
        ignoreNodes: [new NodeId('ns=1;s=AGENT.DISPLAYS.Main')],
      })
        .prependOnceListener('session-open', () => {
          stream.session.browse = (options, callback) => {
            callback(null, [{
              statusCode: StatusCodes.Good,
              references: [
                { nodeId: new NodeId('ns=1;s=AGENT.DISPLAYS.Main') },
              ],
            }]);
          };
        });

      return expect(stream, 'to yield objects satisfying', 'to have length', 0);
    });

    it('should not push discovered object nodes', function() {
      const stream = new NodeStream(testNodes, { recursive: false })
        .prependOnceListener('session-open', () => {
          stream.session.browse = (options, callback) => {
            callback(null, [{
              statusCode: StatusCodes.Good,
              references: [
                {
                  nodeClass: NodeClass.Object,
                  nodeId: new NodeId('ns=1;s=AGENT.DISPLAYS.Main'),
                },
              ],
            }]);
          };
        });

      return expect(stream, 'to yield objects satisfying', 'to have length', 0);
    });

    it('should push discovered variable nodes', function() {
      const stream = new NodeStream(testNodes, { recursive: false })
        .prependOnceListener('session-open', () => {
          stream.session.browse = (options, callback) => {
            callback(null, [{
              statusCode: StatusCodes.Good,
              references: [
                {
                  nodeClass: NodeClass.Variable,
                  nodeId: new NodeId('ns=1;s=AGENT.DISPLAYS.Main'),
                },
              ],
            }]);
          };
        });

      return expect(stream, 'to yield objects satisfying', 'to have length', 1);
    });

    it('should write discovered nodes if recursive', function() {
      let alreadyCalled = false;
      const stream = new NodeStream(testNodes, { recursive: true })
        .prependOnceListener('session-open', () => {
          stream.session.browse = (options, callback) => {
            if (alreadyCalled) {
              callback(null, [{
                statusCode: StatusCodes.Good,
                references: [],
              }]);
            } else {
              alreadyCalled = true;

              callback(null, [{
                statusCode: StatusCodes.Good,
                references: [
                  {
                    nodeClass: NodeClass.Variable,
                    nodeId: new NodeId('ns=1;s=AGENT.DISPLAYS.Main'),
                  },
                  {
                    nodeClass: NodeClass.Object,
                    nodeId: new NodeId('ns=1;s=AGENT.DISPLAYS.MAIN'),
                  },
                ],
              }]);
            }
          };
        });

      spy(stream, 'write');

      return expect(stream, 'to yield objects satisfying', 'to have length', 1)
        .then(() => expect(stream.write, 'was called times', 2));
    });
  });
});

import { StatusCodes } from 'node-opcua';
import { spy, stub } from 'sinon';
import expect from '../../../expect';
import QueueStream from '../../../../src/lib/server/QueueStream';

function fakeQueueStream(
  err = null, status = StatusCodes.Good, onSuccess = done => done(), options = {}
) {
  return new (class FakeQueueStream extends QueueStream {
    processErrorMessage(chunk) { return `Error processing ${chunk}`; }
    processChunk(chunk, handle) {
      handle(err, status, onSuccess);
    }
  })(options);
}

/** @test {QueueStream} */
describe('QueueStream', function() {
  /** @test {QueueStream#constructor} */
  describe('#constructor', function() {
    it('should work without options', function() {
      expect(() => new QueueStream(), 'not to throw');
    });

    it('should store maxParallel option', function() {
      expect((new QueueStream({ maxParallel: 99 }))._maxParallel, 'to equal', 99);
    });

    it('should store start date', function() {
      expect((new QueueStream())._start, 'to be a', 'number');
    });

    it('should listen for processed-chunk events', function() {
      expect((new QueueStream()).listenerCount('processed-chunk'), 'to be', 1);
    });
  });

  /** @test {QueueStream#hasPending} */
  describe('#hasPending', function() {
    it('should return true if there are queued operations', function() {
      const stream = new QueueStream();
      stream._queued.push({});

      expect(stream.hasPending, 'to be', true);
    });

    it('should return true if there are running operations', function() {
      const stream = new QueueStream();
      stream._processing = 1;

      expect(stream.hasPending, 'to be', true);
    });

    it('should return false otherwise', function() {
      expect((new QueueStream()).hasPending, 'to be', false);
    });
  });

  /** @test {QueueStream#queueEmpty} */
  describe('#queueEmpty', function() {
    it('should return true if not operations are queued', function() {
      expect((new QueueStream()).queueEmpty, 'to be', true);
    });

    it('should return false if there are queued operations', function() {
      const stream = new QueueStream();
      stream._queued.push({});

      expect(stream.queueEmpty, 'to be', false);
    });
  });

  /** @test {QueueStream#processed} */
  describe('#processed', function() {
    it('should return the number of processed items', function() {
      const stream = new QueueStream();
      stream._processed = 13;

      expect(stream.processed, 'to equal', 13);
    });
  });

  /** @test {QueueStream#opsPerSecond} */
  describe('#opsPerSecond', function() {
    it('should return 0 right after creating the stream', function() {
      expect((new QueueStream()).opsPerSecond, 'to be', 0);
    });

    it('should return the number of processed items after one second', function() {
      const stream = new QueueStream();
      stream._start -= 1000;
      stream._processed = 13;

      expect(stream.opsPerSecond, 'to be close to', 13, 0.5);
    });
  });

  /** @test {QueueStream#processErrorMessage} */
  describe('#processErrorMessage', function() {
    it('should throw if not overridden', function() {
      expect(() => (new QueueStream()).processErrorMessage({}), 'to throw', /must be implemented/);
    });
  });

  /** @test {QueueStream#processChunk} */
  describe('#processChunk', function() {
    it('should throw if not overridden', function() {
      expect(cb => (new QueueStream()).processChunk({}, cb),
        'to call the callback with error', /must be implemented/);
    });
  });

  /** @test {QueueStream#_processChunk} */
  describe('#_processChunk', function() {
    it('should increase #_processing', function() {
      const stream = fakeQueueStream();

      expect(stream._processing, 'to equal', 0);
      stream._processChunk('item');
      expect(stream._processing, 'to equal', 0);
    });

    it('should emit processed-chunk event', function() {
      const stream = fakeQueueStream();
      const listener = spy();
      stream.on('processed-chunk', listener);

      stream._processChunk('item');
      expect(listener, 'was called once');
      expect(listener.lastCall, 'to satisfy', ['item']);
    });

    it('should emit errors', function() {
      const stream = fakeQueueStream(new Error('Test'));
      const listener = spy();
      stream.on('error', listener);

      stream._processChunk('item');
      expect(listener, 'was called once');
      expect(listener.lastCall, 'to satisfy', [/Error processing item: Test/]);
    });

    it('should emit error on invalid status', function() {
      const stream = fakeQueueStream(null, StatusCodes.BadAggregateInvalidInputs);

      const listener = spy();
      stream.on('error', listener);

      stream._processChunk('item');
      expect(listener, 'was called once');
      expect(listener.lastCall, 'to satisfy', [/invalid data inputs/]);
    });
  });

  /** @test {QueueStream#_enqueueChunk} */
  describe('#_enqueueChunk', function() {
    it('should call _processChunk if allowed', function() {
      const stream = fakeQueueStream();
      stub(stream, '_processChunk');

      stream._enqueueChunk('item');
      expect(stream._processChunk, 'was called once');
      expect(stream._processChunk.lastCall, 'to satisfy', ['item']);
    });

    it('should add chunk to queue if maxParallel is reached', function() {
      const stream = fakeQueueStream();
      stream._processing = stream._maxParallel;

      stream._enqueueChunk('item');
      expect(stream._queued, 'to have length', 1);
      expect(stream._queued, 'to contain', 'item');
    });
  });

  /** @test {QueueStream#_transform} */
  describe('#_transform', function() {
    it('should wait for session to open', function() {
      const stream = fakeQueueStream();
      stub(stream, '_enqueueChunk');

      stream._transform('item', 'utf8', () => {});
      expect(stream._enqueueChunk, 'was not called');
    });

    it('should enqueue item once session is open', function(done) {
      const stream = fakeQueueStream();
      stub(stream, '_enqueueChunk');

      stream.once('session-open', () => {
        stream._transform('item', 'utf8', () => {});
        expect(stream._enqueueChunk, 'was called once');
        expect(stream._enqueueChunk.lastCall, 'to satisfy', ['item']);
        done();
      });
    });
  });

  /** @test {QueueStream#_flush} */
  describe('#_flush', function() {
    it('should wait for queue to drain', function() {
      const stream = fakeQueueStream();
      stream._processing = 1;
      const listener = spy();
      stream._flush(listener);

      expect(listener, 'was not called');

      stream.emit('drained');
      expect(listener, 'was called once');
    });

    it('should flush instantly if queue is empty', function() {
      const stream = fakeQueueStream();
      const listener = spy();
      stream._flush(listener);

      expect(listener, 'was called once');
    });
  });

  context('when chunk has been processed', function() {
    context('and queue is empty', function() {
      it('should emit drained if not processing any items', function() {
        const stream = new QueueStream();
        const listener = spy();
        stream.on('drained', listener);

        stream.emit('processed-chunk');
        expect(listener, 'was called once');
      });

      it('should not emit drained if processing an items', function() {
        const stream = new QueueStream();
        stream._processing = 1;
        const listener = spy();
        stream.on('drained', listener);

        stream.emit('processed-chunk');
        expect(listener, 'was not called');
      });
    });

    context('and queue is not empty', function() {
      it('should call _processChunk with queued chunk', function() {
        const stream = new QueueStream();
        const item = {};
        stub(stream, '_processChunk');
        stream._queued = [item];

        stream.emit('processed-chunk');
        expect(stream._processChunk, 'was called once');
        expect(stream._processChunk, 'was called with', item);
      });
    });
  });
});

import { StatusCodes } from 'node-opcua';
import expect from '../../../expect';
import NodeId from '../../../../src/lib/server/NodeId';
import ReadStream from '../../../../src/lib/server/ReadStream';

/** @test {ReadStream} */
describe('ReadStream', function() {
  /** @test {ReadStream#processErrorMessage} */
  describe('#processErrorMessage', function() {
    it('should include node id', function() {
      const nodeId = new NodeId('ns=1;s=AGENT.DISPLAYS.Main');
      expect(ReadStream.prototype.processErrorMessage({ nodeId }), 'to contain', nodeId.value);
    });
  });

  /** @test {ReadStream#processChunk} */
  describe('#processChunk', function() {
    it('should error without results', function() {
      const stream = new ReadStream();

      stream.once('session-open', () => {
        stream.session.read = (node, cb) => cb(null, [node], undefined);
      });

      return expect([{ nodeId: new NodeId('ns=1;s=AGENT.DISPLAYS.Main') }],
        'when piped through', stream,
        'to error with', /No results/);
    });

    it('should error with empty results', function() {
      const stream = new ReadStream();

      stream.once('session-open', () => {
        stream.session.read = (node, cb) => cb(null, [node], []);
      });

      return expect([{ nodeId: new NodeId('ns=1;s=AGENT.DISPLAYS.Main') }],
        'when piped through', stream,
        'to error with', /No results/);
    });

    it('should error when reading fails', function() {
      const stream = new ReadStream();

      stream.once('session-open', () => {
        stream.session.read = (node, cb) => cb(new Error('Test'), [node], []);
      });

      return expect([{ nodeId: new NodeId('ns=1;s=AGENT.DISPLAYS.Main') }],
        'when piped through', stream,
        'to error with', /Test/);
    });

