import NodeId from '../model/opcua/NodeId';
import DisplayTransformer from '../../transform/DisplayTransformer';
import {
  ServerscriptTransformer,
  QuickDynamicTransformer,
} from '../../transform/ScriptTransformer.js';
import MappingTransformer from '../../transform/Mapping';

/**
 * An *atvise-scm* project's configuration.
 * @abstract
 */
export default class Atviseproject {

  /**
   * The atvise-server's host. Defaults to *localhost*.
   * @type {string}
   */
  static get host() {
    return 'localhost';
  }

  /**
   * The atvise-server ports to use.
   * @type {Object}
   * @property {number} [opc=4840] The OPC-UA port the atvise-server runs on.
   * @property {number} [http=80] The HTTP port the atvise-server can be reached at.
   */
  static get port() {
    return {
      opc: 4840,
      http: 80,
    };
  }

  /**
   * The login to use. Return `false` if no login is required (default).
   * @type {Boolean|Object}
   * @property {string} username The username to log in with.
   * @property {string} password The password to log in with.
   */
  static get login() {
    return false;
  }

  /**
   * The transformers to use in this project. Returns a {@link DisplayTransformer}, a
   * {@link ScriptTransformer} and a {@link NewlinesTransformer} by default.
   * @type {Transformer[]}
   */
  static get useTransformers() {
    return [
      new DisplayTransformer(),
      new ServerscriptTransformer(),
      new QuickDynamicTransformer(),
      new MappingTransformer(),
    ];
  }

  /**
   * The atvise-server nodes that atvise-scm should sync. Defaults to the nodes
   * *AGENT*, *SYSTEM*, *ObjectTypes.PROJECT* and *VariableTypes.PROJECT*.
   * @type {String[]|NodeId[]}
   */
  static get nodes() {
    return [
      new NodeId('AGENT'),
      new NodeId('SYSTEM'),
      new NodeId('ObjectTypes.PROJECT'),
      new NodeId('VariableTypes.PROJECT'),
    ];
  }

  /**
   * The atvise-server nodes to watch in the corresponding tasks. Defaults to all nodes containing
   * displays.
   * @type {String[]|NodeId[]}
   */
  static get nodesToWatch() {
    return [
      new NodeId('AGENT.DISPLAYS'),
      new NodeId('SYSTEM.LIBRARY.PROJECT.OBJECTDISPLAYS'),
    ];
  }

  /**
   * An array of editor related node ids. They should be ignored in a atvise-scm project.
   * @type {NodeId[]}
   */
  static get EditorRelatedNodes() {
    return [
      new NodeId('SYSTEM\.JOURNALS\.ProjectHistory'),
    ];
  }

  /**
   * An array of server related node ids. They should be ignored in a atvise-scm project
   * as they are read-only.
   * @type {NodeId[]}
   */
  static get ServerRelatedNodes() {
    return [
      new NodeId('AGENT\.OPCUA\.server_url'),
      new NodeId('AGENT\.WEBACCESS\.https?[^\.]+\.(state)'),
      new NodeId('SYSTEM\.INFORMATION\.LOGS\.'),
      new NodeId('AGENT\.GENERATOR\.METHODS'),
      new NodeId('AGENT\.MIRROR\.METHODS'),
      new NodeId('AGENT\.HISTORY\.METHODS'),
      new NodeId('AGENT\.SCRIPT\.METHODS'),
      new NodeId('AGENT\.OPCUA\.METHODS'),
    ];
  }

  /**
   * These nodes (and their subnodes, if any) will be ignored by atvise-scm. Defaults to
   * {@link Atviseproject.EditorRelatedNodes} combined with
   * {@link Atviseproject.ServerRelatedNodes}.
   * @type {NodeId[]}
   */
  static get ignoreNodes() {
    return this.EditorRelatedNodes.concat(this.ServerRelatedNodes);
  }

  /**
   * Returns an object containing the properties to inspect.
   * @see https://nodejs.org/api/util.html#util_util_inspect_object_options
   * @return {Object} The object to inspect.
   */
  static inspect() {
    return {
      host: this.host,
      port: this.port,
      login: this.login,
      useTransformers: this.useTransformers,
      nodes: this.nodes,
      nodesToWatch: this.nodesToWatch,
      ignoreNodes: this.ignoreNodes,
    };
  }

  /**
   * The *version control system* to optimize tasks for.
   * @type {'git' | 'svn'}
   * @since 1.0.0
   */
  static get vcs() {
    return 'git';
  }

}

import { DataType } from 'node-opcua/lib/datamodel/variant';
import Logger from 'gulplog';
import NodeId from '../model/opcua/NodeId';
import CallMethodStream from '../server/scripts/CallMethodStream';

/**
 * The import operation's scope, which is set to be *absolute*.
 * @type {NodeId}
 */
const scopeId = new NodeId(NodeId.NodeIdType.NUMERIC, 0, 0);

/**
 * A stream that imports xml files in parallel.
 */
export default class ImportStream extends CallMethodStream {

  /**
   * Id of the `importNodes` OPC-UA method.
   * @type {NodeId}
   */
  get methodId() {
    return new NodeId('ns=1;s=AGENT.OPCUA.METHODS.importNodes');
  }

  /**
   * Returns the arguments the `importNodes` needs to be called with for the given file.
   * @param {vinyl~File} file The file being processed.
   * @return {node-opcua~Variant[]} The arguments for the `importNodes` method:
   *  - The import scope (which is set to be absolute)
   *  - The XML code (read from *file*)
   */
  inputArguments(file) {
    return [
      {
        dataType: DataType.NodeId,
        value: scopeId,
      },
      {
        dataType: DataType.XmlElement,
        value: file.contents,
      },
    ];
  }

  /**
   * Returns an error message specifically for the given file.
   * @param {vinyl~File} file The file to generate the error message for.
   * @return {string} The specific error message.
   */
  processErrorMessage(file) {
    return `Error importing file: ${file.relative}`;
  }

  /**
   * Checks if the import succeeded and calls `callback` with an error otherwise.
   * @param {vinyl~File} file The file that was processed.
   * @param {?node-opcua~Variant[]} outputArguments The import status output arguments (Array with a
   * single entry).
   * @param {function(err: ?Error)} callback The callback called with an error if import failed.
   */
  handleOutputArguments(file, outputArguments, callback) {
    const [importStatus] = outputArguments || [{}];

    if (importStatus.value) {
      Logger.debug(`Successfully imported file: ${file.relative}`);

      callback(null);
    } else {
      callback(new Error('Import failed'));
    }
  }

}

import readline from 'readline';
import Logger from 'gulplog';
import ProjectConfig from '../../config/ProjectConfig';
import Transformer, { TransformDirection } from '../transform/Transformer';
import dest from './dest';

/**
 * A stream that transforms read {@link ReadStream.ReadResult}s and stores the on the filesystem.
 */
export default class PullStream {

  /**
   * Creates a new PullStream based on a stream that writes {@link ReadStream.ReadResult} which may
   * be an instance of {@link ReadStream}.
   * @param {ReadStream} readStream The stream to read from.
   */
  constructor(readStream) {
    const printProgress = setInterval(() => {
      Logger.info(`Pulled: ${readStream.processed} (${readStream.opsPerSecond.toFixed(1)} ops/s)`);

      if (Logger.listenerCount('info') > 0) {
        readline.cursorTo(process.stdout, 0);
        readline.moveCursor(process.stdout, 0, -1);
      }
    }, 1000);

    return Transformer.applyTransformers(
      readStream,
      ProjectConfig.useTransformers,
      TransformDirection.FromDB
    )
      .pipe(dest('./src')) // FIXME: Get from config file
      .on('finish', () => {
        if (Logger.listenerCount('info') > 0) {
          readline.clearLine(process.stdout, 0);
          readline.cursorTo(process.stdout, 0);
        }

        clearInterval(printProgress);
      });
  }

}

import readline from 'readline';
import Logger from 'gulplog';
import ProjectConfig from '../../config/ProjectConfig';
import Transformer, { TransformDirection } from '../transform/Transformer';
import WriteStream from '../server/WriteStream';
import CreateNodeStream from '../server/CreateNodeStream';
import AddReferencesStream from '../server/AddReferencesStream';

/**
 * A stream that transforms read {@link vinyl~File}s and pushes them to atvise server.
 */
export default class PushStream {

  /**
   * Creates a new PushSteam based on a source file stream.
   * @param {Stream} srcStream The file stream to read from.
   */
  constructor(srcStream) {
    const createStream = new CreateNodeStream();
    const addReferencesStream = new AddReferencesStream();
    const writeStream = new WriteStream(createStream, addReferencesStream);

    const printProgress = setInterval(() => {
      Logger.info(
        `Pushed: ${writeStream._processed} (${writeStream.opsPerSecond.toFixed(1)} ops/s)`
      );

      if (Logger.listenerCount('info') > 0) {
        readline.cursorTo(process.stdout, 0);
        readline.moveCursor(process.stdout, 0, -1);
      }
    }, 1000);

    return Transformer.applyTransformers(
      srcStream,
      ProjectConfig.useTransformers,
      TransformDirection.FromFilesystem
    )
      .pipe(writeStream)
      .pipe(createStream)
      .pipe(addReferencesStream)
      .on('finish', () => {
        if (Logger.listenerCount('info') > 0) {
          readline.cursorTo(process.stdout, 0);
          readline.clearLine(process.stdout);
        }

        clearInterval(printProgress);
      });
  }

}

import { Writable } from 'stream';
import { join } from 'path';
import { NodeClass } from 'node-opcua/lib/datamodel/nodeclass';
import { outputFile, readJson } from 'fs-extra';
import hasha from 'hasha';
import Logger from 'gulplog';
import ProjectConfig from '../../config/ProjectConfig';
import { encodeVariant } from '../coding';

/**
 * Relative path to the rename file.
 * @type {string}
 */
const renameConfigPath = './atscm/rename.json';

/**
 * The default name inserted into the rename file.
 * @type {string}
 */
const renameDefaultName = 'insert node name';

/**
 * Options to pass to *hasha*.
 * @type {Object}
 */
const hashaOptions = { algorithm: 'md5' };

/**
 * If checksums should be used to decide if writes are needed.
 * @type {boolean}
 */
const useChecksums = ProjectConfig.vcs === 'svn';

const escapePathComponent = a => a.replace(/\//g, '%2F');

/**
 * A stream that writes {@link Node}s to the file system.
 */
export class WriteStream extends Writable {

  /**
   * Creates a new WriteStream.
   * @param {Object} options The options to use.
   * @param {string} options.path The path to write to **(required)**.
   * @param {string} options.base The base path to write to (defaults to *path*).
   */
  constructor(options) {
    if (!options.path) {
      throw new Error('Missing `path` option');
    }

    super(Object.assign({}, options, { objectMode: true, highWaterMark: 10000 }));

    /**
     * If the stream is destroyed.
     * @type {boolean}
     */
    this._isDestroyed = false;

    /**
     * The number of processed nodes.
     * @type {number}
     */
    this._processed = 0;

    /**
     * The number of written nodes.
     * @type {number}
     */
    this._written = 0;

    /**
     * The base to output to.
     * @type {string}
     */
    this._base = options.base || options.path;

    /**
     * The object stored in the *rename file* (usually at './atscm/rename.json')
     */
    this._renameConfig = {};

    /**
     * A promise that resolves once the *rename file* is loaded.
     * @type Promise<Object>
     */
    this._loadRenameConfig = readJson(renameConfigPath)
      .then(config => (this._renameConfig = config))
      .catch(() => Logger.debug('No rename config file loaded'));

    /**
     * A map of ids used for renaming.
     */
    this._idMap = new Map();

    /**
     * If writes should actually be performed. Set to `false` once id conflicts were discovered.
     */
    this._performWrites = true;

    this._discoveredIdConflicts = 0;

    if (useChecksums) {
      Logger.info('Optimizing for SVN diffs');
    }
  }

  /**
   * If the stream is destroyed.
   * @type {boolean}
   */
  get isDestroyed() {
    return this._isDestroyed;
  }

  /**
   * Transverses the node tree to see if any parent node has an id conflict.
   * @param {ServerNode} node The processed node.
   * @return {boolean} `true` if a parent node has an id conflict.
   */
  _parentHasIdConflict(node) {
    let current = node.parent;

    while (current) {
      if (current._hasIdConflict) { return true; }
      current = current.parent;
    }

    return false;
  }

  async _outputFile(path, content) {
    if (useChecksums) {
      const oldSum = await hasha.fromFile(path, hashaOptions)
        .catch(() => null);

      if (oldSum) {
        if (oldSum === hasha(content, hashaOptions)) {
          Logger.debug(`Content did not change at ${path}`);
          return Promise.resolve();
        }

        Logger.debug(`Content changed at ${path}`);
      } else {
        Logger.debug(`No checksums for ${path}`);
      }
    }

    return outputFile(path, content);
  }

  /**
   * Writes a single node to disk.
   * @param {ServerNode} node The processed node.
   * @return {Promise<boolean>} Resolves once the node has been written, `true` indicates the node
   * has actually been written.
   */
  async _writeNode(node) {
    // TODO: Throw if node.name ends with '.inner'
    const dirPath = node.filePath.map(escapePathComponent);

    const writeOps = [];

    // Rename nodes specified in the rename config
    const rename = this._renameConfig[node.id.value];
    if (rename && rename !== renameDefaultName) {
      node.renameTo(rename);
      Logger.debug(`'${node.nodeId}' was renamed to '${rename}'`);

      Object.assign(node, { _renamed: true });
    }

    // Resolve invalid ids
    if (!node._renamed && node.nodeId !== node.id.value) {
      Logger.debug(`Resolved ID conflict: '${
        node.id.value}' should be renamed to '${node.nodeId}'`);
    }

    Object.assign(node, { specialId: node.id.value });

    if (node.name.match(/:/)) {
      const before = node.name;
      node.renameTo(node.name.replace(/:/g, '_'));
      Logger.debug(`Resolved ID conflict: '${before}' was renamed to safe name '${node.name}'`);
    }

    // Detect "duplicate" ids (as file names are case insensitive)
    const pathKey = dirPath.concat(node.fileName).join('/').toLowerCase();
    if (this._idMap.has(pathKey)) {
      if (this._parentHasIdConflict(node)) {
        Logger.debug(`ID conflict: Skipping '${node.nodeId}'`);
      } else {
        Logger.error(`ID conflict: '${node.nodeId}' conflicts with '${
          this._idMap.get(pathKey)
        }'`);

        this._discoveredIdConflicts++;

        const existingRename = this._renameConfig[node.nodeId];
        if (existingRename) {
          if (existingRename === renameDefaultName) {
            // eslint-disable-next-line max-len
            Logger.error(` - '${node.nodeId}' is present inside the rename file at './atscm/rename.json', but no name has been inserted yet.`);
          } else {
            // eslint-disable-next-line max-len
            Logger.error(` - The name for '${node.nodeId}' inside './atscm/rename.json' is not unique.`);
          }

          Logger.info(" - Edit the node's name and run 'atscm pull' again");
        } else {
          this._renameConfig[node.nodeId] = renameDefaultName;
          Logger.info(` - '${node.nodeId}' was added to the rename file at './atscm/rename.json'`);
          Logger.info("Edit it's name and run 'atscm pull' again.");
        }
      }

      Object.assign(node, { _hasIdConflict: true });
      this._performWrites = false;
    } else {
      this._idMap.set(pathKey, node.nodeId);
    }

    // Write definition file (if needed)
    if (node.hasUnresolvedMetadata) {
      const name = node.nodeClass === NodeClass.Variable ?
        `./.${escapePathComponent(node.fileName)}.json` :
        `./${escapePathComponent(node.fileName)}/.${node.nodeClass.key}.json`;

      if (this._performWrites) {
        writeOps.push(
          this._outputFile(join(this._base, dirPath.join('/'), name),
            JSON.stringify(node.metadata, null, '  '))
        );
      }
    }

    // Write value
    if (node.nodeClass === NodeClass.Variable) {
      if (node.value) {
        if (!node.value.noWrite) {
          if (this._performWrites) {
            writeOps.push(
              this._outputFile(
                join(this._base, dirPath.join('/'), escapePathComponent(node.fileName)),
                encodeVariant(node.value))
            );
          }

          // Store child nodes as file.inner/...
          node.renameTo(`${node.name}.inner`);
        }
      } else {
        throw new Error('Missing value');
      }
    }

    return Promise.all(writeOps)
      .then(() => {
        this._processed++;
        this._written += writeOps.length;
      })
      .then(() => writeOps.length > 0);
  }

  /**
   * Writes a single node to the file system.
   * @param {Node} node The node to write.
   * @param {string} enc The encoding used.
   * @param {function(err: ?Error): void} callback Called once finished.
   */
  _write(node, enc, callback) {
    this._loadRenameConfig
      .then(() => this._writeNode(node))
      .then(() => callback())
      .catch(err => callback(err));
  }

  writeAsync(node) {
    return new Promise((resolve, reject) => {
      this._write(node, null, err => (err ? reject(err) : resolve()));
    });
  }

  /**
   * Writes multiple nodes in parallel.
   * @param {Node[]} nodes The nodes to write.
   * @param {function(error: ?Error): void} callback Called once all nodes have been written.
   */
  _writev(nodes, callback) {
    if (this.isDestroyed) { return; }

    this._loadRenameConfig
      .then(() => Promise.all(nodes
        .map(({ chunk }) => this._writeNode(chunk)))
      )
      .then(() => callback())
      .catch(err => callback(err));
  }

  /**
   * Destroys the stream.
   * @param {?Error} err The error that caused the destroy.
   * @param {function(err: ?Error): void} callback Called once finished.
   */
  _destroy(err, callback) {
    this._isDestroyed = true;
    super._destroy(err, callback);
  }

  /**
   * Writes the updated rename config to disk.
   */
  writeRenamefile() {
    if (this._discoveredIdConflicts) {
      Logger.error(
        `Discovered ${this._discoveredIdConflicts} node id conflicts, results are incomplete.
 - Resolve all conflicts inside '${renameConfigPath}' and run 'atscm pull' again`);
      // FIXME: Insert link to node ide conflict manual here once 1.0.0 is released.
    }

    return outputFile(renameConfigPath, JSON.stringify(this._renameConfig, null, '  '));
  }

}

/**
 * Creates a new {@link WriteStream} to write to *path*.
 * @param {string} path The path to write to.
 */
export default function dest(path) {
  return new WriteStream({ path });
}

/**
 * Switches keys and values in an object. E.G.: { "a": 1 } becomes { 1: "a" }.
 * @param {Object} obj The object to reverse.
 * @return {Object} The reversed object.
 */
export function reverse(obj) {
  return Object.keys(obj)
    .reduce((result, key) => Object.assign(result, {
      [obj[key]]: key,
    }), {});
}

/**
 * Picks some properties from an object and returns a new object containing these.
 * @param {Object} obj The object to pick from.
 * @param {Array<string>} properties Names of the properties to pick.
 * @return {Object} The resulting object.
 */
export function pick(obj, properties) {
  return properties
    .reduce((props, key) => Object.assign(props, { [key]: obj[key] }), {});
}

/* eslint-disable import/prefer-default-export */

/**
 * Returns a promise that resolves after the given duration.
 * @param {number} ms Milliseconds to delay.
 * @return {Promise<void>} A promise resolved after the specified delay.
 */
export const delay = ms => new Promise((resolve) => setTimeout(() => resolve(), ms));

/* eslint-disable import/prefer-default-export */

import readline from 'readline';
import Logger from 'gulplog';

/**
 * Prints the progress of a task.
 * @param {Promise<any>} task The task to print the progress for.
 * @param {Object} options The options used.
 * @param {function(): number} options.getter A function returning the current progress.
 * @param {function(value: number): string} options.formatter A function returning a log message for
 * the progress passed.
 * @param {string} [options.level='info'] The log level to use.
 * @param {boolean} [options.logResult=true] If the final progress should be printed.
 * @example <caption>A basic implementation</caption>
 * const task = doSomething(); // Returns a Promise
 *
 * reportProgress(task, {
 *   getter: () => getTaskProgress(), // returns a number, e.g. 13 if 13 files have been written
 *   formatter: value => `${value} files written`,
 * })
 *  .then(result => { // Results get passed directly from `task`
 *    console.log(`The result is: ${result}`);
 *  })
 *  .catch(console.error) // which means you need error handling as well!
 */
export function reportProgress(task, { getter, formatter, level = 'info', logResult = true } = {}) {
  const start = Date.now();
  const ops = value => (value / ((Date.now() - start) / 1000)).toFixed(1);
  const message = () => {
    const value = getter();
    return `${formatter(value)} (${ops(value)}/s)`;
  };

  const interval = setInterval(() => {
    if (Logger.listenerCount(level) > 0) {
      Logger[level](message());

      readline.cursorTo(process.stdout, 0);
      readline.moveCursor(process.stdout, 0, -1);
    }
  }, 80);

  const done = err => {
    clearInterval(interval);

    if (logResult && !err) {
      Logger[level](message());
    } else if (Logger.listenerCount(level) > 0) {
      readline.cursorTo(process.stdout, 0);
      readline.clearLine(process.stdout);
    }

    if (err) {
      throw err;
    }
  };

  return task.then(() => done(), err => done(err));
}

/* eslint-disable import/prefer-default-export */

/**
 * Sorts the given references before they are persisted to disk.
 * @param {Object} references The references to be sorted.
 */
export function sortReferences(references) {
  return Object.keys(references)
    .sort()
    .reduce((sorted, key) => Object.assign(sorted, {
      [key]: Array.isArray(references[key]) ?
        references[key].sort() :
        references[key],
    }), {});
}

/* eslint-disable import/prefer-default-export */

/**
 * Adds additional infomation to the error's message and rethows it.
 * @param {Error} error The error that occured.
 * @throws {Error} The extended error.
 */
export function handleTaskError(error) {
  const additionalMessage = error.node ? `
 - Node: ${error.node.nodeId}` : '';

  Object.assign(error, {
    message: `${error.message}${additionalMessage}`,
    stack: `${error.stack}${additionalMessage}`,
  });

  throw error;
}

// Nodes properties

/**
 * Tells if the given parsed XML node is an element (not a text / cdata node).
 * @param {Object} node The parsed node.
 * @return {boolean} `true` if the node has type 'element'.
 */
export function isElement({ type }) {
  return type === 'element';
}

/** Tells if the given parsed XML node is an elment and has the given tag name.
 * @param {Object} node The parsed node.
 * @param {string} tagName The tag name to check for.
 * @return {boolean} `true` if the node is an element with the given tag name.
 */
export function isElementWithName({ type, name }, tagName) {
  return isElement({ type }) && name === tagName;
}

/**
 * Returns a parsed node's text content. Works for 'text' and 'cdata' nodes.
 * @param {Object} node The parsedNode.
 * @return {string?} The nodes text content.
 */
export function textContent(node) {
  if (!node || !node.elements) { return null; }

  const contentNode = node.elements[0];

  // FIXME: Only works for { type: 'text', text: 'value' } and { type: 'cdata', cdata: 'data' }
  return contentNode[contentNode.type];
}

// Traversing nodes

/**
 * Returns a node's child elements with the given tag name.
 * @param {Object} node The node to check in.
 * @param {string|string[]} tagName The tag name to search for. If an array is passed, the tree is
 * traversed.
 * @return {Object[]} The matching child elements.
 */
export function findChildren(node, tagName) {
  if (!node || !node.elements) { return []; }

  if (Array.isArray(tagName)) {
    return tagName.reduce(
      (nodes, name) => nodes.reduce(
        (results, n) => results.concat(findChildren(n, name)),
        []),
      [node]
    );
  }

  return node.elements.filter(child => isElementWithName(child, tagName));
}

/**
 * Returns a node's first child element with the given tag name, or `null`.
 * @param {Object} node The node to check in.
 * @param {string|string[]} tagName The tag name to search for. If an array is passed, the tree is
 * traversed.
 * @return {Object?} The matching child elements.
 */
export function findChild(node, tagName) {
  const gotPath = Array.isArray(tagName);

  if (!node || (!node.elements && !gotPath)) { return null; }

  if (gotPath) {
    if (tagName.length === 0) { return node; }

    const child = findChild(node, tagName[0]);
    const remaining = tagName.slice(1);

    return remaining.length ? findChild(child, remaining) : child;
  }

  for (let i = 0; i < node.elements.length; i++) {
    if (isElementWithName(node.elements[i], tagName)) {
      return node.elements[i];
    }
  }

  return null;
}

// Manipulating nodes

/**
 * Returns and removes a node's child elements with the given tag name.
 * @param {Object} node The node to check in.
 * @param {string} tagName The tag name to search for.
 * @return {Object[]} The matching child elements.
 */
export function removeChildren(node, tagName) {
  if (!node || !node.elements) { return []; }

  const removed = [];

  // eslint-disable-next-line no-param-reassign
  node.elements = node.elements.filter(child => {
    if (isElementWithName(child, tagName)) {
      removed.push(child);
      return false;
    }
    return true;
  });

  return removed;
}

/**
 * Returns and removes a node's first child element with the given tag name, if no match is found
 * `null` is returned.
 * @param {Object} node The node to check in.
 * @param {string} tagName The tag name to search for.
 * @return {Object?} The matching child elements.
 */
export function removeChild(node, tagName) {
  if (!node || !node.elements) { return null; }

  for (let i = 0; i < node.elements.length; i++) {
    if (isElementWithName(node.elements[i], tagName)) {
      return node.elements.splice(i, 1)[0];
    }
  }

  return null;
}

// Creating nodes

/**
 * Creates a new text node.
 * @param {string} [text=''] The node's content.
 * @return {Object} A text node containing the given text.
 */
export function createTextNode(text = '') {
  return { type: 'text', text: text || '' };
}

/**
 * Creates a new CData node.
 * @param {string} [cdata=''] The node's content.
 * @return {Object} A CData node containing the given data.
 */
export function createCDataNode(cdata = '') {
  return { type: 'cdata', cdata };
}

/**
 * Creates a new element node.
 * @param {string} name The node's name.
 * @param {Object[]} [elements=undefined] The child elements to add.
 * @param {Object} [attributes={}] The attributes the new node should have.
 * @return {Object} An element node.
 */
export function createElement(name, elements = undefined, attributes = {}) {
  return { type: 'element', name, elements, attributes };
}

/**
 * Browses a parsed document's tree up starting with the given element.
 * @param {Object} element A parsed XML element.
 * @param {function(element: Object): void} onElement A function called once an element is
 * discovered in the document tree.
 */
function browseUp(element, onElement) {
  let current = element;

  while (current) {
    onElement(current);

    current = current.parent;
  }
}

/**
 * Returns an element's path.
 * @param {Object} element The element to use.
 * @return {string[]} The element's path.
 */
export function elementPath(element) {
  const path = [];

  browseUp(element, ({ name }) => {
    if (name) path.unshift(name);
  });

  return path;
}

/**
 * Returns a string describing an element's path.
 * @param {Object} element Th element to use.
 * @return {string} A description of the element's path.
 */
export function displayPath(element) {
  return elementPath(element).join(' > ');
}

/**
 * An option the "atscm init" command handles.
 * TODO: Support function values for name, message, default, choices...
 */
export default class InitOption {

  /**
   * Creates a new option based either on a message and (optionally) a default value or some
   * options.
   * @param {String|Object} messageOrOptions The message or options to use.
   * @param {inquirer~PromptType} [messageOrOptions.type] The option's type.
   * @param {string} messageOrOptions.message The option's message. Required if `messageOrOptions`
   * is an object.
   * @param {String|Number} [messageOrOptions.default] The options's default value.
   * @param {inquirer~Validator} [messageOrOptions.validate] A function that validates user input.
   * @param {String[]|Number[]} [messageOrOptions.choices] The options's choices. Applies to to list
   * types only.
   * @param {Boolean|function(answers: Object): Boolean} [messageOrOptions.when] Weather or not to
   * prompt this option.
   * @param {String|Number} [defaultOrUndefined] The default value to use.
   */
  constructor(messageOrOptions, defaultOrUndefined) {
    if (messageOrOptions === undefined) {
      throw new Error('message or options required');
    }

    if (typeof messageOrOptions === 'string') {
      if (messageOrOptions.length === 0) {
        throw new Error('message is required');
      }

      /**
       * The option's {@link inquirer~PromptType}. Defaults to 'input'.
       * @type {inquirer~PromptType}
       */
      this.type = InitOption.DefaultType;

      /**
       * The option's message. A '?' sign is added automatically.
       * @type {String}
       */
      this.message = `${messageOrOptions}?`;

      /**
       * The default value to use.
       * @type {String|Number|undefined}
       */
      this.default = defaultOrUndefined;
    } else {
      if (!messageOrOptions.message || messageOrOptions.length === 0) {
        throw new Error('message is required');
      }

      this.type = messageOrOptions.type || InitOption.DefaultType;
      this.message = `${messageOrOptions.message}?`;
      this.default = messageOrOptions.default;

      /**
       * The choices available. Applies to list types only.
       * @type {String[]|Number[]|undefined}
       */
      this.choices = messageOrOptions.choices;

      /**
       * Validates the user input for this option.
       * @type {inquirer~Validator}
       */
      this.validate = messageOrOptions.validate;

      /**
       * A function or boolean that indicates weather or not to prompt this option.
       * @type {Boolean|function(answers: Object): Boolean|undefined}
       */
      this.when = messageOrOptions.when;
    }
  }

  /**
   * The default {@link inquirer~PromptType} to use. Equals 'input'.
   * @return {inquirer~PromptType} The default {@link inquirer~PromptType} to use.
   */
  static get DefaultType() {
    return 'input';
  }

}

import { ReferenceTypeIds as RealReferenceTypeIds } from 'node-opcua/lib/opcua_node_ids';
import { NodeClass } from 'node-opcua/lib/datamodel/nodeclass';
import { reverse } from '../helpers/Object';
import { sortReferences } from '../helpers/mapping';

/**
 * References type ids.
 * @type {Map<string, number>}
 */
export const ReferenceTypeIds = Object.assign({
  toParent: -1,
}, RealReferenceTypeIds);

/**
 * Names for references.
 * @type {Map<number, string>}
 */
export const ReferenceTypeNames = reverse(ReferenceTypeIds);

/**
 * A map specialized for holding references.
 */
class ReferenceMap extends Map {

  /**
   * Adds a new reference.
   * @param {number} type The reference id.
   * @param {string} nodeId The reference target node's id.
   */
  addReference(type, nodeId) {
    const set = this.get(type);
    if (set) {
      set.add(nodeId);
    } else {
      this.set(type, new Set([nodeId]));
    }
  }

  /**
   * Removes the given reference.
   * @param {number} type The reference id.
   * @param {string} nodeId The reference target node's id.
   */
  deleteReference(type, nodeId) {
    const set = this.get(type);
    if (set) {
      const ref = set.delete(nodeId);

      if (ref) {
        if (set.size === 0) {
          this.delete(type);
        }
        return ref;
      }
    }

    throw new Error(`No ${ReferenceTypeNames[type] || type} reference to ${nodeId}`);
  }

  /**
   * Returns a plain object of refernces.
   * @type {Object}
   */
  toJSON() {
    return [...this]
      .reduce((result, [key, value]) => Object.assign(result, {
        [ReferenceTypeNames[key] || key]: [...value],
      }), {});
  }

}

/**
 * The main model class.
 */
export default class Node {

  /**
   * Creates a new node.
   * @param {Object} options The options to use.
   * @param {string} options.name The node's name.
   * @param {Node} options.parent The node's parent node.
   * @param {node-opcua~NodeClass} options.nodeClass The node's class.
   */
  constructor({ name, parent, nodeClass/* , referenceToParent */ }) {
    /** The node's name when stored to a file. {@type string} */
    this.fileName = name;
    /** The node's name when written to the server. {@type string} */
    this.idName = name;
    /** The node's parent node. {@type Node} */
    this.parent = parent;
    /** The node's class. {@type node-opcua~NodeClass} */
    this.nodeClass = nodeClass;

    /**
     * A set of unresolved properties.
     * @type {Set<string>}
     */
    this._unresolved = new Set([
      'nodeClass',
      // Only for variables
      'dataType',
      'arrayType',
    ]);
    /**
     * A set of resolved properties.
     * @type {Set<string>}
     */
    this._resolved = new Set();

    /**
     * The node's refernces.
     * @type {ReferenceMap}
     */
    this.references = new ReferenceMap();
    /**
     * The node's unresolved refernces.
     * @type {ReferenceMap}
     */
    this._resolvedReferences = new ReferenceMap();
    /**
     * The node's resolved refernces.
     * @type {ReferenceMap}
     */
    this._unresolvedReferences = new ReferenceMap();

    /**
     * If the parent node resolves metadata.
     * @type {boolean}
     */
    this._parentResolvesMetadata = false;
  }

  /**
   * If the parent resolves metadata (for example: split transformer source files).
   * @type {boolean}
   */
  get parentResolvesMetadata() {
    return this._parentResolvesMetadata;
  }

  markAsResolved(key) {
    const value = this._unresolved.delete(key);

    // FIXME: Only test if debug / test
    if (value === false) {
      throw new Error(`'${key}' is already resolved`);
    }

    this._resolved.add(key);
  }

  isResolved(key) {
    return this._resolved.has(key);
  }

  /**
   * Adds a new reference.
   * @param {number} type The reference type's id.
   * @param {string} id The reference target node's id.
   */
  addReference(type, id) {
    this.references.addReference(type, id);
    this._unresolvedReferences.addReference(type, id);
  }

  setReferences(type, ids) {
    this.references.set(type, new Set(ids));
    this._unresolvedReferences.set(type, new Set(ids));
  }

  markReferenceAsResolved(name, value) {
    const type = ReferenceTypeIds[name];
    const ref = this._unresolvedReferences.deleteReference(type, value);
    this._resolvedReferences.addReference(type, ref);
  }

  markAllReferencesAsResolved(name) {
    const type = ReferenceTypeIds[name];
    this._unresolvedReferences.delete(type);
  }

  hasUnresolvedReference(name) {
    const type = ReferenceTypeIds[name];
    return this._unresolvedReferences.has(type);
  }

  /**
   * The node's file path, used to compute {@link Node#filePath}.
   */
  get _filePath() {
    if (!this.parent) { return [this.fileName]; }
    return this.parent._filePath.concat(this.fileName);
  }

  /**
   * The node's file path.
   * @type {string[]}
   */
  get filePath() {
    if (!this.parent) { return []; }
    return this.parent._filePath;
  }

  /**
   * The node's id, used to compute {@link Node#nodeId}.
   * @type {string}
   */
  get _nodeId() {
    if (this.specialId) {
      return {
        id: this.specialId,
        separator: this.specialId.match(/\.RESOURCES\/?/) ? '/' : '.',
      };
    }

    if (!this.parent) {
      return {
        id: this.idName,
        separator: '.',
      };
    }

    const { separator, id } = this.parent._nodeId;

    if (this._parentResolvesMetadata) {
      return { separator, id };
    }

    return {
      separator: this.idName === 'RESOURCES' ? '/' : separator,
      id: `${id}${separator}${this.idName}`,
    };
  }

  /**
   * The node's id.
   * @type {string}
   */
  get nodeId() {
    return this._nodeId.id;
  }

  /**
   * The node's type definition if given.
   * @type {?number}
   */
  get typeDefinition() {
    const refs = this.references.get(ReferenceTypeIds.HasTypeDefinition);
    return refs ? [...refs][0] : undefined;
  }

  /**
   * Returns `true` if the node has the given type definition.
   * @param {string} typeDefName The type definition to check.
   * @return {boolean} If the node has the given type definition.
   */
  hasTypeDefinition(typeDefName) {
    const def = this.typeDefinition;

    return def ? def === typeDefName : false;
  }

  /**
   * `true` at the moment.
   * @type {boolean}
   */
  get hasUnresolvedMetadata() {
    return true;
    /* FIXME: Once plugin mapping is implemented
    const value = !this._parentResolvesMetadata && (Boolean(this._unresolved.size) ||
      Boolean(this._unresolvedReferences.size) || this.specialId);

    // FIXME: If debug / test
    if (!value && Object.keys(this.metadata).length > 0) {
      throw new Error(`#hasUnresolvedMetadata did return invalid result ${
        value
      } for ${
        JSON.stringify(Object.assign(this, {parent: undefined, value: undefined }), null, '  ')
      }`);
    } else if (value && Object.keys(this.metadata).length === 0) {
      throw new Error('#metadata did return invalid result');
    }

    return value; */
  }

  /**
   * The metadata to store in the node's definition file.
   * @type {Object}
   */
  get metadata() {
    if (this._parentResolvesMetadata) { return {}; }

    const meta = {};

    if (this.specialId) {
      meta.nodeId = this.specialId;
    }

    if (this.specialName) {
      meta.name = this.specialName;
    }

    if (this.nodeClass === NodeClass.Variable) {
      meta.dataType = this.value.dataType.key;
      meta.arrayType = this.value.arrayType.key;
    } else {
      meta.nodeClass = this.nodeClass.key;
    }

    meta.references = sortReferences(this.references.toJSON());

    /* FIXME: Once plugin mapping is implemented
    for (const unresolved of this._unresolved) {
      let value = this[unresolved];

      if (unresolved === 'dataType') {
        value = this.value.dataType ? this.value.dataType.key : 'UNKNOWN';
      } else if (unresolved === 'arrayType') {
        value = this.value.arrayType ? this.value.arrayType.key : 'UNKNOWN';
      }

      meta[unresolved] = value;
    }


    if (this._unresolvedReferences.size) {
      meta.references = sortReferences(this._unresolvedReferences.toJSON());
    }
    */

    return meta;
  }

  // Manipulation

  /**
   * Creates a new child node.
   * @param {Object} options The options to use.
   * @param {string} options.extension The extension to append to the node's name.
   */
  createChild({ extension }) {
    const node = new Node({
      name: this.idName,
      parent: this,
      nodeClass: this.nodeClass,
    });

    Object.setPrototypeOf(node, this.constructor.prototype);

    node.fileName = `${this.fileName}${extension}`;

    node.references = this.references;
    node._parentResolvesMetadata = true;

    return node;
  }

  // Convenience getters

  /**
   * The node's data type.
   * @type {node-opcua~DataType}
   */
  get dataType() {
    return this.value.dataType;
  }

  /**
   * The node's array type.
   * @type {node-opcua~VariantArrayType}
   */
  get arrayType() {
    return this.value.arrayType;
  }

  /**
   * If the node is a variable.
   * @type {boolean}
   */
  get isVariable() {
    return this.nodeClass === NodeClass.Variable;
  }

  // FIXME: Move to display / script transformers

  /**
   * If the node is an object display.
   * @type {boolean}
   */
  get isDisplay() {
    return this.hasTypeDefinition('VariableTypes.ATVISE.Display');
  }

  /**
   * If the node is a serverside script.
   * @type {boolean}
   */
  get isScript() {
    return this.hasTypeDefinition('VariableTypes.ATVISE.ScriptCode');
  }

  /**
   * If the node is a quickdynamic.
   * @type {boolean}
   */
  get isQuickDynamic() {
    return this.hasTypeDefinition('VariableTypes.ATVISE.QuickDynamic');
  }

}

/**
 * A node during a *pull*.
 */
export class ServerNode extends Node {

  /**
   * The node's name.
   * @type {string}
   */
  get name() {
    return this.fileName;
