import readline from 'readline';
import Logger from 'gulplog';
import Session from '../lib/server/Session';

/**
 * Cleans up after the app ended with the specified code or signal.
 * @param {?Number} exitCode The exit code received.
 * @param {?string} signal The signal that triggered the exit.
 * @param {function()} uninstall The uninstall script to run.
 * @return {boolean} `true` if the process should continue exiting.
 */
export default function cleanup(exitCode, signal, uninstall) {
  uninstall();

  if (signal === 'SIGINT') {
    readline.clearLine(process.stdout);
    readline.cursorTo(process.stdout, 0);
    Logger.warn('Ctrl-C');
  }

  Logger.debug('Running cleanup...');

  if (Session.open.length > 0) {
    Logger.debug('  Closing', Session.open.length, 'open sessions...');

    // Ignore further gulp error messages
    Logger.removeAllListeners('error');
    Logger.on('error', () => {});

    Session.closeOpen()
      .then(() => process.kill(process.pid, signal))
      .catch(e => {
        Logger.error('Error in cleanup', e.message);
        process.kill(process.pid, signal);
      });

    return false;
  }

  return true;
}

import { resolve as resolvePath } from 'path';
import { stat } from 'fs';

/**
 * Returns the {@link fs~Stats} for a path.
 * @param {string} path The path to look at.
 * @return {Promise<fs~Stats, Error>} Fulfilled with the requested stats or rejected with the error
 * that occurred.
 */
export function getStat(path) {
  return new Promise((resolve, reject) => {
    stat(resolvePath(path), (err, stats) => {
      if (err) {
        reject(err);
      } else {
        resolve(stats);
      }
    });
  });
}

/**
 * Checks it a given path holds a directory and returns it's {@link fs~Stats} if found.
 * @param {string} directoryPath The path to look at.
 * @return {Promise<fs~Stats, Error>} Fulfilled with the directory's stats or rejected with the
 * error that occurred.
 */
export function validateDirectoryExists(directoryPath) {
  return getStat(directoryPath)
    .then(stats => {
      if (!(stats.isDirectory())) {
        throw new Error(`${resolvePath(directoryPath)} is not a directory`);
      }

      return stat;
    });
}

