import { inspect } from 'util';
import { ctor as throughStreamClass } from 'through2';
import Logger from 'gulplog';

/**
 * The directions a transformer can be run in.
 * @type {{FromDB: string, FromFilesystem: string}}
 */
export const TransformDirection = {
  FromDB: 'FromDB',
  FromFilesystem: 'FromFilesystem',
};

/**
 * Checks if the given string is a valid {@link TransformDirection}.
 * @param {string} direction The direction string to check.
 * @return {boolean} `true` if the direction is valid.
 */
function isValidDirection(direction) {
  return [
    TransformDirection.FromDB,
    TransformDirection.FromFilesystem,
  ].includes(direction);
}

/**
 * A special kind of an object transform stream: It does apply different transformations based on
 * it's direction.
 * @abstract
 */
export default class Transformer extends throughStreamClass({ objectMode: true }) {

  /**
   * Creates a new Transformer with the specified options.
   * @param {Object} [options] The options to use.
   * @param {TransformDirection} [options.direction] The direction to use.
   * @throws {Error} Throws an error if the given direction is invalid.
   */
  constructor(options = {}) {
    super();

    /**
     * The options the transformer was created with. Used for printing description.
     * @type {Object}
     */
    this._options = options;

    if (options.direction) {
      if (isValidDirection(options.direction)) {
        /**
         * The transformer's direction
         * @type {TransformerDirection}
         */
        this.direction = options.direction;
      } else {
        throw new Error('Invalid direction');
      }
    }
  }

  /**
   * Returns the Transformer with the given direction.
   * @param {TransformDirection} direction The direction to use.
   * @return {Transformer} Itself, to be chainable.
   * @throws {Error} Throws an error if the given direction is invalid.
   */
  withDirection(direction) {
    if (!isValidDirection(direction)) {
      throw new Error('Invalid direction');
    }

    this.direction = direction;
    return this;
  }

  _processError(err, chunk, callback, ...args) {
    if (err) {
      const id = (this.direction === TransformDirection.FromDB ?
        chunk.nodeId :
        chunk.relative) || chunk.toString();

      // eslint-disable-next-line no-param-reassign
      err.message = `[${this.constructor.name}] ${err.message} (in ${id})`;

      if (process.env.CONTINUE_ON_FAILURE) {
        Logger.error(err.message);
        callback(null);
      } else {
        callback(err);
      }
    } else {
      callback(err, ...args);
    }
  }

  /**
   * Calls {@link Transformer#transformFromDB} or {@link Transformer#transformFromFilesystem}
   * based on the transformer's direction.
   * @param {Object} chunk The chunk to transform.
   * @param {string} enc The encoding used.
   * @param {function(err: ?Error, obj: ?Object)} callback Called with the error that occured while
   * transforming or (optionally) the transformed object.
   * @throws {Error} Throws an error if the transformer has no valid direction.
   */
  _transform(chunk, enc, callback) {
    const processError = (err, ...args) => this._processError(err, chunk, callback, ...args);

    if (!this.direction) {
      callback(new Error('Transformer has no direction'));
    } else if (this.direction === TransformDirection.FromDB) {
      this.transformFromDB(chunk, enc, processError);
    } else {
      this.transformFromFilesystem(chunk, enc, processError);
    }
  }

  /**
   * **Must be overridden by all subclasses:** Transforms the given chunk when using
   * {@link TransformDirection.FromDB}.
   * @param {Object} chunk The chunk to transform.
   * @param {string} enc The encoding used.
   * @param {function(err: ?Error, obj: ?Object)} callback Called with the error that occured while
   * transforming or (optionally) the transformed object.
   */
  transformFromDB(chunk, enc, callback) {
    callback(new Error('Transformer#transformFromDB must be overridden by all subclasses'));
  }

  /**
   * **Must be overridden by all subclasses:** Transforms the given chunk when using
   * {@link TransformDirection.FromFilesystem}.
   * @param {Object} chunk The chunk to transform.
   * @param {string} enc The encoding used.
   * @param {function(err: ?Error, obj: ?Object)} callback Called with the error that occured while
   * transforming or (optionally) the transformed object.
   */
  transformFromFilesystem(chunk, enc, callback) {
    callback(new Error('Transformer#transformFromFilesystem must be overridden by all subclasses'));
  }

  /**
   * Applies the transformer to the given stream. By default this just invokes
   * {@link Transformer#transformFromDB} or {@link Transformer#transformFromFilesystem}. Override
   * this method if you want to pipe streams directly.
   * @param {Stream} stream The stream to apply the transformer to.
   * @param {TransformDirection} direction The direction to use.
   * @return {Stream} The resulting stream.
   */
  applyToStream(stream, direction) {
    return stream.pipe(this.withDirection(direction));
  }

  /**
   * Creates a stream with all transformers passed, with the given direction. Transformers are
   * reversed if using {@link TransformDirection.FromFilesystem}.
   * @param {Stream} stream The stream to apply the transformers to.
   * @param {Transformer[]} transformers The transformers to apply.
   * @param {TransformDirection} direction The direction to use.
   * @return {Transformer} The last transformer passed, piped to the previous.
   */
  static applyTransformers(stream, transformers, direction) {
    if (!isValidDirection(direction)) {
      throw new Error('Direction is invalid');
    }

    if (transformers.length === 0) {
      return stream;
    }

    return (direction === TransformDirection.FromDB ? transformers : transformers.reverse())
      .reduce((prev, curr) => curr.applyToStream(prev, direction), stream);
  }

  /**
   * Prints the transformer.
   * @param {?Number} depth The depth to inspect.
   * @param {Object} options See {@link util~inspect} for details.
   * @return {string} A string representation of the transformer.
   */
  inspect(depth, options) {
    const newOptions = options;
    newOptions.depth = options.depth === null ? null : options.depth - 1;

    if (depth < 0) {
      return options.stylize(`[${this.constructor.name}]`, 'special');
    }

    return `${options.stylize(this.constructor.name, 'special')}${inspect(this._options, newOptions)
      .replace(/^{/, '<').replace(/}$/, '>')
      .replace(/\n/, `\n${' '.repeat(this.constructor.name.length)}`)
    }`;
  }

}

import { xml2js, js2xml } from 'xml-js';
import { TransformDirection } from './Transformer';
import SplittingTransformer from './SplittingTransformer';

/**
 * A transformer used to transform XML documents.
 */
export default class XMLTransformer extends SplittingTransformer {

  /**
   * Creates a new XMLTransformer based on some options.
   * @param {Object} options The options to use.
   */
  constructor(options) {
    super(options);

    function build(object, buildOptions) {
      if (!object.declaration) {
        Object.assign(object, {
          declaration: {
            attributes: { version: '1.0', encoding: 'UTF-8', standalone: 'no' },
          },
        });
      }

      return js2xml(object, Object.assign(buildOptions, {
        attributeValueFn(val) {
          return val
            .replace(/&(?!amp;)/g, '&amp;')
            .replace(/</g, '&lt;');
        },
      }));
    }

    // eslint-disable-next-line jsdoc/require-param
    /**
     * The builder to use with direction {@link TransformDirection.FromDB}.
     * @type {function(object: Object): string}
     */
    this._fromDBBuilder = object => build(object, { compact: false, spaces: 2 });

    // eslint-disable-next-line jsdoc/require-param
    /**
     * The builder to use with direction {@link TransformDirection.FromFilesystem}.
     * @type {function(object: Object): string}
     */
    this._fromFilesystemBuilder = object => {
      const xml = build(object, { compact: false, spaces: 1 });
      return xml.replace(/\r?\n/g, '\r\n');
    };
  }

  /**
   * Returns the XML builder to use based on the current {@link Transformer#direction}.
   * @type {function(object: Object): string}
   */
  get builder() {
    return this.direction === TransformDirection.FromDB ?
      this._fromDBBuilder :
      this._fromFilesystemBuilder;
  }

  /**
   * Parses XML in a file's contents.
   * @param {AtviseFile} file The file to process.
   * @param {function(err: ?Error, result: ?Object)} callback Called with the parsed document or the
   * parse error that occurred.
   */
  decodeContents(file, callback) {
    try {
      callback(null, xml2js(file.contents, { compact: false }));
    } catch (e) {
      callback(e);
    }
  }

  /**
   * Builds an XML string from an object.
   * @param {Object} object The object to encode.
   * @param {function(err: ?Error, result: ?String)} callback Called with the resulting string or
   * the error that occurred while building.
   */
  encodeContents(object, callback) {
    try {
      callback(null, this.builder(object));
    } catch (e) {
      callback(e);
    }
  }

  /**
   * Tells if the given parsed XML node is an element (not a text / cdata node).
   * @param {Object} node The parsed node.
   * @return {boolean} `true` if the node has type 'element'.
   */
  isElement({ type }) {
    return type === 'element';
  }

  /** Tells if the given parsed XML node is an elment and has the given tag name.
   * @param {Object} node The parsed node.
   * @param {string} tagName The tag name to check for.
   * @return {boolean} `true` if the node is an element with the given tag name.
   */
  isElementWithName({ type, name }, tagName) {
    return this.isElement({ type }) && name === tagName;
  }

  /**
   * Returns a node's child elements with the given tag name.
   * @param {Object} node The node to check in.
   * @param {string} tagName The tag name to search for.
   * @return {Object[]} The matching child elements.
   */
  findChildren(node, tagName) {
    if (!node || !node.elements) { return []; }

    return node.elements.filter(child => this.isElementWithName(child, tagName));
  }

  /**
   * Returns a node's first child element with the given tag name, or `null`.
   * @param {Object} node The node to check in.
   * @param {string} tagName The tag name to search for.
   * @return {Object?} The matching child elements.
   */
  findChild(node, tagName) {
    if (!node || !node.elements) { return null; }

    for (let i = 0; i < node.elements.length; i++) {
      if (this.isElementWithName(node.elements[i], tagName)) {
        return node.elements[i];
      }
    }

    return null;
  }

  /**
   * Returns and removes a node's child elements with the given tag name.
   * @param {Object} node The node to check in.
   * @param {string} tagName The tag name to search for.
   * @return {Object[]} The matching child elements.
   */
  removeChildren(node, tagName) {
    if (!node || !node.elements) { return []; }

    const removed = [];

    // eslint-disable-next-line no-param-reassign
    node.elements = node.elements.filter(child => {
      if (this.isElementWithName(child, tagName)) {
        removed.push(child);
        return false;
      }
      return true;
    });

    return removed;
  }

  /**
   * Returns and removes a node's first child element with the given tag name, if no match is found
   * `null` is returned.
   * @param {Object} node The node to check in.
   * @param {string} tagName The tag name to search for.
   * @return {Object?} The matching child elements.
   */
  removeChild(node, tagName) {
    if (!node || !node.elements) { return null; }

    for (let i = 0; i < node.elements.length; i++) {
      if (this.isElementWithName(node.elements[i], tagName)) {
        return node.elements.splice(i, 1)[0];
      }
    }

    return null;
  }

  /**
   * Returns a parsed node's text content. Works for 'text' and 'cdata' nodes.
   * @param {Object} node The parsedNode.
   * @return {string?} The nodes text content.
   */
  textContent(node) {
    if (!node || !node.elements) { return null; }

    const contentNode = node.elements[0];

    // FIXME: Only works for { type: 'text', text: 'value' } and { type: 'cdata', cdata: 'data' }
    return contentNode[contentNode.type];
  }

}

import { join } from 'path';
import { src } from 'gulp';
import ImportStream from '../lib/gulp/ImportStream';

/**
 * Imports all xml files needed for atscm usage.
 * @return {ImportStream} The import stream used.
 */
export default function importTask() {
  const srcStream = src(join(__dirname, '../../res/import/scripts', '/**/*.xml'));

  return srcStream
    .pipe(new ImportStream());
}

importTask.description = 'Imports all xml resources needed for atscm usage';

import ProjectConfig from '../config/ProjectConfig';
import NodeStream from '../lib/server/NodeStream';
import ReadStream from '../lib/server/ReadStream';
import PullStream from '../lib/gulp/PullStream';

/**
 * Pulls all nodes from atvise server.
 */
export default function pull() {
  return new PullStream(
    (new NodeStream(ProjectConfig.nodes))
      .pipe(new ReadStream())
  );
}

pull.description = 'Pull all nodes from atvise server';

import { src } from 'gulp';
import PushStream from '../lib/gulp/PushStream';

/**
 * Pushes {@link AtviseFile}s to atvise server.
 */
export default function push() {
  return new PushStream(src(['./src/**/.*', '!./src/**/.*.rc', './src/**/*.*']));
}

push.description = 'Push all stored nodes to atvise server';

import { join } from 'path';
import { src } from 'gulp';
import sane from 'sane';
import browserSync from 'browser-sync';
import Logger from 'gulplog';
import { obj as createStream } from 'through2';
import PushStream from '../lib/gulp/PushStream';
import PullStream from '../lib/gulp/PullStream';
import AtviseFile from '../lib/server/AtviseFile';
import ServerWatcher from '../lib/server/Watcher';
import ProjectConfig from '../config/ProjectConfig';
import { validateDirectoryExists } from '../util/fs';

/**
 * The task executed when running `atscm watch`.
 */
export class WatchTask {

  /**
   * Creates a new watch task instance. Also creates a new Browsersync instance.
   */
  constructor() {
    /**
     * The Browsersync instance used.
     * @type {events~Emitter}
     */
    this.browserSyncInstance = browserSync.create();

    /**
     * If the task is currently pulling.
     * @type {boolean}
     */
    this._pulling = false;

    /**
     * If the task is currently pushing.
     * @type {boolean}
     */
    this._pushing = false;

    /**
     * Timestamp of the last pull
     * @type {number}
     */
    this._lastPull = 0;

    /**
     * The {@link NodeId} of the last push.
     * @type {?NodeId}
     */
    this._lastPushed = null;
  }

  /**
   * The directory to watch.
   * @type {string}
   */
  get directoryToWatch() {
    return './src';
  }

  /**
   * Waits for a watcher (which can actually be any kind of {@link events~Emitter}) to emit a
   * "ready" event.
   * @param {events~Emitter} watcher The watcher to wait for.
   * @return {Promise<events~Emitter, Error>} Fulfilled with the set up watcher or rejected with the
   * watcher error that occurred while waiting for it to get ready.
   */
  _waitForWatcher(watcher) {
    return new Promise((resolve, reject) => {
      watcher.on('error', err => reject(err));
      watcher.on('ready', () => resolve(watcher));
    });
  }

  /**
   * Starts a file watcher for the directory {@link WatchTask#directoryToWatch}.
   * @return {Promise<sane~Watcher, Error>} Fulfilled with the file watcher once it is ready or
   * rejected with the error that occurred while starting the watcher.
   */
  startFileWatcher() {
    return validateDirectoryExists(this.directoryToWatch)
      .catch(err => {
        if (err.code === 'ENOENT') {
          Logger.info(`Create a directory at ${this.directoryToWatch} or run \`atscm pull\` first`);

          Object.assign(err, {
            message: `Directory ${this.directoryToWatch} does not exist`,
          });
        }

        throw err;
      })
      .then(() => this._waitForWatcher(sane(this.directoryToWatch, {
        glob: '**/*.*',
        watchman: process.platform === 'darwin',
      })));
  }

  /**
   * Starts a watcher that watches the atvise server for changes.
   * @return {Promise<Watcher, Error>} Fulfilled with the server watcher once it is ready or
   * rejected with the error that occurred while starting the watcher.
   */
  startServerWatcher() {
    return this._waitForWatcher(new ServerWatcher());
  }

  /**
   * Initializes {@link WatchTask#browserSyncInstance}.
   */
  initBrowserSync() {
    this.browserSyncInstance.init({
      proxy: `${ProjectConfig.host}:${ProjectConfig.port.http}`,
      ws: true,
      // logLevel: 'debug', FIXME: Use log level specified in cli options
      // logPrefix: '',
    });

    /* bs.logger.logOne = function(args, msg, level, unprefixed) {
      args = args.slice(2);

      if (this.config.useLevelPrefixes && !unprefixed) {
        msg = this.config.prefixes[level] + msg;
      }

      msg = this.compiler.compile(msg, unprefixed);

      args.unshift(msg);

      Logger[level](format(...args));

      this.resetTemps();

      return this;
    }; */
  }

  /**
   * Handles a file change.
   * @param {string} path The path of the file that changed.
   * @param {string} root The root of the file that changed.
   * @param {fs~Stats} stats The stats of the file that changed.
   * @return {Promise<boolean>} Resolved with `true` if the change triggered a push operation,
   * with `false` otherwise.
   */
  handleFileChange(path, root, stats) {
    return new Promise(resolve => {
      if (!this._pulling && AtviseFile.normalizeMtime(stats.mtime) > this._lastPull) {
        this._pushing = true;
        Logger.info(path, 'changed');

        const source = src(join(root, path), { base: root });

        (new PushStream(source))
          .on('data', file => (this._lastPushed = file.nodeId.toString()))
          .on('end', () => {
            this._pushing = false;
            this.browserSyncInstance.reload();

            resolve(true);
          });
      } else {
        resolve(false);
      }
    });
  }

  /**
   * Handles an atvise server change.
   * @param {ReadStream.ReadResult} readResult The read result of the modification.
   * @return {Promise<boolean>} Resolved with `true` if the change triggered a pull operation,
   * with `false` otherwise.
   */
  handleServerChange(readResult) {
    return new Promise(resolve => {
      if (!this._pushing) {
        if (readResult.nodeId.toString() !== this._lastPushed) {
          this._pulling = true;
          Logger.info(readResult.nodeId.toString(), 'changed');

          const readStream = createStream();
          readStream.write(readResult);
          readStream.end();

          (new PullStream(readStream))
            .on('end', () => {
              this._pulling = false;
              this._lastPull = AtviseFile.normalizeMtime(readResult.mtime);
              this.browserSyncInstance.reload();

              resolve(true);
            });
        } else {
          this._lastPushed = null;

          resolve(false);
        }
      } else {
        resolve(false);
      }
    });
  }

  /**
   * Starts the file and server watchers, initializes Browsersync and registers change event
   * handlers.
   * @return {Promise<undefined, Error>} Fulfilled once all watchers are set up and Browsersync was
   * initialized.
   */
  run() {
    return Promise.all([
      this.startFileWatcher(),
      this.startServerWatcher(),
    ])
      .then(([fileWatcher, serverWatcher]) => {
        this.browserSyncInstance.emitter.on('service:running', () => {
          Logger.info('Watching for changes...');
          Logger.debug('Press Ctrl-C to exit');
        });

        fileWatcher.on('change', this.handleFileChange.bind(this));
        serverWatcher.on('change', this.handleServerChange.bind(this));

        this.initBrowserSync();
      });
  }

}

/**
 * The gulp task invoced when running `atscm watch`.
 * @return {Promise<undefined, Error>} Fulfilled once all watchers are set up and Browsersync was
 * initialized.
 */
export default function watch() {
  return (new WatchTask()).run();
}

watch.description = 'Watch local files and atvise server nodes to trigger pull/push on change';

import { Buffer } from 'buffer';
import XMLTransformer from '../lib/transform/XMLTransformer';

/**
 * Splits read atvise display XML nodes into their SVG and JavaScript sources,
 * alongside with a .json file containing the display's parameters.
 */
export default class DisplayTransformer extends XMLTransformer {

  /**
   * Returns true for all files containing atvise displays.
   * @param {AtviseFile} file The file to check.
   * @return {boolean} `true` for all atvise display files.
   */
  shouldBeTransformed(file) {
    return file.isDisplay;
  }

  /**
   * Splits any read files containing atvise displays into their SVG and JavaScript sources,
   * alongside with a json file containing the display's parameters.
   * @param {AtviseFile} file The display file to split.
   * @param {string} enc The encoding used.
   * @param {function(err: Error, file: AtviseFile)} callback Called with the error that occured
   * while transforming the display, or the file passed through.
   */
  transformFromDB(file, enc, callback) {
    this.decodeContents(file, (err, results) => {
      if (err) {
        callback(err);
      } else if (!results) {
        callback(new Error('Error parsing display: No `svg` tag'));
      } else {
        const xml = results;
        const document = this.findChild(xml, 'svg');

        if (!document) {
          callback(new Error('Error parsing display: No `svg` tag'));
          return;
        }

        const config = {};
        const scriptTags = this.removeChildren(document, 'script');

        // Extract JavaScript
        if (scriptTags.length) {
          scriptTags.forEach(script => {
            if (script.attributes && (script.attributes.src || script.attributes['xlink:href'])) {
              if (!config.dependencies) {
                config.dependencies = [];
              }

              config.dependencies.push(script.attributes.src || script.attributes['xlink:href']);
            } else {
              // TODO: Warn on multiple inline scripts

              const scriptContentNode = script.elements ?
                script.elements[0] :
                { type: 'text', text: '' };

              const scriptFile = DisplayTransformer.splitFile(file, '.js');

              const scriptText = scriptContentNode[scriptContentNode.type] || '';

              scriptFile.contents = Buffer.from(scriptText);
              this.push(scriptFile);
            }
          });
        }

        // Extract metadata
        const metaTag = this.findChild(document, 'metadata');
        if (metaTag && metaTag.elements) {
          // TODO: Warn on multiple metadata tags

          // - Parameters
          const paramTags = this.removeChildren(metaTag, 'atv:parameter');
          if (paramTags.length) {
            config.parameters = [];

            paramTags.forEach(({ attributes }) => config.parameters.push(attributes));
          }
        }

        const configFile = DisplayTransformer.splitFile(file, '.json');

        configFile.contents = Buffer.from(JSON.stringify(config, null, '  '));
        this.push(configFile);

        const svgFile = DisplayTransformer.splitFile(file, '.svg');

        this.encodeContents(xml, (encodeErr, stringValue) => {
          if (encodeErr) {
            callback(encodeErr);
          } else {
            svgFile.contents = Buffer.from(stringValue);
            this.push(svgFile);

            callback(null);
          }
        });
      }
    });
  }

  /**
   * Creates a display from the collected files.
   * @param {Map<String, vinyl~File>} files The collected files, stored against their extension.
   * @param {vinyl~File} lastFile The last file read. *Used for error messages only*.
   * @param {function(err: ?Error, data: vinyl~File)} callback Called with the error that occured
   * while creating the display or the resulting file.
   */
  createCombinedFile(files, lastFile, callback) {
    const configFile = files['.json'];
    let config = {};

    if (configFile) {
      try {
        config = JSON.parse(configFile.contents.toString());
      } catch (e) {
        callback(new Error(`Error parsing JSON in ${configFile.relative}: ${e.message}`));
        return;
      }
    }

    const svgFile = files['.svg'];
    if (!svgFile) {
      callback(new Error(`No display SVG in ${lastFile.dirname}`));
      return;
    }

    const scriptFile = files['.js'];
    let inlineScript = '';
    if (scriptFile) {
      inlineScript = scriptFile.contents.toString();
    }

    this.decodeContents(svgFile, (err, xml) => {
      if (err) {
        callback(err);
      } else {
        const result = xml;
        const svg = this.findChild(result, 'svg');

        if (!svg) {
          callback(new Error('Error parsing display SVG: No `svg` tag'));
          return;
        }

        // Handle empty svg tag
        if (!svg.elements) {
          svg.elements = [];
        }

        // Insert dependencies
        if (config.dependencies) {
          config.dependencies.forEach(src => {
            svg.elements.push({
              type: 'element',
              name: 'script',
              attributes: { 'xlink:href': src },
            });
          });
        }

        // Insert script
        // FIXME: Import order is not preserved!
        if (scriptFile) {
          svg.elements.push({
            type: 'element',
            name: 'script',
            attributes: { type: 'text/ecmascript' },
            elements: [
              {
                type: 'cdata',
                cdata: inlineScript,
              },
            ],
          });
        }

        // Insert metadata
        // - Parameters
        if (config.parameters && config.parameters.length > 0) {
          let metaTag = this.removeChild(svg, 'metadata');

          if (!metaTag) {
            metaTag = { type: 'element', name: 'metadata' };
          }

          if (!metaTag.elements) {
            metaTag.elements = [];
          }

          // Parameters should come before other atv attributes, e.g. `atv:gridconfig`
          for (let i = config.parameters.length - 1; i >= 0; i--) {
            metaTag.elements.unshift({
              type: 'element',
              name: 'atv:parameter',
              attributes: config.parameters[i],
            });
          }

          // Insert <metadata> as first element in the resulting svg
          svg.elements.unshift(metaTag);
        }

        const display = DisplayTransformer.combineFiles(
          Object.keys(files).map(ext => files[ext]),
          '.xml'
        );

        this.encodeContents(result, (encodeErr, xmlString) => {
          if (encodeErr) {
            callback(encodeErr);
          } else {
            display.contents = Buffer.from(xmlString);

            callback(null, display);
          }
        });
      }
    });
  }

}

import { readFile } from 'fs';
import Logger from 'gulplog';
import { NodeClass } from 'node-opcua';
import Transformer from '../lib/transform/Transformer';
import AtviseFile from '../lib/server/AtviseFile';
import NodeId from '../lib/model/opcua/NodeId';

/**
 * A Transformer that maps {@link ReadStream.ReadResult}s to {@link AtviseFile}s.
 */
export default class MappingTransformer extends Transformer {

  /**
   * Writes an {@link AtviseFile} for each {@link ReadStream.ReadResult} read. If a read file has a
   * non-standard type (definition) an additional `rc` file is pushed holding this type.
   * @param {ReadStream.ReadResult} readResult The read result to create the file for.
   * @param {string} encoding The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error that occurred
   * while transforming the read result or the resulting file.
   */
  transformFromDB(readResult, encoding, callback) {
    try {
      const file = AtviseFile.fromReadResult(readResult);

      if (file.relative.match(/\.var\./)) {
        const rc = file.clone();

        rc.extname = '';
        rc.basename = `.${rc.stem}.rc`;

        rc.contents = Buffer.from(JSON.stringify({
          typeDefinition: file.typeDefinition,
        }, null, '  '));

        this.push(rc);
      }

      callback(null, file);
    } catch (e) {
      Logger[e.message === 'no value' ? 'debug' : 'warn'](
        `Unable to map ${readResult.nodeId.value}: ${e.message}`
      );
      Logger.debug(e);

      callback(null);
    }
  }

  /**
   * Writes an {@link AtviseFile} for each {@link vinyl~File} read.
   * @param {vinyl~File} file The raw file.
   * @param {string} encoding The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error that occurred
   * while transforming the read result or the resulting file.
   */
  transformFromFilesystem(file, encoding, callback) {
    if (file.isDirectory()) {
      callback(null);
    } else if (file.stem[0] === '.' && !NodeClass[file.stem.slice(1)]) {
      Logger.debug('Ignoring file', file.relative);
      callback(null);
    } else {
      const atFile = new AtviseFile({
        cwd: file.cwd,
        base: file.base,
        path: file.path,
        contents: file.contents,
      });

      if (file.relative.match(/\.var\./)) {
        const rcFile = file.clone({ contents: false });
        rcFile.extname = '';
        rcFile.basename = `.${rcFile.stem}.rc`;

        readFile(rcFile.path, 'utf8', (err, data) => {
          try {
            const rc = JSON.parse(data);
            atFile._typeDefinition = new NodeId(rc.typeDefinition);

            callback(null, atFile);
          } catch (e) {
            Logger.error(`Unable to get runtime configuration for ${file.relative}`);
            callback(err || e);
          }
        });
      } else {
        callback(null, atFile);
      }
    }
  }

}

import Logger from 'gulplog';
import XMLTransformer from '../lib/transform/XMLTransformer';

/**
 * A transformer that splits atvise scripts and quick dynamics into a code file and a .json file
 * containing parameters and metadata.
 */
export default class ScriptTransformer extends XMLTransformer {

  /**
   * Returns `true` for all files containing script code or quick dynamics.
   * @param {AtviseFile} file The file to check.
   * @return {boolean} `true` for all files containing script code or quick dynamics.
   */
  shouldBeTransformed(file) {
    return file.isScript || file.isQuickDynamic;
  }

  /**
   * Splits any read files containing scripts or quick dynamics into their JavaScript sources,
   * alongside with a json file containing parameters and metadata.
   * @param {AtviseFile} file The script file to split.
   * @param {string} enc The encoding used.
   * @param {function(err: Error, file: AtviseFile)} callback Called with the error that occured
   * while transforming the script, or the file passed through.
   */
  transformFromDB(file, enc, callback) {
    this.decodeContents(file, (err, results) => {
      if (err) {
        callback(err);
      } else {
        const document = results && this.findChild(results, 'script');

        if (!document) {
          Logger.warn(`Empty document at ${file.relative}`);
        }

        const config = {};

        // Extract metadata
        const metaTag = this.findChild(document, 'metadata');
        if (metaTag && metaTag.elements) {
          // TODO: Warn on multiple metadata tags
          metaTag.elements.forEach(child => {
            if (child.type === 'element') {
              if (child.name === 'icon') { // - Icon
                config.icon = Object.assign({
                  content: this.textContent(child) || '',
                }, child.attributes);
              } else if (child.name === 'visible') { // - Visible
                config.visible = Boolean(parseInt(this.textContent(child), 10));
              } else if (child.name === 'title') {
                config.title = this.textContent(child);
              } else if (child.name === 'description') {
                config.description = this.textContent(child);
              } else {
                Logger.warn(`Unknown metadata element '${child.name}' at ${file.relative}`);
              }
            }
          });
        }

        // Extract Parameters
        const paramTags = this.findChildren(document, 'parameter');
        if (paramTags.length) {
          config.parameters = [];
          paramTags.forEach(({ attributes }) => config.parameters.push(attributes));
        }

        // Extract JavaScript
        const codeNode = this.findChild(document, 'code');
        const code = this.textContent(codeNode) || '';

        // Write config file
        const configFile = ScriptTransformer.splitFile(file, '.json');
        configFile.contents = Buffer.from(JSON.stringify(config, null, '  '));
        this.push(configFile);

        // Write script file
        const scriptFile = ScriptTransformer.splitFile(file, '.js');
        scriptFile.contents = Buffer.from(code);
        this.push(scriptFile);

        callback(null);
      }
    });
  }

  /**
   * Creates a script from the collected files.
   * @param {Map<String, AtviseFile>} files The collected files, stored against their extension.
   * @param {AtviseFile} lastFile The last file read. *Used for error messages only*.
   * @param {function(err: ?Error, data: vinyl~File)} callback Called with the error that occured
   * while creating the script or the resulting file.
   */
  createCombinedFile(files, lastFile, callback) {
    const configFile = files['.json'];
    let config = {};

    if (configFile) {
      try {
        config = JSON.parse(configFile.contents.toString());
      } catch (e) {
        callback(new Error(`Error parsing JSON in ${configFile.relative}: ${e.message}`));
        return;
      }
    }

    const scriptFile = files['.js'];
    let code = '';

    if (scriptFile) {
      code = scriptFile.contents.toString();
    }

    const document = {
      type: 'element',
      name: 'script',
      elements: [],
    };

    const result = {
      elements: [
        document,
      ],
    };

    // Insert metadata
    if (lastFile.isQuickDynamic) {
      const meta = [];

      // - Icon
      if (config.icon) {
        const icon = config.icon.content;
        delete config.icon.content;

        meta.push({
          type: 'element',
          name: 'icon',
          attributes: config.icon,
          elements: [
            {
              type: 'text',
              text: icon,
            },
          ],
        });
      }

      // - Other fields
      if (config.visible !== undefined) {
        meta.push({
          type: 'element',
          name: 'visible',
          elements: [
            {
              type: 'text',
              text: config.visible ? 1 : 0,
            },
          ],
        });
      }

      if (config.title !== undefined) {
        meta.push({
          type: 'element',
          name: 'title',
          elements: [
            { type: 'text', text: config.title },
          ],
        });
      }

      if (config.description !== undefined) {
        meta.push({
          type: 'element',
          name: 'description',
          elements: [
            { type: 'text', text: config.description },
          ],
        });
      }

      document.elements.push({
        type: 'element',
        name: 'metadata',
        elements: meta,
      });
      // result.script.metadata = meta;
    }

    // Insert parameters
    if (config.parameters) {
      config.parameters.forEach(attributes => {
        document.elements.push({
          type: 'element',
          name: 'parameter',
          attributes,
        });
      });
    }

    // Insert script code
    document.elements.push({
      type: 'element',
      name: 'code',
      elements: [
        {
          type: 'cdata',
          cdata: code,
        },
      ],
    });

    const script = ScriptTransformer.combineFiles(
      Object.keys(files).map(ext => files[ext]),
      '.xml'
    );

    this.encodeContents(result, (encodeErr, xmlString) => {
      if (encodeErr) {
        callback(encodeErr);
      } else {
        script.contents = Buffer.from(xmlString);

        callback(null, script);
      }
    });
  }

}

