  },
}).default;

const FailingSubscribeStream = proxyquire('../../../../src/lib/server/Watcher', {
  'node-opcua': {
    ClientSubscription: class StubClientSubscription extends Emitter {

      constructor() {
        super();

        this.monitor = spy(() => new StubMonitoredItem());

