class StubSplittingTransformer extends proxyquire(
  '../../../../src/lib/transform/SplittingTransformer',
  {
    '../server/AtviseFile': {
      _esModule: true,
      default: class StubAtviseFile extends AtviseFile {

        static read(options) {
