import { Buffer } from 'buffer';
import File from 'vinyl';
import { stub } from 'sinon';
import expect from '../../expect';
import AtviseFile from '../../../src/lib/server/AtviseFile';
import { TransformDirection } from '../../../src/lib/transform/Transformer';
import DisplayTransformer from '../../../src/transform/DisplayTransformer';

/** @test {DisplayTransformer} */
describe('DisplayTransformer', function() {
  const nonDisplayFile = { isDisplay: false };

  /** @test {DisplayTransformer#shouldBeTransformed} */
  describe('#shouldBeTransformed', function() {
    it('should return false for non-display files', function() {
      expect(DisplayTransformer.prototype.shouldBeTransformed(nonDisplayFile), 'to equal', false);
    });
  });

  /** @test {DisplayTransformer#transformFromDB} */
  describe('#transformFromDB', function() {
    function writeXMLToDisplayTransformer(xmlString) {
      const transformer = new DisplayTransformer({ direction: TransformDirection.FromDB });
      const file = new AtviseFile({
        path: 'AGENT/DISPLAYS/Main.display.xml',
        contents: Buffer.from(`<?xml version="1.0" encoding="UTF-8" standalone="no"?>
${xmlString}`),
      });

      const data = [];
      transformer.on('data', d => data.push(d));

      const promise = new Promise((resolve, reject) => {
        transformer.once('error', err => reject(err));
        transformer.once('end', () => resolve(data));
      });

      transformer.write(file);
      transformer.end();

      return promise;
    }

    it('should forward parse errors', function() {
      return expect(writeXMLToDisplayTransformer('invalid xml'),
        'to be rejected with', /Text data outside of root node/);
    });

    it('should error with invalid xml', function() {
      return expect(writeXMLToDisplayTransformer(''),
        'to be rejected with', /No `svg` tag/);
    });

    function expectFileContents(xmlString, filter) {
      return expect(writeXMLToDisplayTransformer(xmlString), 'to be fulfilled')
        .then(resultingFiles => {
          const _files = resultingFiles.filter(filter);
          try {
            expect(_files, 'to have length', 1);
          } catch (e) {
            throw new Error('Error in expectFileContents: filter returned multiple files');
          }

          const file = _files[0];

          return expect(file.contents, 'when decoded as', 'utf-8');
        });
    }

    function expectConfig(xmlString) {
      return expectFileContents(xmlString, file => file.extname === '.json')
        .then(string => {
          let obj;
          expect(() => (obj = JSON.parse(string)), 'not to throw');
          return obj;
        });
    }

    it('should store empty config for empty display', function() {
      return expectConfig('<svg></svg>')
        .then(config => {
          expect(config, 'to equal', {});
        });
    });

    it('should store dependencies in config when referenced as "src"', function() {
      return expectConfig(`<svg>
  <script src="path/to/dependency.js"></script>
</svg>`)
        .then(config => {
          expect(config.dependencies, 'to be defined');
          expect(config.dependencies, 'to have length', 1);
          expect(config.dependencies[0], 'to equal', 'path/to/dependency.js');
        });
    });

    it('should store dependencies in config when referenced as "xlink:href"', function() {
      return expectConfig(`<svg>
  <script xlink:href="path/to/dependency.js"></script>
</svg>`)
        .then(config => {
          expect(config.dependencies, 'to be defined');
          expect(config.dependencies, 'to have length', 1);
          expect(config.dependencies[0], 'to equal', 'path/to/dependency.js');
        });
    });

    it('should store multiple dependencies in config', function() {
      return expectConfig(`<svg>
  <script src="path/to/dependency1.js"></script>
  <script src="path/to/dependency2.js"></script>
</svg>`)
        .then(config => {
          expect(config.dependencies, 'to be defined');
          expect(config.dependencies, 'to have length', 2);
          expect(config.dependencies[0], 'to equal', 'path/to/dependency1.js');
          expect(config.dependencies[1], 'to equal', 'path/to/dependency2.js');
        });
    });

    context('when display contains inline script', function() {
      const script = 'console.log("called");';

      it('should be stored when given with attributes', function() {
        return expectFileContents(`<svg>
  <script type="text/javascript">${script}</script>
</svg>`, file => file.extname === '.js')
          .then(string => {
            expect(string, 'to equal', script);
          });
      });

      it('should be stored when given without attributes', function() {
        return expectFileContents(`<svg>
  <script>${script}</script>
</svg>`, file => file.extname === '.js')
          .then(string => {
            expect(string, 'to equal', script);
          });
      });

      it('should store empty inline scripts in separate file', function() {
        return expectFileContents(`<svg>
  <script type="text/javascript"></script>
</svg>`, file => file.extname === '.js')
          .then(string => {
            expect(string, 'to equal', '');
          });
      });
    });

    context('when display contains metadata tag', function() {
      it('should work without actual metadata', function() {
        return expectConfig(`<svg>
  <metadata></metadata>
</svg>`)
          .then(config => {
            expect(config, 'to be defined');
          });
      });

      it('should work without parameters', function() {
        return expectConfig(`<svg>
  <metadata>
    <atv:gridconfig height="20" width="20" enabled="false" gridstyle="lines"/>
  </metadata>
</svg>`)
          .then(config => {
            expect(config, 'to be defined');
          });
      });

      it('should store parameters', function() {
        return expectConfig(`<svg>
  <metadata>
    <atv:parameter behavior="mandatory" desc="base" valuetype="address" name="base"/>
    <atv:parameter behavior="optional" desc="state label" substitute="$LABEL$" valuetype="trstring"
      defaultvalue="T{Switched on}" name="labelOn"/>
  </metadata>
</svg>`)
          .then(config => {
            expect(config.parameters, 'to be defined');
            expect(config.parameters, 'to be a', Array);
            expect(config.parameters, 'to have length', 2);
            expect(config.parameters[0], 'to equal', {
              name: 'base',
              behavior: 'mandatory',
              desc: 'base',
              valuetype: 'address',
            });
            expect(config.parameters[1], 'to equal', {
              name: 'labelOn',
              behavior: 'optional',
              desc: 'state label',
              valuetype: 'trstring',
              defaultvalue: 'T{Switched on}',
              substitute: '$LABEL$',
            });
          });
      });
    });

    context('when encoding fails', function() {
      beforeEach(() => stub(DisplayTransformer.prototype, 'encodeContents')
        .callsFake((obj, cb) => cb(new Error('Encode error'))));
      afterEach(() => DisplayTransformer.prototype.encodeContents.restore());

      it('should forward encode error', function() {
        return expect(writeXMLToDisplayTransformer('<svg></svg>'),
          'to be rejected with', /Encode error/);
      });
    });
  });

  /** @test {DisplayTransformer#createCombinedFile} */
  describe('#createCombinedFile', function() {
    function createDisplayWithFileContents(contents) {
      let lastKey;
      const files = Object.keys(contents).reduce((prev, ext) => {
        const result = prev;

        result[ext] = new File({
          path: `path/to/test.display/test${ext}`,
          contents: Buffer.from(contents[ext]),
        });

        lastKey = ext;
        return result;
      }, {});

      const transformer = new DisplayTransformer({ direction: TransformDirection.FromFilesystem });

      return cb => transformer.createCombinedFile(files, files[lastKey], cb);
    }

    it('should fail with invalid config file', function() {
      return expect(createDisplayWithFileContents({
        '.json': '',
      }), 'to call the callback with error', /Error parsing JSON/);
    });

    it('should fail without SVG file', function() {
      return expect(createDisplayWithFileContents({
        '.json': '{}',
      }), 'to call the callback with error', /No display SVG in/);
    });

    it('should fail with invalid SVG', function() {
      return expect(createDisplayWithFileContents({
        '.svg': 'invalid XML',
      }), 'to call the callback with error', /Text data outside of root node/);
    });

    it('should fail without `svg` tag', function() {
      return expect(createDisplayWithFileContents({
        '.svg': '<root></root>',
      }), 'to call the callback with error', /Error parsing display SVG: No `svg` tag/);
    });

    function expectDisplayWithFileContentToHaveXML(contents, xmlString) {
      return expect(createDisplayWithFileContents(contents), 'to call the callback')
        .then(args => {
          expect(args[0], 'to be falsy');
          expect(args[1], 'to be a', File);
          expect(args[1].contents, 'to be a', Buffer);

          return expect(args[1].contents.toString(),
            'to equal', `<?xml version="1.0" encoding="UTF-8" standalone="no"?>
${xmlString}`.replace(/\r?\n|\r/g, '\r\n'));
        });
    }

    it('should work with empty svg tag', function() {
      return expectDisplayWithFileContentToHaveXML({
        '.svg': '<svg></svg>',
      }, '<svg/>');
    });

    it('should inline script', function() {
      return expectDisplayWithFileContentToHaveXML({
        '.svg': '<svg><rect></rect></svg>',
        '.js': 'code()',
      }, `<svg>
 <rect/>
 <script type="text/ecmascript"><![CDATA[code()]]></script>
</svg>`);
    });

    it('should link dependencies', function() {
      return expectDisplayWithFileContentToHaveXML({
        '.svg': '<svg><rect></rect></svg>',
        '.json': '{ "dependencies": ["path/to/dep.js"] }',
      }, `<svg>
 <rect/>
 <script xlink:href="path/to/dep.js"/>
</svg>`);
    });

    it('should work without empty parameters config', function() {
      return expectDisplayWithFileContentToHaveXML({
        '.svg': '<svg><rect></rect></svg>',
        '.json': '{ "parameters": [] }',
      }, `<svg>
 <rect/>
</svg>`);
    });

    it('should reuse existant metadata section', function() {
      return expectDisplayWithFileContentToHaveXML({
        '.svg': `<svg>
  <metadata>
    <atv:gridconfig height="20" width="20" enabled="false" gridstyle="lines"/>
  </metadata>
</svg>`,
        '.json': '{ "parameters": [{ "name": "test" }] }',
      }, `<svg>
 <metadata>
  <atv:parameter name="test"/>
  <atv:gridconfig height="20" width="20" enabled="false" gridstyle="lines"/>
 </metadata>
</svg>`);
    });

    it('should create metadata section if omitted', function() {
      return expectDisplayWithFileContentToHaveXML({
        '.svg': '<svg></svg>',
        '.json': '{ "parameters": [{ "name": "test" }] }',
      }, `<svg>
 <metadata>
  <atv:parameter name="test"/>
 </metadata>
</svg>`);
    });

    it('should keep parameters specified in SVG', function() {
      return expectDisplayWithFileContentToHaveXML({
        '.svg': `<svg>
  <metadata>
    <atv:parameter name="existant"/>
  </metadata>
</svg>`,
        '.json': '{ "parameters": [{ "name": "test" }] }',
      }, `<svg>
 <metadata>
  <atv:parameter name="test"/>
  <atv:parameter name="existant"/>
 </metadata>
</svg>`);
    });

    it('should insert parameters before other atv tags', function() {
      return expectDisplayWithFileContentToHaveXML({
        '.svg': `<svg>
  <metadata>
    <atv:parameter name="existant"/>
    <atv:gridconfig height="20" width="20" enabled="false" gridstyle="lines"/>
  </metadata>
</svg>`,
        '.json': '{ "parameters": [{ "name": "test" }] }',
      }, `<svg>
 <metadata>
  <atv:parameter name="test"/>
  <atv:parameter name="existant"/>
  <atv:gridconfig height="20" width="20" enabled="false" gridstyle="lines"/>
 </metadata>
</svg>`);
    });

    it('should insert parameters in the correct order', function() {
      return expectDisplayWithFileContentToHaveXML({
        '.svg': '<svg></svg>',
        '.json': '{ "parameters": [{ "name": "test" }, { "name": "another" }] }',
      }, `<svg>
 <metadata>
  <atv:parameter name="test"/>
  <atv:parameter name="another"/>
 </metadata>
</svg>`);
    });

    context('when encoding fails', function() {
      beforeEach(() => stub(DisplayTransformer.prototype, 'encodeContents')
        .callsFake((obj, cb) => cb(new Error('Encode error'))));
      afterEach(() => DisplayTransformer.prototype.encodeContents.restore());

      it('should forward encode error', function() {
        return expect(createDisplayWithFileContents({
          '.svg': '<svg><rect></rect></svg>',
        }), 'to call the callback with error', 'Encode error');
      });
    });
  });
});

import { Buffer } from 'buffer';
import { stub, spy } from 'sinon';
import proxyquire from 'proxyquire';
import { DataType, VariantArrayType, NodeClass } from 'node-opcua';
import File from 'vinyl';
import Logger from 'gulplog';
import expect from '../../expect';
import { TransformDirection } from '../../../src/lib/transform/Transformer';
import NodeId from '../../../src/lib/model/opcua/NodeId';
import AtviseFile from '../../../src/lib/server/AtviseFile';

const readFile = (path, enc, cb) => cb(null, JSON.stringify({
  typeDefinition: 'ns=1;s=VariableTypes.PROJECT.Custom',
}));
const fs = { readFile };

const MappingTransformer = proxyquire('../../../src/transform/Mapping', { fs }).default;

/** @test {MappingTransformer} */
describe('MappingTransformer', function() {
  before(() => Logger.on('error', () => true));

  /** @test {MappingTransformer#transformFromDB} */
  describe('#transformFromDB', function() {
    context('when AtviseFile.fromReadResult returns error', function() {
      let warnListener;
      let debugListener;

      beforeEach(() => {
        stub(AtviseFile, 'fromReadResult').callsFake(() => {
          throw new Error('Test');
        });
        Logger.on('warn', (warnListener = spy()));
        Logger.on('debug', (debugListener = spy()));
      });

      afterEach(() => {
        AtviseFile.fromReadResult.restore();
        Logger.removeListener('warn', warnListener);
        Logger.removeListener('debug', debugListener);
      });

      it('should not forward errors', function() {
        const stream = new MappingTransformer({ direction: TransformDirection.FromDB });

        return expect(cb => stream.transformFromDB({
          nodeId: new NodeId('AGENT.DISPLAYS.Main'),
        }, 'utf8', cb), 'to call the callback')
          .then(args => expect(args, 'to have length', 1));
      });

      it('should log warning', function() {
        const stream = new MappingTransformer({ direction: TransformDirection.FromDB });

        return expect(cb => stream.transformFromDB({
          nodeId: new NodeId('AGENT.DISPLAYS.Main'),
        }, 'utf8', cb), 'to call the callback')
          .then(args => expect(args, 'to have length', 1))
          .then(() => expect(warnListener, 'was called once'))
          .then(() => expect(debugListener, 'was called once'));
      });
    });

    context('when AtviseFile.fromReadResult returns "no value" error', function() {
      let warnListener;
      let debugListener;

      beforeEach(() => {
        stub(AtviseFile, 'fromReadResult').callsFake(() => {
          throw new Error('no value');
        });
        Logger.on('warn', (warnListener = spy()));
        Logger.on('debug', (debugListener = spy()));
      });

      afterEach(() => {
        AtviseFile.fromReadResult.restore();
        Logger.removeListener('warn', warnListener);
        Logger.removeListener('debug', debugListener);
      });

      it('should only debug log', function() {
        const stream = new MappingTransformer({ direction: TransformDirection.FromDB });

        return expect(cb => stream.transformFromDB({
          nodeId: new NodeId('AGENT.DISPLAYS.Main'),
        }, 'utf8', cb), 'to call the callback')
          .then(args => expect(args, 'to have length', 1))
          .then(() => expect(debugListener, 'was called twice'))
          .then(() => expect(warnListener, 'was not called'));
      });
    });

    it('should return an AtviseFile for the given ReadResult', function() {
      const stream = new MappingTransformer({ direction: TransformDirection.FromDB });

      return expect([{
        nodeId: new NodeId('AGENT.DISPLAYS.Main'),
        nodeClass: NodeClass.Variable,
        value: {
          value: '<xml></xml>',
          $dataType: DataType.XmlElement,
          $arrayType: VariantArrayType.Scalar,
        },
        references: {
          HasTypeDefinition: [
            new NodeId('VariableTypes.ATVISE.Display'),
          ],
        },
      }], 'when piped through', stream, 'to yield chunks satisfying', [
        expect.it('to be an', AtviseFile),
      ]);
    });

    context('when file has non-standard type-definition', function() {
      it('should push .rc file', function() {
        const stream = new MappingTransformer({ direction: TransformDirection.FromDB });

        return expect([{
          nodeId: new NodeId('AGENT.OBJECTS.CustomVar'),
          nodeClass: NodeClass.Variable,
          value: {
            value: '<xml></xml>',
            $dataType: DataType.XmlElement,
            $arrayType: VariantArrayType.Scalar,
          },
          references: {
            HasTypeDefinition: [
              new NodeId('VariableTypes.PROJECT.CustomType'),
            ],
          },
        }], 'when piped through', stream, 'to yield chunks satisfying', [
          {
            contents: new Buffer(JSON.stringify({
              typeDefinition: 'ns=1;s=VariableTypes.PROJECT.CustomType',
            }, null, '  ')),
          },
          {
            typeDefinition: new NodeId('VariableTypes.PROJECT.CustomType'),
          },
        ]);
      });
    });
  });

  /** @test {MappingTransformer#transformFromFilesystem} */
  describe('#transformFromFilesystem', function() {
    it('should write AtviseFiles for read Files', function() {
      const stream = new MappingTransformer({ direction: TransformDirection.FromFilesystem });

      return expect([
        new File({ path: 'Test.ext' }),
      ], 'when piped through', stream,
      'to yield chunks satisfying', [
        expect.it('to be an', AtviseFile),
      ]);
    });

    it('should keep base', function() {
      const stream = new MappingTransformer({ direction: TransformDirection.FromFilesystem });

      return expect(cb => stream.transformFromFilesystem(
        new File({ path: 'folder/Test.ext', base: 'folder' }), 'utf8', cb
      ), 'to call the callback')
        .then(args => {
          expect(args[0], 'to be falsy');

          const result = args[1];
          expect(result.base, 'to equal', 'folder');
          expect(result.relative, 'to equal', 'Test.ext');
        });
    });

    it('should skip directories', function() {
      const stream = new MappingTransformer({ direction: TransformDirection.FromFilesystem });

      return expect(cb => stream.transformFromFilesystem(
        { isDirectory: () => true }, 'utf8', cb
      ), 'to call the callback')
        .then(args => {
          expect(args, 'to have length', 1);
          expect(args[0], 'to be falsy');
        });
    });

    it('should skip non-atscm dot files', function() {
      const stream = new MappingTransformer({ direction: TransformDirection.FromFilesystem });

      return expect(cb => stream.transformFromFilesystem(
        { isDirectory: () => false, stem: '.eslintrc' }, 'utf8', cb
      ), 'to call the callback')
        .then(args => {
          expect(args, 'to have length', 1);
          expect(args[0], 'to be falsy');
        });
    });

    context('when file has non-standard type-definition', function() {
      context('with .rc file', function() {
        before(() => spy(fs, 'readFile'));
        after(() => fs.readFile.restore());

        it('should read .rc file', function() {
          const stream = new MappingTransformer({ direction: TransformDirection.FromFilesystem });

          return expect([
            new AtviseFile({ path: 'AGENT/OBJECTS/CustomVar.var.ext' }),
          ], 'when piped through', stream)
            .then(() => {
              expect(fs.readFile, 'was called once');
            });
        });
      });

      context('when .rc file cannot be read', function() {
        beforeEach(() => stub(fs, 'readFile').callsFake((path, enc, cb) => cb(new Error('Test'))));
        afterEach(() => fs.readFile.restore());

        it('should forward error', function() {
          const stream = new MappingTransformer({ direction: TransformDirection.FromFilesystem });

          const promise = expect(stream, 'to error with', /Test/);

          stream.write(new AtviseFile({ path: 'AGENT/OBJECTS/CustomVar.var.ext' }));
          stream.end();

          return promise;
        });
      });

      context('when .rc file cannot be parsed', function() {
        beforeEach(() => stub(fs, 'readFile').callsFake((path, enc, cb) => cb(null, 'invalid')));
        afterEach(() => fs.readFile.restore());

        it('should forward error', function() {
          const stream = new MappingTransformer({ direction: TransformDirection.FromFilesystem });

          const promise = expect(stream, 'to error with', /Unexpected token i in JSON/);

          stream.write(new AtviseFile({ path: 'AGENT/OBJECTS/CustomVar.var.ext' }));
          stream.end();

          return promise;
        });
      });
    });
  });
});

import Logger from 'gulplog';
import { spy } from 'sinon';
import expect from '../../expect';
import TransformerHelper from '../../helpers/Transformer';
import ScriptTransformer from '../../../src/transform/ScriptTransformer';

const transformerHelper = new TransformerHelper(ScriptTransformer);

/** @test {ScriptTransformer} */
describe('ScriptTransformer', function() {
  const ScriptPath = 'SYSTEM/LIBRARY/PROJECT/SERVERSCRIPTS/Test.script';
  const QDPath = 'SYSTEM/LIBRARY/PROJECT/SERVERSCRIPTS/Test.qd';

  /** @test {ScriptTransformer#shouldBeTransformed} */
  describe('#shouldBeTransformed', function() {
    it('should return true for ScriptCode type nodes', function() {
      expect(ScriptTransformer.prototype.shouldBeTransformed(
        { isScript: true }
      ), 'to be true');
    });

    it('should return true for QuickDynamic type nodes', function() {
      expect(ScriptTransformer.prototype.shouldBeTransformed(
        { isQuickDynamic: true }
      ), 'to be true');
    });
  });

  /** @test {ScriptTransformer#transformFromDB} */
  describe('#transformFromDB', function() {
    it('should forward parse errors', function() {
      return expect(transformerHelper.writeXMLToTransformer(ScriptPath, 'invalid xml'),
        'to be rejected with', /Text data outside of root node/);
    });

    it('should warn with invalid xml', function() {
      const onWarn = spy();
      Logger.on('warn', onWarn);

      return expect(transformerHelper.writeXMLToTransformer(ScriptPath, '<root></root>'),
        'to be fulfilled')
        .then(() => {
          expect(onWarn, 'was called once');
          expect(onWarn, 'to have a call satisfying', { args: [/Empty document/] });
        });
    });

    it('should write empty config file for empty Script', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, '<script></script>')
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => {
          expect(contents[0], 'to equal', '{}');
        });
    });

    it('should write empty .js file for empty Script', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, '<script></script>')
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => {
          expect(contents[1], 'to equal', '');
        });
    });

    it('should only store metadata elements', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <metadata>shouldn't be handled</metadata>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(JSON.parse(contents[0]), 'to equal', {}));
    });

    it('should warn on unknown metadata elements', function() {
      const onWarn = spy();
      Logger.on('warn', onWarn);

      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <metadata>
    <custom>test</custom>
  </metadata>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(JSON.parse(contents[0]), 'to equal', {}))
        .then(() => {
          expect(onWarn, 'was called once');
          expect(onWarn, 'to have a call satisfying', { args: [/unknown metadata/i] });
        });
    });

    it('should store icon metadata', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <metadata>
    <icon type="image/png">asdf</icon>
  </metadata>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(JSON.parse(contents[0]), 'to equal', {
          icon: {
            type: 'image/png',
            content: 'asdf',
          },
        }));
    });

    it('should store empty icon metadata', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <metadata>
    <icon></icon>
  </metadata>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(JSON.parse(contents[0]), 'to equal', {
          icon: {
            content: '',
          },
        }));
    });

    it('should store visible metadata', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <metadata>
    <visible>1</visible>
  </metadata>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(JSON.parse(contents[0]), 'to equal', {
          visible: true,
        }));
    });

    it('should properly interpret visible metadata', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <metadata>
    <visible>0</visible>
  </metadata>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(JSON.parse(contents[0]), 'to equal', {
          visible: false,
        }));
    });

    it('should store title metadata', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <metadata>
    <title>script title</title>
  </metadata>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(JSON.parse(contents[0]), 'to equal', {
          title: 'script title',
        }));
    });

    it('should store description metadata', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <metadata>
    <description>script description</description>
  </metadata>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(JSON.parse(contents[0]), 'to equal', {
          description: 'script description',
        }));
    });

    it('should store parameters', function() {
      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <parameter name="paramname"/>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(JSON.parse(contents[0]), 'to equal', {
          parameters: [
            { name: 'paramname' },
          ],
        }));
    });

    it('should store code', function() {
      const code = 'console.log("called");';
      return transformerHelper.writeXMLToTransformer(ScriptPath, `<script>
  <code>${code}</code>
</script>`)
        .then(files => transformerHelper.expectFileContents(files))
        .then(contents => expect(contents[1], 'to equal', code));
    });
  });

  /** @test {ScriptTransformer#createCombinedFile} */
  describe('#createCombinedFile', function() {
    it('should forward config parse errors', function() {
      return expect(transformerHelper.createCombinedFileWithContents(`${ScriptPath}/Test`, {
        '.json': '"description": "script description" }',
      }), 'to call the callback with error', /Error parsing JSON in /);
    });

    context('when called on a quick dynamic', function() {
      it('should store empty metadata', function() {
        return expect(transformerHelper.createCombinedFileWithContents(`${QDPath}/Test`, {
          '.json': '{ }',
        }), 'to call the callback')
          .then(args => transformerHelper.expectFileContents([args[1]]))
          .then(contents => expect(contents[0], 'to contain', '<metadata/>'));
      });

      it('should insert icon metadata', function() {
        return expect(transformerHelper.createCombinedFileWithContents(`${QDPath}/Test`, {
          '.json': '{ "icon": { "type": "image/png", "content": "asdf" } }',
        }), 'to call the callback')
          .then(args => transformerHelper.expectFileContents([args[1]]))
          .then(contents => expect(contents[0],
            'to contain', '<icon type="image/png">asdf</icon>'));
      });

      it('should insert visible metadata', function() {
        return Promise.all([
          expect(transformerHelper.createCombinedFileWithContents(`${QDPath}/Test`, {
            '.json': '{ "visible": false }',
          }), 'to call the callback')
            .then(args => transformerHelper.expectFileContents([args[1]]))
            .then(contents => expect(contents[0], 'to contain', '<visible>0</visible>')),
          expect(transformerHelper.createCombinedFileWithContents(`${QDPath}/Test`, {
            '.json': '{ "visible": true }',
          }), 'to call the callback')
            .then(args => transformerHelper.expectFileContents([args[1]]))
            .then(contents => expect(contents[0], 'to contain', '<visible>1</visible>')),
        ]);
      });

      it('should insert title metadata', function() {
        return expect(transformerHelper.createCombinedFileWithContents(`${QDPath}/Test`, {
          '.json': '{ "title": "qd title" }',
        }), 'to call the callback')
          .then(args => transformerHelper.expectFileContents([args[1]]))
          .then(contents => expect(contents[0], 'to contain', '<title>qd title</title>'));
      });

      it('should insert description metadata', function() {
        return expect(transformerHelper.createCombinedFileWithContents(`${QDPath}/Test`, {
          '.json': '{ "description": "qd desc" }',
        }), 'to call the callback')
          .then(args => transformerHelper.expectFileContents([args[1]]))
          .then(contents => expect(contents[0],
            'to contain', '<description>qd desc</description>'));
      });
    });

    context('when called on a script', function() {
      it('should ignore metadata', function() {
        return expect(transformerHelper.createCombinedFileWithContents(`${ScriptPath}/Test`, {
          '.json': '{ "icon": { "type": "image/png", "content": "asdf" } }',
        }), 'to call the callback')
          .then(args => transformerHelper.expectFileContents([args[1]]))
          .then(contents => expect(contents[0],
            'not to contain', '<icon', '<metadata'));
      });
    });

    it('should insert parameters', function() {
      return expect(transformerHelper.createCombinedFileWithContents(`${QDPath}/Test`, {
        '.json': '{ "parameters": [{ "name": "paramname" }] }',
      }), 'to call the callback')
        .then(args => transformerHelper.expectFileContents([args[1]]))
        .then(contents => expect(contents[0],
          'to contain', '<parameter name="paramname"/>'));
    });

    it('should insert script code', function() {
      const code = 'console.log("called");';
      return expect(transformerHelper.createCombinedFileWithContents(`${QDPath}/Test`, {
        '.js': code,
      }), 'to call the callback')
        .then(args => transformerHelper.expectFileContents([args[1]]))
        .then(contents => expect(contents[0],
          'to contain', `<code><![CDATA[${code}]]></code>`));
    });

    context('when encoding fails', function() {
      it('should forward encode errors', function() {
        class FailingScriptTransformer extends ScriptTransformer {

          encodeContents(object, callback) {
            callback(new Error('Encode error'));
          }

        }

        const helper = new TransformerHelper(FailingScriptTransformer);

        return expect(helper.createCombinedFileWithContents(`${QDPath}/Test`, {
          '.json': '{ "parameters": [{ "name": "paramname" }] }',
        }), 'to call the callback with error', 'Encode error');
      });
    });
  });
});

import expect from 'unexpected';
import { stub, spy } from 'sinon';
import proxyquire from 'proxyquire';
import Logger from 'gulplog';

function createCleanup(numberOfOpenSessions, closeOpenShouldFail) {
  return proxyquire('../../../src/util/cleanup', {
    '../lib/server/Session': {
      __esModule: true,
      default: class Session {

        static get open() {
          return new Array(numberOfOpenSessions);
        }

        static closeOpen() {
          return closeOpenShouldFail ?
            Promise.reject('Session.closeOpen error') :
            Promise.resolve();
        }

      },
    },
  }).default;
}

/** @test {cleanup} */
describe('cleanup', function() {
  before(() => stub(process, 'kill'));
  after(() => process.kill.restore());
  afterEach(() => process.kill.resetHistory());

  it('should call uninstall', function() {
    const uninstall = spy();
    createCleanup(0, false)(null, null, uninstall);

    expect(uninstall.calledOnce, 'to be', true);
  });

  context('when receiving SIGINT', function() {
    it('should log "Ctrl-C"', function() {
      const onWarn = spy();
      Logger.on('warn', onWarn);

      createCleanup(0, false)(null, 'SIGINT', () => {});
      expect(onWarn.calledOnce, 'to be', true);
      expect(onWarn.lastCall.args[0], 'to match', /Ctrl-C/);
    });
  });

  context('with open sessions', function() {
    it('should return false', function() {
      expect(createCleanup(3, false)(null, null, () => {}), 'to be', false);
    });

    it('should call process.kill', function(done) {
      createCleanup(3, false)(null, null, () => {});
      setTimeout(() => {
        expect(process.kill.calledOnce, 'to be', true);
        done();
      }, 10);
    });

    it('should forward Session.closeOpen errors', function(done) {
      createCleanup(3, true)(null, null, () => {});
      setTimeout(() => {
        expect(process.kill.calledOnce, 'to be', true);
        done();
      }, 10);
    });
  });

  context('without open sessions', function() {
    it('should return true', function() {
      expect(createCleanup(0, false)(null, null, () => {}), 'to be', true);
    });
  });
});

