import cleanup from 'node-cleanup';
import cleanupHandler from './util/cleanup';

// Register tasks
export { default as pull } from './tasks/pull';
export { default as push } from './tasks/push';
export { default as watch } from './tasks/watch';
export { default as importXml } from './tasks/importXml';
export { default as diff } from './tasks/diff';
export { default as deleteFs } from './tasks/deleteFs';
export { default as deleteServer } from './tasks/deleteServer';

// Register cleanup
/* istanbul ignore if */
if (process.env.NODE_ENV !== 'test') {
  // Prevent node-opcua logging
  console.log = () => {}; // eslint-disable-line no-console

  cleanup((code, signal) => cleanupHandler(code, signal, cleanup.uninstall), {
    ctrl_C: '',
    unhandledRejection: '',
  });
}

/**
 * The path to the project's configuration file.
 * @type {string}
 */
export const path = process.env.ATSCM_CONFIG_PATH;

/**
 * The current project's configuration.
 * @type {Atviseproject}
 */
const Config = require(path).default;

/**
 * The current project's configuration, with overrides (through `ATSCM_PROJECT__` env vars) already
 * handled.
 */
export default class ProjectConfig extends Config {

  /**
   * Return the project configuration override for the given name if available.
   * @param {string} name The variable to return
   * @type {string|undefined} The variables's value or `undefined`.
   */
  static _env(name) {
    return process.env[`ATSCM_PROJECT__${name}`];
  }

  /**
   * The atvise server's host. Can be overridden with the `ATSCM_PROJECT__HOST` env variable.
   * @type {string}
   */
  static get host() {
    return this._env('HOST') || super.host;
  }

  /**
   * The atvise server ports to use.
   * @type {Object}
   * @property {number} opc The OPC-UA port the atvise server runs on. Can be overridden with the
   * `ATSCM_PROJECT__PORT__OPC` env variable.
   * @property {number} http The HTTP port the atvise server can be reached at. Can be overridden
   * with the `ATSCM_PROJECT__PORT__HTTP` env variable.
   */
  static get port() {
    return {
      opc: parseInt(this._env('PORT__OPC'), 10) || super.port.opc,
      http: parseInt(this._env('PORT__HTTP'), 10) || super.port.http,
    };
  }

  /**
   * The login to use. Return false if no login is required (default).
   * @type {Object}
   * @property {string} username The username to log in with. Can be overridden with the
   * `ATSCM_PROJECT__LOGIN__USERNAME` env variable.
   * @property {string} password The password to log in with. Can be overridden with the
   * `ATSCM_PROJECT__LOGIN__PASSWORD` env variable.
   */
  static get login() {
    if (this._env('LOGIN__USERNAME') || this._env('LOGIN__PASSWORD')) {
      return {
        username: this._env('LOGIN__USERNAME') || super.login.username,
        password: this._env('LOGIN__PASSWORD') || super.login.password,
      };
    }

    return super.login;
  }

}

export { default as Atviseproject } from './lib/config/Atviseproject';
export { default as ProjectConfig } from './config/ProjectConfig';
export { default as NodeId } from './lib/ua/NodeId';
export { default as Transformer, TransformDirection } from './lib/transform/Transformer';
export { default as PartialTransformer } from './lib/transform/PartialTransformer';
export { default as SplittingTransformer } from './lib/transform/SplittingTransformer';
export { default as DisplayTransformer } from './transform/DisplayTransformer';
export { default as ScriptTransformer } from './transform/ScriptTransformer';

import { join } from 'path';
import { src, dest } from 'gulp';
import handlebars from 'gulp-compile-handlebars';
import helpers from 'handlebars-helpers';
import streamToPromise from 'stream-to-promise';
import deps from '../../res/init/templates/dependencies.json';

/**
 * The action run when running 'atscm init'.
 */
export default class InitTask {

  /**
   * Returns the globs of the processed files for the given config lanugage.
   * @param {String} langId The configuration language used.
   * @return {String[]} Globs of the files to handle.
   */
  static filesToHandle(langId) {
    return [
      './general/**/*',
      './general/**/.*',
      `./lang/${langId}/**/*.*`,
      `./lang/${langId}/**/.*`,
    ].map(p => join(__dirname, '../../res/init/templates', p));
  }

  /**
   * Runs the task with the given options.
   * @param {Object} options The options to use.
   * @return {Promise<{ install: String[] }, Error>} Resolved with information on further actions
   * to run or rejected if the task failed.
   */
  static run(options) {
    const langId = options.configLang;

    const install = deps.lang[langId];

    const stream = src(this.filesToHandle(langId))
      .pipe(handlebars(options, {
        helpers: helpers(),
      }))
      .pipe(dest('./'));

    return streamToPromise(stream)
      .then(() => ({ install }));
  }

}

import { basename } from 'path';
import InitOption from '../lib/init/Option';
import Atviseproject from '../lib/config/Atviseproject';
import Validator from './OptionsValidator';

/**
 * A map providing dirnames for config langs
 * @type {Map<String, String>}
 */
export const ConfigLangs = {
  'ES2015 JavaScript': 'es2015',
  'Regular JavaScript': 'es5',
  TypeScript: 'ts',
  'CoffeeScript (alpha)': 'coffee',
};

/**
 * Options available for the 'atscm init' command.
 * @type {Object}
 * @property {InitOption<input>} name The new project's name.
 * @property {InitOption<input>} description The new project's description.
 * @property {InitOption<input>} author The new project's author.
 * @property {InitOption<input>} atviseHost The new project's atvise server host.
 * @property {InitOption<input>} atvisePortOpc The new project's atvise server OPC port.
 * @property {InitOption<input>} atvisePortHttp The new project's atvise server HTTP port.
 * @property {InitOption<confirm>} useLogin If the new porject's atvise server requires login.
 * @property {InitOption<input>} atviseUsername The new project's atvise server login name.
 * @property {InitOption<input>} atvisePassword The new project's atvise server login password.
 * @property {InitOption<list>} configLang The language to use for the new project's configuration.
 */
export const InitOptions = {
  name: new InitOption({
    message: 'Project name',
    default: basename(process.cwd()),
    validate: Validator.name,
  }),
  description: new InitOption('Project description'),
  author: new InitOption('Project author'),
  atviseHost: new InitOption('Atvise server host', Atviseproject.host),
  atvisePortOpc: new InitOption('Atvise OPC port', Atviseproject.port.opc),
  atvisePortHttp: new InitOption('Atvise HTTP port', Atviseproject.port.http),
  useLogin: new InitOption({
    message: 'Does your atvise server require login',
    type: 'confirm',
    default: false,
  }),
  atviseUser: new InitOption({
    message: ' - Username',
    when: answers => answers.useLogin,
  }),
  atvisePassword: new InitOption({
    message: ' - Password',
    when: answers => answers.useLogin,
  }),
  configLang: new InitOption({
    type: 'list',
    message: 'Configuration language to use',
    choices: Object.keys(ConfigLangs).map(name => ({ name, value: ConfigLangs[name] })),
  }),
};

/**
 * {@link InitOptions}, exported as an array. Required to run {@link Inquirer}.
 * @type {InitOption[]}
 */
const InitOptionsAsArray = Object.keys(InitOptions)
  .map(name => Object.assign({ name }, InitOptions[name]));

export default InitOptionsAsArray;

import validatePackageName from 'validate-npm-package-name';

/**
 * A static class containing validators for the options used when running 'atscm init'.
 */
export default class InitOptionsValidator {

  /**
   * Validates a project name to be a valid npm package name.
   * @param {String} value The name to validate.
   * @return {Boolean|String} Returns true if `value` is a valid npm package name, or an error
   * message otherwise.
   */
  static name(value) {
    const result = validatePackageName(value);

    if (result.errors) { return result.errors[0]; }

    // First letter must be a letter
    if (value.match(/^[a-z]+/i) === null) {
      return 'name must start with a letter';
    }

    return result.validForNewPackages ? true : result.warnings[0];
  }

}

/* eslint-disable no-useless-escape */

import NodeId from '../ua/NodeId';
import DisplayTransformer from '../../transform/DisplayTransformer';
import ScriptTransformer from '../../transform/ScriptTransformer';

/**
 * An *atvise-scm* project's configuration.
 * @abstract
 */
export default class Atviseproject {

  /**
   * The atvise-server's host. Defaults to *localhost*.
   * @type {String}
   */
  static get host() {
    return 'localhost';
  }

  /**
   * The atvise-server ports to use.
   * @type {Object}
   * @property {Number} [opc=4840] The OPC-UA port the atvise-server runs on.
   * @property {Number} [http=80] The HTTP port the atvise-server can be reached at.
   */
  static get port() {
    return {
      opc: 4840,
      http: 80,
    };
  }

  /**
   * The login to use. Return `false` if no login is required (default).
   * @type {Boolean|Object}
   * @property {String} username The username to log in with.
   * @property {String} password The password to log in with.
   */
  static get login() {
    return false;
  }

  /**
   * The source directory path.
   * @type {String}
   */
  static get RelativeSourceDirectoryPath() {
    return './src';
  }

  /**
   * The diff file path.
   * @type {String}
   */
  static get DiffFileName() {
    return 'diff.log';
  }

  /**
   * The delete file paths.
   * @type {Object}
   * @property {String} server The server delete file name.
   * @property {String} fs The file system delete file name.
   */
  static get DeleteFileNames() {
    return {
      server: 'deleteServer.txt',
      fs: 'deleteFs.txt',
    };
  }

  /**
   * The xml resource directory path.
   * @type {String}
   */
  static get RelativeXmlResourcesPath() {
    return './node_modules/atscm/xml_resources/**/*.xml';
  }

  /**
   * The transformers to use in this project. Defaults to a single {@link DisplayTransformer}
   * @type {Transformer[]}
   */
  static get useTransformers() {
    return [
      new DisplayTransformer(),
      new ScriptTransformer(),
    ];
  }

  /**
   * The atvise-server nodes that atvise-scm should sync. Defaults to
   * `['ns=1;s=AGENT', 'ns=1;s=SYSTEM', 'ns=1;s=ObjectTypes.PROJECT']`
   * @type {String[]|NodeId[]}
   */
  static get nodes() {
    return [
      new NodeId('AGENT'),
      new NodeId('SYSTEM'),
      new NodeId('ObjectTypes.PROJECT'),
      new NodeId('VariableTypes.PROJECT'),
    ];
  }

  /**
   * An array of editor related node ids. They should be ignored in a atvise-scm project.
   * @type {NodeId[]}
   */
  static get EditorRelatedNodes() {
    return [
      new NodeId('SYSTEM\.JOURNALS\.ProjectHistory'),
    ];
  }

  /**
   * An array of server related node ids. They should be ignored in a atvise-scm project
   * as they are read-only.
   * @type {NodeId[]}
   */
  static get ServerRelatedNodes() {
    return [
      // eslint-disable-next-line max-len
      new NodeId('AGENT\.OPCUA\.server_url'),
      new NodeId('AGENT\.WEBACCESS\.https?[0-9]+\.(state|port)'),
      new NodeId('SYSTEM\.INFORMATION\.LOGS\.'),
      new NodeId('SYSTEM\.LIBRARY\.ATVISE\.SERVERSCRIPTS\.atscm'),
    ];
  }

  /**
   * These nodes (and their subnodes, if any) will be ignored by atvise-scm. Defaults to
   * {@link Atviseproject.EditorRelatedNodes} combined with
   * {@link Atviseproject.ServerRelatedNodes}.
   * @type {NodeId[]}
   */
  static get ignoreNodes() {
    return this.EditorRelatedNodes.concat(this.ServerRelatedNodes);
  }

  /**
   * Returns an object containing the properties to inspect.
   * @see https://nodejs.org/api/util.html#util_util_inspect_object_options
   * @return {Object} The object to inspect.
   */
  static inspect() {
    return {
      host: this.host,
      port: this.port,
      login: this.login,
      useTransformers: this.useTransformers,
      nodes: this.nodes,
      nodesToWatch: this.nodesToWatch,
      ignoreNodes: this.ignoreNodes,
    };
  }

}

import Logger from 'gulplog';
import { StatusCodes, DataType, Variant } from 'node-opcua';
import CallScriptStream from '../script/CallScriptStream';
import NodeId from '../ua/NodeId';


/**
 * Definition for the parameter name of the Delete node script
 * @type {String}
 */
const DeleteNodeScriptParameterName = 'nodeId';


/**
 * A stream that deletes atvise server nodes for the given node ids.
 */
export default class DeleteNodeStream extends CallScriptStream {

  /**
   * Creates a new CreateNodeStream
   */
  constructor() {
    super(new NodeId('ns=1;s=SYSTEM.LIBRARY.ATVISE.SERVERSCRIPTS.atscm.DeleteNode'));
  }


  /**
   * Returns an error message specifically for the given combined file.
   * @param {NodeId} nodeId The nodeId to create the error message for
   * @return {String} The specific error message.
   */
  processErrorMessage(nodeId) {
    return `Error deleting node:  ${nodeId.toString()}`;
  }

  /**
   * Creates the parameter object for creating nodes
   * @param {NodeId} nodeId The nodeId to use as parameter
   * parameter object for.
   * @return {Object} The resulting parameter object.
   */
  createParameters(nodeId) {
    const paramValue = new Variant({
      dataType: DataType.String,
      value: nodeId.toString(),
    });

    return { paramNames: [DeleteNodeScriptParameterName], paramValues: [paramValue] };
  }

  /**
   * Handles the call script methods callback
   * @param {Array} results The result of the call
   * @param {node} nodeId The nodeId to process
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  handleCallback(results, nodeId, handleErrors) {
    const outputArguments = results[0].outputArguments;

    if (outputArguments[0].value.value !== StatusCodes.Good.value) {
      handleErrors(new Error(outputArguments[1].value));
    } else {
      const deleteSuccessful = outputArguments[3].value[0].value === StatusCodes.Good.value;

      if (deleteSuccessful) {
        Logger.debug(`Successfully deleted node ${nodeId.toString()}`);
      } else {
        Logger.error(`Error deleting node ${nodeId.toString()}.`,
          'Node does not exist in atvise server address space');
      }
    }
    handleErrors(null, StatusCodes.Good, done => done());
  }
}

import checkType from '../../util/validation';
import AtviseFile from '../mapping/AtviseFile';
/**
 * An Object that creates diff items for the given {AtviseFiles}
 */
export default class DiffFile {

  /**
   * Creates a new DiffFile based on given {AtviseFile} and file type.
   * @param {AtviseFile} file The file to add in first place
   */
  constructor(file, fileType) {
    let fileValue = file.value;

    if (!checkType(file, AtviseFile) || !checkType(fileType, String)) {
      throw new Error('DiffFile#constructor: Can not parse given argument!');
    } else if (!DiffFile.isValidFileType(fileType)) {
      throw new Error('DiffFile#constructor: File has wrong item type!');
    }


    // handle empty an undefined file values
    if (fileValue === null) {
      fileValue = 'null';
    } else if (fileValue === undefined) {
      fileValue = 'undefined';
    } else {
      fileValue = fileValue.toString();
    }


    /**
     * Defines wether the given file is a fs resource or a atvise server resource.
     * @type {DiffFile.ItemType}
     */
    this.fileType = fileType;

    /**
     * The file's value
     * @type {AtviseFile}
     */
    this.value = fileValue;

    /**
     * The files's nodeId
     * @type {NodeId}
     */
    this.nodeId = file.nodeId;

    /**
     * The files relative path
     * @type {vinyl~path}
     */
    this.path = file.relativeFilePath;
  }

  /**
   * The directions a transformer can be run in.
   * @type {{DBToServer: String, ServerToDB: String}}
   */
  static get FileType() {
    return {
      ServerFile: 'ServerFile',
      FsFile: 'FsFile',
    };
  }


  /**
   * 'true' for {AtviseFiles}'s created from atvise server resources
   * @type {Bool}
   */
  get isServerFile() {
    return this.fileType === DiffFile.FileType.ServerFile;
  }

  /**
   * 'true' for {AtviseFiles}'s created from file system resources
   * @type {Bool}
   */
  get isFsFile() {
    return this.fileType === DiffFile.FileType.FsFile;
  }

  /**
   * Returns an error message specifically for the given mapping item.
   * @param {DiffFile.ItemType} fileType The fileType to check
   * @return {Bool} 'true' for valid item types.
   */
  static isValidFileType(fileType) {
    return [
      DiffFile.FileType.ServerFile,
      DiffFile.FileType.FsFile,
    ].includes(fileType);
  }
}

import { StatusCodes } from 'node-opcua';
import QueueStream from '../stream/QueueStream';
import DiffFile from './DiffFile';

/**
 * A stream checks if the given {@link NodeId}s exist on the atvise server or
 * on the filesystem, depending on the stream direction
 */
export default class DiffFileStream extends QueueStream {

  /**
   * Creates a new DiffStream based on the given stream direction an some options.
   * @param {Object} The options to use.
   * @param {DiffFile.fileType} [options.fileType] The diff file type to create.
   */
  constructor(options = {}) {
    super(options);

    /**
     * The diff file type
     * @type {Map<vinyl~path, DiffItem>}
     */
    const fileType = options.fileType;

    if (!DiffFile.isValidFileType(fileType)) {
      throw new Error('DiffFileStream#constructor: Invalid file type');
    } else {
      this.itemType = fileType;
    }
  }

  /**
   * Returns an error message specifically for the given mapping item.
   * @param {AtviseFile} file The file to create the error message for
   * @return {String} The specific error message.
   */
  processErrorMessage(file) {
    return `DiffFileStream#processErrorMessage: Error processing item ${file.nodeId.toString()}`;
  }

  /**
   * Creates {DiffFiles}'s for the given {AtviseFile}'s
   * @param {DiffFile} file The file to process
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */

  processChunk(file, handleErrors) {
    handleErrors(null, StatusCodes.Good, done => {
      this.push(new DiffFile(file, this.itemType));
      done();
    });
  }
}

import checkType from '../../util/validation';
import DiffFile from '../diff/DiffFile';

export default class DiffItem {

  /**
   * Creates a new CombinedNodeFile based on given atvise File.
   * @param {DiffFile} file The file to add in first place
   */
  constructor(file) {
    if (!checkType(file, DiffFile)) {
      throw new Error('DiffItem#constructor: Can not parse given argument!');
    }

    /**
     * The server resource file
     * @type {DiffFile}
     */
    this.serverFile = {};

    /**
     * The file system resource file
     * @type {DiffFile}
     */
    this.fsFile = {};

    /**
     * The items's nodeId
     * @type {NodeId}
     */
    this.nodeId = file.nodeId;

    /**
     * The file's path
     * @type {vinyl~path}
     */
    this.path = file.path;

    if (file.isServerFile) {
      this.serverFile = file;
    } else {
      this.fsFile = file;
    }
  }

  /**
   * Checks if the given file has a valid type for combined files
   * @param {AtviseFile} file The file to check
   * @return {Boolean} file has valid type(=true) or not(=false)
   */
  static hasValidType(file) {
    return !file.isAtviseReferenceConfig && !file.isDirectory();
  }

  /**
   * The possible diff states
   * @type {{Equal:Object, Added: Object, Modified: Object, Deleted: Object}}
   */
  static get DiffStates() {
    return {
      Equal: { text: 'Equ', value: 0 },
      Added: { text: 'Add', value: 1 },
      Modified: { text: 'Mod', value: 2 },
      Deleted: { text: 'Del', value: 3 },
    };
  }

  /**
   * `true` for diff items that already contain an file system and an server resource.
   * @type {Boolean}
   */
  get isComplete() {
    return checkType(this.fsFile, DiffFile) && checkType(this.serverFile, DiffFile);
  }

  /**
   * `true` if the server resource file and the mapped fs file are equal.
   * @type {Boolean}
   */
  get filesAreEqual() {
    if (!this.isComplete) {
      return false;
    }

    return this.fsFile.value === this.serverFile.value;
  }


  /**
   * The diff items state
   * @type {DiffItem.DiffStates}
   */
  get state() {
    const states = DiffItem.DiffStates;
    let state;

    if (this.filesAreEqual) {
      state = states.Equal;
    } else if (this.isComplete) {
      state = states.Modified;
    } else if (!checkType(this.serverFile, DiffFile)) {
      state = states.Deleted;
    } else if (!checkType(this.fsFile, DiffFile)) {
      state = states.Added;
    }

    return state;
  }

  /**
   * Adds the given file
   * @param{DiffFile} file The file to add
   * @return {Bool} file type was already added(=true) or not(=false)
   */
  addFile(file) {
    if (this.fileTypeWasAlreadyAdded(file)) {
      throw new Error(`DiffItem#addFile: File ${file.path} was already added`);
    }

    if (file.isServerFile) {
      this.serverFile = file;
    } else {
      this.fsFile = file;
    }
  }

  /**
   * checks if the given file type was already added
   * @param{DiffFile} file The file to add
   * @return {Bool} file type was already added(=true) or not(=false)
   */
  fileTypeWasAlreadyAdded(file) {
    if (file.isServerFile) {
      return checkType(this.serverFile, DiffFile);
    }

    return checkType(this.fsFile, DiffFile);
  }
}

import { StatusCodes } from 'node-opcua';
import QueueStream from '../stream/QueueStream';
import DiffItem from './DiffItem';

/**
 * A stream checks if the given {@link NodeId}s exist on the atvise server
 * or on the filesystem, depending on the stream direction
 */
export default class DiffItemStream extends QueueStream {

  /**
   * Creates a new DiffItemStream.
   * @param {Object} The options to use.
   */
  constructor() {
    super();

    /**
     * The diff item cache
     * @type {Map<vinyl~path, DiffItem>}
     */
    this.itemsCache = {};
  }

  /**
   * Returns an error message specifically for the given mapping item.
   * @param {DiffFile} file The diff file to create the error message for.
   * @return {String} The specific error message.
   */
  processErrorMessage(file) {
    return `DiffStream#processErrorMessage: Error processing item ${file.toString()}`;
  }

  /**
   * Combines the given DiffFiles to DiffItems.
   * @param {DiffFile} file The file to process
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */

  processChunk(file, handleErrors) {
    handleErrors(null, StatusCodes.Good, done => {
      const path = file.path;
      const itemsCache = this.itemsCache;

      if (typeof itemsCache[path] !== 'undefined') {
        const diffItem = itemsCache[path];

        diffItem.addFile(file);

        if (diffItem.isComplete) {
          this.push(diffItem);
          delete itemsCache[path];
        }
      } else {
        itemsCache[path] = new DiffItem(file);
      }

      done();
    });
  }
}

import { StatusCodes } from 'node-opcua';
import { createWriteStream } from 'fs';
import { dirname } from 'path';
import QueueStream from '../stream/QueueStream';

/**
 * A stream checks if the given {@link NodeId}s exist on the atvise server or on
 * the filesystem, depending on the stream direction
 */
export default class DiffResultStream extends QueueStream {


  /**
   * Creates a new DiffItemStream.
   * @param {Object} The options to use.
   * @param {String|Path|Buffer} [options.filePath] The diff files path.
   */
  constructor(options = {}) {
    super();

    const filePath = options.filePath || 'diff.log';

    /**
     * The output write stream to create the logfile
     * @type {Stream}
     */
    this.logger = createWriteStream(filePath);
  }

  /**
   * Returns an error message specifically for the given mapping item.
   * @param {DiffFile} file The diff file to create the error message for.
   * @return {String} The specific error message.
   */
  processErrorMessage(file) {
    return `DiffStream#processErrorMessage: Error processing item ${file.toString()}`;
  }


  /**
   * Diffs the given {AtviseFile} with the corresponding atvise server resource or
   * file systsem resource.
   * @param {DiffItem} diffItem The diffItem to process
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */

  processChunk(diffItem, handleErrors) {
    handleErrors(null, StatusCodes.Good, done => {
      this.logger.write(`${diffItem.state.text} filePath=${diffItem.path}` +
        `, nodeFilePath=${dirname(diffItem.path)}` +
        `, nodeId=${diffItem.nodeId.value}\n`
      );

      done();
    });
  }
}

import readline from 'readline';
import Logger from 'gulplog';
import { join } from 'path';
import { remove, existsSync, createReadStream } from 'fs-extra';
import ProjectConfig from '../../config/ProjectConfig';

/**
 * A stream that deletes listed nodes on the filesystem
 */
export default class DeleteFsStream {

  /**
   * Creates a new DeleteFsStream based on the given options.
   * @param {Object} options The stream configuration options.
   * @param {String} [options.deleteFileName] The delete file name.
   */
  constructor(options = {}) {
    /**
     * The delete file name
     * @type {String}
     */
    const deleteFileName = options.deleteFileName || 'deleteFs.txt';

    let processed = 0;
    const base = join(process.cwd(), ProjectConfig.RelativeSourceDirectoryPath);

    const lineReader = readline.createInterface({
      input: createReadStream(deleteFileName),
    });

    const printProgress = setInterval(() => {
      Logger.info(`Deleted: ${processed}`);

      if (Logger.listenerCount('info') > 0) {
        readline.cursorTo(process.stdout, 0);
        readline.moveCursor(process.stdout, 0, -1);
      }
    }, 1000);

    lineReader.on('line', line => {
      const filePath = line.indexOf('nodeFilePath=') > -1 ?
        line.split('nodeFilePath=')[1].split(', nodeId=')[0].trim() :
        line.trim();

      const path = join(base, filePath);

      processed++;

      if (existsSync(path)) {
        remove(path)
          .catch(err => Logger.error(`Error removing file: '${path}', message: ${err.message}`));
      } else {
        Logger.error(`File '${path}' does not exist`);
      }
    });

    lineReader.on('close', () => {
      clearInterval(printProgress);
    });

    return lineReader;
  }
}

import readline from 'readline';
import Logger from 'gulplog';
import { createReadStream } from 'fs';
import DeleteNodeStream from '../delete/DeleteNodeStream';
import NodeId from '../ua/NodeId';

/**
 * A stream that deletes listed nodes from atvise server
 */
export default class DeleteFsStream {

  /**
   * Creates a new DeleteServerStream based on the given options.
   * @param {Object} options The stream configuration options.
   * @param {String} [options.deleteFileName] The delete file name.
   */
  constructor(options = {}) {
    /**
     * The delete file name
     * @type {String}
     */
    const deleteFileName = options.deleteFileName || 'deleteServer.txt';

    const lineReader = readline.createInterface({
      input: createReadStream(deleteFileName),
    });

    const deleteNodeStream = new DeleteNodeStream();

    const printProgress = setInterval(() => {
      Logger.info(
        `Deleted: ${deleteNodeStream.processed}`,
        `(${deleteNodeStream.opsPerSecond.toFixed(1)} ops/s)`
      );

      if (Logger.listenerCount('info') > 0) {
        readline.cursorTo(process.stdout, 0);
        readline.moveCursor(process.stdout, 0, -1);
      }
    }, 1000);

    lineReader.on('line', line => {
      const nodeId = line.indexOf('nodeId=') > -1 ? new NodeId(line.split('nodeId=')[1].trim()) :
        new NodeId(line.trim());

      deleteNodeStream.write(nodeId);
    });


    deleteNodeStream.on('drained', () => {
      deleteNodeStream._flush(() => {
        clearInterval(printProgress);
        deleteNodeStream.emit('finish');
      });
    });

    return deleteNodeStream;
  }
}

import readline from 'readline';
import Logger from 'gulplog';
import filter from 'gulp-filter';
import CombinedStream from 'combined-stream';
import DiffResultStream from '../diff/DiffResultStream';
import DiffItemStream from '../diff/DiffItemStream';
import DiffFileStream from '../diff/DiffFileStream';
import DiffFile from '../diff/DiffFile';
import DiffItem from '../diff/DiffItem';
import UaNodeToAtviseFileTransformer from '../../transform/UaNodeToAtviseFileTransformer';
import FileToAtviseFileTransformer from '../../transform/FileToAtviseFileTransformer';

/**
 * A stream that diff atvise server resources with file system resources.
 */
export default class DiffStream {

  /**
   * Creates a new DiffStream based on the given options.
   * @param {Object} options The stream configuration options.
   * @param {NodeId[]} [options.nodesToDiff] The nodes to diff.
   * @param {String|Path|Buffer} [options.filePath] The diff files path.
   */
  constructor(options = {}) {
    /**
     * The nodes to diff
     * @type {NodeId[]}
     */
    const nodesToDiff = options.nodesToDiff || [];

    /**
     * The diff file path
     * @type {String|Path|Buffer}
     */
    const diffFilePath = options.filePath || 'diff.log';

    // diff file streams
    const fsFileStream = new FileToAtviseFileTransformer(
      {
        nodesToTransform: nodesToDiff,
        applyTransformers: false,
      })
      .pipe(new DiffFileStream({ fileType: DiffFile.FileType.FsFile }));

    const serverFileTransformer = new UaNodeToAtviseFileTransformer(
        { nodesToTransform: nodesToDiff }
    );

    const serverFileStream = serverFileTransformer.stream
      .pipe(new DiffFileStream({ fileType: DiffFile.FileType.ServerFile }));

    // diff file processors
    const diffItemStream = new DiffItemStream();

    const diffResultStream = new DiffResultStream({ filePath: diffFilePath });
    const equalFilesFilter = filter(
      diffItem => diffItem.state.value !== DiffItem.DiffStates.Equal.value
    );
    const logger = diffResultStream.logger;

    const combinedStream = new CombinedStream({ pauseStreams: false });

    logger.write('Modified:\n');

    combinedStream.append(fsFileStream);
    combinedStream.append(serverFileStream);

    const printProgress = setInterval(() => {
      Logger.info(
        `Diffed: ${diffResultStream._processed} (${diffResultStream.opsPerSecond.toFixed(1)} ops/s)`
      );

      if (Logger.listenerCount('info') > 0) {
        readline.cursorTo(process.stdout, 0);
        readline.moveCursor(process.stdout, 0, -1);
      }
    }, 1000);


    return combinedStream
      .pipe(diffItemStream)
      .pipe(equalFilesFilter)
      .pipe(diffResultStream)
      .once('finish', () => {
        const itemsCache = Object.values(diffItemStream.itemsCache);
        const states = DiffItem.DiffStates;
        const addedItems = [];
        const deletedItems = [];

        if (itemsCache.length > 0) {
          diffResultStream.once('drained', () => {
            clearInterval(printProgress);
          });

          itemsCache.forEach(diffItem => {
            if (diffItem.state.value === states.Added.value) {
              addedItems.push(diffItem);
            } else if (diffItem.state.value === states.Deleted.value) {
              deletedItems.push(diffItem);
            }
          });

          logger.write('\nAdded:\n');
          addedItems.forEach(file => diffResultStream.write(file));
          logger.write('\nDeleted:\n');
          deletedItems.forEach(file => diffResultStream.write(file));
        } else {
          clearInterval(printProgress);
        }
      });
  }
}

import { DataType, StatusCodes } from 'node-opcua';
import Logger from 'gulplog';
import QueueStream from '../stream/QueueStream';
import NodeId from '../ua/NodeId';

/**
 * Call script node id
 * @type {node-opcua~NodeId}
 */
const ImportNodesMethodId = new NodeId('ns=1;s=AGENT.OPCUA.METHODS.importNodes');

/**
 * Base node id for callscript node
 * @type {node-opcua~NodeId}
 */
const ImportNodesMethodBaseNodeId = ImportNodesMethodId.parentNodeId;


/**
 * A stream that imports xml files in parallel.
 */
export default class ImportXmlStream extends QueueStream {

  /**
   * @param {vinyl~file} file The file to create the call object for.
   * Creates the call object for the given file
   * @return {Object} The resulting call script object.
   */
  createCallObject(file) {
    return {
      objectId: ImportNodesMethodBaseNodeId.toString(),
      methodId: ImportNodesMethodId.toString(),
      inputArguments: [
        {
          dataType: DataType.NodeId,
          value: new NodeId(NodeId.NodeIdType.NUMERIC, 0, 0), // absolute import
        },
        {
          dataType: DataType.XmlElement,
          value: file.contents,
        },
      ],
    };
  }

  /**
   * Returns an error message specifically for the given file.
   * @param {vinyl~file} file The file to generate the error message for
   * @return {String} The specific error message.
   */
  processErrorMessage(file) {
    return `Error importing file: ${file.path}`;
  }


  /**
   * Performs opcua method calls for the given call object configuration
   * @param {vinyl~file} file The file being processed.
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  processChunk(file, handleErrors) {
    const callObj = this.createCallObject(file);

    try {
      this.session.call([callObj], (err, results) => {
        if (err) {
          handleErrors(err);
        } else if (results[0].statusCode.value !== StatusCodes.Good.value) {
          handleErrors(err, results[0].statusCode, done => done());
        } else {
          const importSuccessFull = results[0].outputArguments[0].value;

          if (importSuccessFull) {
            Logger.info(`Successfully imported file: ${file.path}`);
          } else {
            Logger.error(this.processErrorMessage(file));
          }

          handleErrors(err, StatusCodes.Good, done => done());
        }
      });
    } catch (e) {
      handleErrors(e);
    }
  }
}

import readline from 'readline';
import { dest } from 'gulp';
import Logger from 'gulplog';
import UaNodeToAtviseFileTransformer from '../../transform/UaNodeToAtviseFileTransformer';
import ProjectConfig from '../../config/ProjectConfig';

/**
 * A stream that transforms read {@link ReadStream.ReadResult}s and stores the on the filesystem.
 */
export default class PullStream {

  /**
   * Creates a new PullStream based on the given options.
   * @param {Object} options The stream configuration options.
   * @param {NodeId[]} [options.nodesToPull] The nodes to push.
   * @param {Boolean} [options.useInputStream] Defines if the given input
   * stream should be used for mapping.
   * @param {Stream} [options.inputStream] The input stream to use.
   */
  constructor(options = {}) {
    /**
     * The nodes to pull
     * @type {NodeId[]}
     */
    const nodesToPull = options.nodesToPull || [];

    const fileTransformer = new UaNodeToAtviseFileTransformer({
      nodesToTransform: nodesToPull,
      useInputStream: options.useInputStream,
      inputStream: options.inputStream,
    });

    const readStream = fileTransformer.readStream;

    const printProgress = setInterval(() => {
      Logger.info(`Pulled: ${readStream.processed} (${readStream.opsPerSecond.toFixed(1)} ops/s)`);

      if (Logger.listenerCount('info') > 0) {
        readline.cursorTo(process.stdout, 0);
        readline.moveCursor(process.stdout, 0, -1);
      }
    }, 1000);

    return fileTransformer.stream
      .pipe(dest(ProjectConfig.RelativeSourceDirectoryPath))
      .on('finish', () => {
        if (Logger.listenerCount('info') > 0) {
          readline.clearLine(process.stdout, 0);
          readline.cursorTo(process.stdout, 0);
        }

        clearInterval(printProgress);
      });
  }
}

import readline from 'readline';
import Logger from 'gulplog';
import filter from 'gulp-filter';
import FileToAtviseFileTransformer from '../../transform/FileToAtviseFileTransformer';
import NodeFileStream from '../push/NodeFileStream';
import WriteStream from '../push/WriteStream';
import CreateNodeStream from '../push/CreateNodeStream';
import AddReferenceStream from '../push/AddReferenceStream';

/**
 * A stream that transforms read {@link vinyl~File}s and pushes them to atvise server.
 */
export default class PushStream {

  /**
   * Creates a new PushSteam based on the given options.
   * @param {Object} options The stream configuration options.
   * @param {NodeId[]} [options.nodesToPush] The nodes to push.
   * @param {Boolean} [options.createNodes] Defines if nodes shall be created or not.
   */
  constructor(options = {}) {
    /**
     * Defines shall be created or not.
     * @type {Boolean}
     */
    const createNodesOnPush = options.createNodes || false;

    /**
     * The nodes to push
     * @type {NodeId[]}
     */
    const nodesToPush = options.nodesToPush || [];

    const fileTransformer = new FileToAtviseFileTransformer({ nodesToTransform: nodesToPush });
    const atvReferenceFilter = filter(file => !file.isAtviseReferenceConfig, { restore: true });
    const nodeFileStream = new NodeFileStream({ createNodes: createNodesOnPush });
    const createNodeStream = new CreateNodeStream();
    const writeStream = new WriteStream({ createNodes: createNodesOnPush });

    this.printProgress = setInterval(() => {
      Logger.info(
      `Pushed: ${writeStream._processed} (${writeStream.opsPerSecond.toFixed(1)} ops/s)`
      );

      if (Logger.listenerCount('info') > 0) {
        readline.cursorTo(process.stdout, 0);
        readline.moveCursor(process.stdout, 0, -1);
      }
    }, 1000);

    this.pushStream = fileTransformer
      .pipe(atvReferenceFilter)
      .pipe(nodeFileStream)
      .pipe(writeStream);

    if (createNodesOnPush) {
      this.pushStream.pipe(createNodeStream);
    }

    this.pushStream.once('finish', () => {
      Logger.debug('Writing and creating nodes finished. Adding references...');

      if (createNodesOnPush && atvReferenceFilter.restore._readableState.buffer.length > 0) {
        const addReferenceStream = new AddReferenceStream();

        this.pushStream.pipe(atvReferenceFilter.restore)
          .pipe(addReferenceStream)
          .on('finish', () => this.endStream());
      } else {
        this.endStream();
      }
    });

    return this.pushStream;
  }

  /**
   * Stops the print progress when push stream has finished and stops the push task process
   */
  endStream() {
    if (Logger.listenerCount('info') > 0) {
      readline.cursorTo(process.stdout, 0);
      readline.clearLine(process.stdout);
    }

    clearInterval(this.printProgress);
  }
}

/**
 * An option the 'atscm init' command handles.
 * TODO: Support function values for name, message, default, choices...
 */
export default class InitOption {

  /**
   * Creates a new option based either on a message and (optionally) a default value or some
   * options.
   * @param {String|Object} messageOrOptions The message or options to use.
   * @param {inquirer~PromptType} [messageOrOptions.type] The option's type.
   * @param {String} messageOrOptions.message The option's message. Required if `messageOrOptions`
   * is an object.
   * @param {String|Number} [messageOrOptions.default] The options's default value.
   * @param {inquirer~Validator} [messageOrOptions.validate] A function that validates user input.
   * @param {String[]|Number[]} [messageOrOptions.choices] The options's choices. Applies to to list
   * types only.
   * @param {Boolean|function(answers: Object): Boolean} [messageOrOptions.when] Weather or not to
   * prompt this option.
   * @param {String|Number} [defaultOrUndefined] The default value to use.
   */
  constructor(messageOrOptions, defaultOrUndefined) {
    if (messageOrOptions === undefined) {
      throw new Error('message or options required');
    }

    if (typeof messageOrOptions === 'string') {
      if (messageOrOptions.length === 0) {
        throw new Error('message is required');
      }

      /**
       * The option's {@link inquirer~PromptType}. Defaults to 'input'.
       * @type {inquirer~PromptType}
       */
      this.type = InitOption.DefaultType;

      /**
       * The option's message. A '?' sign is added automatically.
       * @type {String}
       */
      this.message = `${messageOrOptions}?`;

      /**
       * The default value to use.
       * @type {String|Number|undefined}
       */
      this.default = defaultOrUndefined;
    } else {
      if (!messageOrOptions.message || messageOrOptions.length === 0) {
        throw new Error('message is required');
      }

      this.type = messageOrOptions.type || InitOption.DefaultType;
      this.message = `${messageOrOptions.message}?`;
      this.default = messageOrOptions.default;

      /**
       * The choices available. Applies to list types only.
       * @type {String[]|Number[]|undefined}
       */
      this.choices = messageOrOptions.choices;

      /**
       * Validates the user input for this option.
       * @type {inquirer~Validator}
       */
      this.validate = messageOrOptions.validate;

      /**
       * A function or boolean that indicates weather or not to prompt this option.
       * @type {Boolean|function(answers: Object): Boolean|undefined}
       */
      this.when = messageOrOptions.when;
    }
  }

  /**
   * The default {@link inquirer~PromptType} to use. Equals 'input'.
   * @return {inquirer~PromptType}
   */
  static get DefaultType() {
    return 'input';
  }

}

import Int64 from 'node-int64';
import { readFile } from 'fs';
import { dirname, join, relative } from 'path';
import { DataType, VariantArrayType, resolveNodeId, coerceLocalizedText } from 'node-opcua';
import File from 'vinyl';
import ProjectConfig from '../../config/ProjectConfig';
import AtviseTypes from './Types';
import NodeId from '../ua/NodeId';

/**
 * A map of AtviseTypes against their definition id's value.
 * @type {Map<String, AtivseType>}
 */
const AtviseTypesByValue = AtviseTypes
    .reduce((result, type) => Object.assign(result, {
      [type.typeDefinition.value]: type,
    }), {});

/**
 * A map of AtviseTypes against their identifiers.
 * @type {Map<String, AtivseType>}
 */
const AtviseTypesByIdentifier = AtviseTypes
    .reduce((result, type) => Object.assign(result, {
      [type.identifier]: type,
    }), {});


/**
 * Seperator for array values
 * @type {String}
 */
const ArrayValueSeperator = '@atscmUaNodeArraySeperator@';

/**
 * A map providing shorter extensions for data types
 * @type {Map<node-opcua~DataType, String>}
 */
export const ExtensionForDataType = {
  [DataType.Boolean]: 'bool',
  [DataType.XmlElement]: 'xml',
};

/**
 * Switches keys and values in an object. E.g.: { 'a': 1 } becomes { 1: 'a' }
 * @param {Object} obj The object to reverse.
 * @return {Object} The reversed object.
 */
function reverseObject(obj) {
  return Object.keys(obj)
      .reduce((result, key) => Object.assign(result, {
        [obj[key]]: key,
      }), {});
}

/**
 * A map providing data types for shorter extensions (Reverse of {@link DataTypeForExtension}).
 * * @type {Map<String, node-opcua~DataType>}
 */
export const DataTypeForExtension = reverseObject(ExtensionForDataType);

// Cache DataType
/**
 * OPC-UA data type names.
 * @type {String[]}
 */
const types = Object.keys(DataType);

/**
 * OPC-UA data type extensions.
 * @type {String[]}
 */
const typeExtensions = types.map(t => t.toLowerCase());

// Cache TypeDefinitions
/**
 * Variable data type definition node id.
 * @type {NodeId}
 */
const VariableTypeDefinition = new NodeId(NodeId.NodeIdType.NUMERIC, 62, 0);
/**
 * Property data type definition node id.
 * @type {NodeId}
 */
const PropertyTypeDefinition = new NodeId(NodeId.NodeIdType.NUMERIC, 68, 0);

// Cache Regular expressions
/**
 * A regular expression picking file extensions from file names.
 * @type {RegExp}
 */
const ExtensionRegExp = /\.([^/\\]*)$/;

// Value encoding related cache
/**
 * A set of functions that decode raw stored node values to their original value.
 * @type {Map<node-opcua~DataType, function(rawValue: String): *>}
 */
const Decoder = {
  [DataType.Boolean]: stringValue => stringValue === 'true',
  [DataType.String]: stringValue => stringValue,
  [DataType.NodeId]: stringValue => resolveNodeId(stringValue),
  [DataType.DateTime]: stringValue => new Date(stringValue),
  [DataType.UInt64]: stringValue => parseInt(stringValue, 10),
  [DataType.Int64]: stringValue => parseInt(stringValue, 10),
  [DataType.Int32]: stringValue => parseInt(stringValue, 10),
  [DataType.SByte]: stringValue => parseInt(stringValue, 10),
  [DataType.Byte]: stringValue => parseInt(stringValue, 10),
  [DataType.UInt32]: stringValue => parseInt(stringValue, 10),
  [DataType.Double]: stringValue => parseFloat(stringValue, 10),
  [DataType.Float]: stringValue => parseFloat(stringValue, 10),
  [DataType.XmlElement]: stringValue => stringValue,
  [DataType.LocalizedText]: stringValue => coerceLocalizedText(stringValue.split('text=')[1]),
};


/**
 * A set of functions that decode node values create node script.
 * @type {Map<node-opcua~DataType, function(value: *): String>}
 */
const CreateNodeDecoder = {
  [DataType.ByteString]: buffer => buffer.toString('binary'),
  [DataType.DateTime]: buffer => new Date(buffer.toString()).getTime(),
};


/**
 * Converts safely two uint32 array to an int64 number type.
 * @param {Number} lowerRangeValue The value for the lower 32 bits of the int 64 value
 * @param {Number} higherRangeValue The value for the higher 32 bits of the int 64 value
 * @returns {Number} The resulting int64 value
 */
function uint32ArraysToInt64(lowerRangeValue, higherRangeValue) {
  const int64 = new Int64(lowerRangeValue, higherRangeValue);

  if (!isFinite(int64)) {
    throw new Error('Value is too big for Javascript Number type');
  }

  return int64.toString();
}


/**
 * A set of functions that encode node values before storing them.
 * @type {Map<node-opcua~DataType, function(value: *): String>}
 */
const Encoder = {
  [DataType.UInt64]: uInt32Array => uint32ArraysToInt64(uInt32Array[0], uInt32Array[1]),
  [DataType.Int64]: int32Array => uint32ArraysToInt64(int32Array[0], int32Array[1]),
  [DataType.ByteString]: byteString => new Buffer(byteString),
};


/**
 * Returns the extension for a specific {@link node-opcua~DataType}.
 * Algorithm:
 *   - if the type has a shortened extension defined in {@link ExtensionForDataType}, return it.
 *   - else return the DataType's name, in lowercase letters.
 * @param {node-opcua~DataType} dataType The datatype to get the extension for.
 * @return {String} The resulting extension.
 */
function extensionForDataType(dataType) {
  return ExtensionForDataType[dataType] || dataType.toString().toLowerCase();
}

/**
 * An extension to {@link vinyl~File} providing some additional, atvise-related properties.
 * @property {node-opcua~DataType} AtviseFile#dataType The {@link node-opcua~DataType} the node is
 * stored against on atvise server.
 * @property {NodeId} typeDefinition The file's type definition on atvise server.
 * FIXME: Additional properties not showing in API docs.
 */
export default class AtviseFile extends File {

  /**
   * Returns a storage path for a {@link MappingItem.configObj}.
   * @param {Object} config The config to create the path for
   */
  static pathForItemConfig(config) {
    let path = `${config.nodeId.filePath}/${config.nodeId.browseName}`;

    const dataType = config.dataType;
    const arrayType = config.arrayType;
    const typeDefinition = config.typeDefinition;

    if (typeDefinition.value === VariableTypeDefinition.value) {
      // Variable nodes are stored with their lowercase datatype as an extension
      path += `.${extensionForDataType(dataType)}`;
    } else if (typeDefinition.value === PropertyTypeDefinition.value) {
      // Property nodes are stored with '.prop' and their lowercase datatype as an extension
      path += `.prop.${extensionForDataType(dataType)}`;
    } else {
      // Handle custom types
      let identifier = 'var';
      let fileExtension = false;
      let keepExtension = false;

      const atType = AtviseTypesByValue[typeDefinition.value];
      if (atType) {
        identifier = atType.identifier;
        fileExtension = atType.fileExtension;
        keepExtension = atType.keepExtension;
      }

      if (!keepExtension) {
        path += `.${identifier}.${fileExtension || extensionForDataType(dataType)}`;
      }
    }

    if (arrayType) {
      // Add 'array' or 'matrix' extensions for corresponding array types
      if (arrayType.value !== VariantArrayType.Scalar.value) {
        path += `.${arrayType.value === VariantArrayType.Array.value ? 'array' : 'matrix'}`;
      }
    }

    return path;
  }

  /**
   * Returns an atvise type with type definition as accessor
   * @return {AtviseTypes{}} Object containing atvise types
   */
  static getAtviseTypesByValue() {
    return AtviseTypesByValue;
  }

  /**
   * Encodes a node's value to file contents.
   * @param {*} value The value to encode.
   * @param {node-opcua~DataType} dataType The {@link node-opcua~DataType} to encode the value for.
   * @param {node-opcua~VariantArrayType} arrayType The files array type
   * @return {?Buffer} The encoded file contents or null.
   */
  static encodeValue(value, dataType, arrayType) {
    if (value === null) {
      return Buffer.from('');
    }

    const encoder = Encoder[dataType];

    if (arrayType === VariantArrayType.Array) {
      const arrayContent = value.map(item => {
        if (encoder) {
          return encoder(item);
        }

        return item;
      })
        .join(ArrayValueSeperator);

      return Buffer.from(arrayContent);
    }

    return Buffer.from(encoder ? encoder(value) : value.toString().trim());
  }

  /**
   * Decodes a file's contents to a node's value
   * @param {Buffer} buffer The file contents to decode.
   * @param {node-opcua~DataType} dataType The {@link node-opcua~DataType} to decode the contents
   * @param {node-opcua~VariantArrayType} arrayType The files array type
   * @param {Boolean} useCreateNodeEncoding If set to `true`, create node decoders will overwrite
   * the existing decoders
   * @return {?*} The decoded node value or null.
   */
  static decodeValue(buffer, dataType, arrayType, useCreateNodeEncoding) {
    let decoder = Decoder[dataType];
    let bufferValue;

    if (buffer === null || buffer.length === 0) {
      return null;
    }

    if (useCreateNodeEncoding && CreateNodeDecoder[dataType]) {
      decoder = CreateNodeDecoder[dataType];
      bufferValue = buffer;
    } else {
      bufferValue = buffer.toString();
    }

    if (arrayType === VariantArrayType.Array) {
      const arrayValue = bufferValue.toString().split(ArrayValueSeperator);

      return arrayValue.map(item => {
        if (decoder) {
          return decoder(item);
        }

        return item;
      });
    } else if (decoder) {
      return decoder(bufferValue);
    }

    return buffer;
  }

  /**
   * As file mtimes do not support millisecond resolution these must be removed before storing
   * files.
   * @param {Date} date The original mtime.
   * @return {Date} The normalized mtime.
   */
  static normalizeMtime(date) {
    date.setMilliseconds(0);

    return date;
  }


  /**
   * Creates a new {@link AtviseFile} for the given {@link MappingItem}.
   * @param {MappingItem} mappingItem The read result to create the file for.
   * @return {AtviseFile} The resulting file.
   */
  static fromMappingItem(mappingItem) {
    let configObj = {};

    if (!mappingItem) {
      throw new Error('Mapping item is undefined');
    }

    configObj = mappingItem.configObj;

    return new AtviseFile({
      path: AtviseFile.pathForItemConfig(configObj),
      contents: AtviseFile.encodeValue(configObj.value, configObj.dataType, configObj.arrayType),
      _dataType: configObj.dataType,
      _arrayType: configObj.arrayType,
      _typeDefinition: configObj.typeDefinition,
      stat: { mtime: configObj.mtime ? this.normalizeMtime(configObj.mtime) : undefined },
    });
  }

  /**
   * Recalculates {@link AtviseFile#dataType}, {@link AtviseFile#arrayType} and
   * {@link AtviseFile#typeDefinition}. **Never call this method directly.**
   */
  _getMetadata() {
    // Set default metadata
    /**
     * The node's stored {@link node-opcua~VariantArrayType}.
     * @type {?node-opcua~VariantArrayType}
     */
    this._arrayType = VariantArrayType.Scalar;

    let extensions = [];
    const relPath = this.relativeFilePath;
    const m = relPath.match(ExtensionRegExp);
    if (m) {
      extensions = m[1].split('.');
    }

    // For split files, add the directory name extension
    const dirnameExts = dirname(relPath).split('.');
    if (dirnameExts.length > 1) {
      extensions.unshift(dirnameExts[dirnameExts.length - 1]);
    }

    function ifLastExtensionMatches(matches, fn) {
      if (matches(extensions[extensions.length - 1])) {
        fn(extensions.pop());
      }
    }

    const complete = () => this._dataType !== undefined && this._typeDefinition !== undefined;

    // Handle array types
    ifLastExtensionMatches(ext => ext === 'array', () => {
      this._arrayType = VariantArrayType.Array;
    });

    ifLastExtensionMatches(ext => ext === 'matrix', () => {
      this._arrayType = VariantArrayType.Matrix;
    });

    ifLastExtensionMatches(ext => typeExtensions.includes(ext), ext => {
      /**
       * The node's stored {@link node-opcua~DataType}.
       * @type {?node-opcua~DataType}
       */
      this._dataType = DataType[types[typeExtensions.indexOf(ext)]];
    });

    // Handle wrapped data types (e.g. 'bool' for DataType.Boolean)
    ifLastExtensionMatches(ext => DataTypeForExtension[ext], ext => {
      this._dataType = DataType[DataTypeForExtension[ext]];
    });

    if (extensions.length === 0) { // Got variable
      /**
       * The node's stored type definition.
       * @type {?node-opcua~NodeId}
       */
      this._typeDefinition = new NodeId(NodeId.NodeIdType.NUMERIC, 62, 0);
    }

    ifLastExtensionMatches(ext => ext === 'prop', () => {
      this._typeDefinition = new NodeId(NodeId.NodeIdType.NUMERIC, 68, 0);
    });

    ifLastExtensionMatches(ext => ext === 'var', () => {
      this._typeDefinition = new NodeId('Custom.VarResourceType');
    });

    if (!complete()) {
      // Handle atvise types
      let foundAtType = false;

      Object.keys(AtviseTypesByIdentifier).forEach(identifier => {
        if (!foundAtType && extensions.includes(identifier)) {
          foundAtType = true;
          const type = AtviseTypesByIdentifier[identifier];

          this._typeDefinition = type.typeDefinition;
          this._dataType = type.dataType;
        }
      });
    }

    if (!complete()) {
      this._typeDefinition = new NodeId('VariableTypes.ATVISE.Resource.OctetStream');
      this._dataType = DataType.ByteString;
    }
  }

  /**
   * The file's relative path (base = 'src' folder)
   * @type {String}
   */
  get relativeFilePath() {
    const path = this.path;
    // build source path here because Project config is undefined when AtviseFile is loaded
    const srcPath = join(process.cwd(), ProjectConfig.RelativeSourceDirectoryPath);

    return path.indexOf(srcPath) > -1 ? relative(srcPath, this.path) : path;
  }

  /**
   * The file's {@link node-opcua~DataType}.
   * @type {node-opcua~DataType}
   */
  get dataType() {
    if (!this._dataType) {
      this._getMetadata();
    }

    return this._dataType;
  }

  /**
   * The file's {@link node-opcua~VariantArrayType}.
   * @type {node-opcua~VariantArrayType}
   */
  get arrayType() {
    if (!this._arrayType) {
      this._getMetadata();
    }

    return this._arrayType;
  }

  /**
   * The file's type definition.
   * @type {node-opcua~NodeId}
   */
  get typeDefinition() {
    if (!this._typeDefinition) {
      this._getMetadata();
    }

    return this._typeDefinition;
  }

  /**
   * `true` for files containing atvise displays.
   * @type {Boolean}
   */
  get isDisplay() {
    return this.typeDefinition.value === 'VariableTypes.ATVISE.Display';
  }

  /**
   * `true` for files containing atvise scripts.
   * @type {Boolean}
   */
  get isScript() {
    return this.typeDefinition.value === 'VariableTypes.ATVISE.ScriptCode';
  }

  /**
   * `true` for files containing atvise quick dynamics.
   * @type {Boolean}
   */
  get isQuickDynamic() {
    return this.typeDefinition.value === 'VariableTypes.ATVISE.QuickDynamic';
  }

  /**
   * `true` for files containing type definitions.
   * @type {Boolean}
   */
  get isTypeDefinition() {
    return this.isBaseTypeDefinition || this.isInstanceTypeDefinition;
  }


  /**
   * `true` for files containing instance type definitions.
   * @type {Boolean}
   */
  get isInstanceTypeDefinition() {
    return this.typeDefinition.value === 'Custom.InstanceTypeDefinition';
  }

  /**
   * `true` for files containing base type definitions.
   * @type {Boolean}
   */
  get isBaseTypeDefinition() {
    return this.typeDefinition.value === 'Custom.BaseTypeDefinition';
  }

  /**
   * `true` for files containing type definitions.
   * @type {Boolean}
   */
  get isAtviseReferenceConfig() {
    return this.typeDefinition.value === 'Custom.AtvReferenceConfig';
  }

  /**
   * Sets the node value for the file.
   * @param {?*} newValue The value to set.
   */
  set value(newValue) {
    /**
     * The file's contents.
     * @type {?Buffer}
     */
    this.contents = AtviseFile.encodeValue(newValue, this.dataType, this.arrayType);
  }

  /**
   * Returns the decoded node value for the file.
   * @type {?*} The file's decoded value.
   */
  get value() {
    return AtviseFile.decodeValue(this.contents, this.dataType, this.arrayType);
  }

  /**
   * Returns the decoded node value for create node serverscript.
   * @type {?*} The file's decoded value.
   */
  get createNodeValue() {
    return AtviseFile.decodeValue(this.contents, this.dataType, this.arrayType, true);
  }

  /**
   * Returns the node id associated with the file.
   * @type {NodeId} The file's node id.
   */
  get nodeId() {
    return NodeId.fromFilePath(dirname(this.relativeFilePath));
  }

  /**
   * Returns a new file with all attributes of the current file.
   * @param {Object} options See https://github.com/gulpjs/vinyl#filecloneoptions for all options
   * available.
   * @return {AtviseFile} The cloned file.
   */
  clone(options) {
    const clonedFile = super.clone(options);

    clonedFile._arrayType = this._arrayType;

    return clonedFile;
  }

  /**
   * Creates a new AtviseFile and reads it's contents.
   * @param {Object} options See {@link vinyl~File} for available options.
   * @return {Promise} Resolved with the new file of rejected with the error that occured while
   * trying to read it's path.
   */
  static read(options = {}) {
    return new Promise((resolve, reject) => {
      if (!options.path) {
        reject(new Error('options.path is required'));
      } else {
        readFile(options.path, (err, contents) => {
          if (err) {
            reject(err);
          } else {
            resolve(new AtviseFile(Object.assign(options, { contents })));
          }
        });
      }
    });
  }
}

