import Logger from 'gulplog';
import { StatusCodes } from 'node-opcua';
import QueueStream from '../stream/QueueStream';

/**
 * A stream that reads atvise server nodes for the {@link node-opcua~ReferenceDescription}s passed.
 */
export default class ReadStream extends QueueStream {

  /**
   * Returns an error message specifically for the given mapping item.
   * @param {MappingItem} mappingItem The reference description to get
   * the error message for.
   * @return {String} The specific error message.
   */
  processErrorMessage(mappingItem) {
    return `ReadStream#processErrorMessage: Error processing item ${mappingItem.nodeId.toString()}`;
  }

  /**
   * Adds the data of read {node-opcua~DataValue}s for given {@link MappingItem}s.
   * @param {MappingItem} mappingItem The mappingItem to process
   * If the proccessed item is not a type definition, the node value will be read from
   * the atvise server
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */

  processChunk(mappingItem, handleErrors) {
    const nodeId = mappingItem.nodeId;

    // skip reference and type definition files and read node files that already contain a config
    if (!mappingItem.shouldBeRead || mappingItem.dataValueAdded) {
      this.push(mappingItem);
      handleErrors(null, StatusCodes.Good, done => done());
    } else {
      this.session.read([{ nodeId }], (err, nodesToRead, results) => {
        if (!err && (!results || results.length === 0)) {
          handleErrors(new Error('No results'));
        } else {
          handleErrors(err, results && results.length > 0 ? results[0].statusCode : null, done => {
            const dataValue = results[0];

            if (dataValue.value == null) {
              Logger.error(`Unable to read value of node:  ${nodeId.toString()}`);
            } else {
              mappingItem.createConfigItemFromDataValue(dataValue);
              this.push(mappingItem);
            }

            done();
          });
        }
      });
    }
  }
}

import Logger from 'gulplog';
import { StatusCodes, DataType, Variant } from 'node-opcua';
import CallScriptStream from '../script/CallScriptStream';
import NodeId from '../ua/NodeId';


/**
 * Definition for the parameter name of the CreateNode script
 * @type {Array}
 */
const AddReferencesScriptParameterName = 'paramObjString';


/**
 * A stream that adds node references for the given reference config {AtviseFile}'s
 * on the atvise server.
 */
export default class AddReferenceStream extends CallScriptStream {
  /**
   * Creates a new CreateNodeStream
   */
  constructor() {
    super(new NodeId('ns=1;s=SYSTEM.LIBRARY.ATVISE.SERVERSCRIPTS.atscm.AddReferences'));
  }


  /**
   * Returns an error message specifically for the given combined file.
   * @param {AtviseFile} referenceConfigFile The combined file to process
   * the error message for.
   * @return {String} The specific error message.
   */
  processErrorMessage(referenceConfigFile) {
    return `Error adding references:  ${referenceConfigFile.nodeId.toString()}`;
  }


  /**
   * Creates the parameter object for creating nodes
   * @param {AtviseFile} referenceConfigFile The referenceConfig file to create the call
   * parameter object for.
   * @return {Object} The resulting parameter object.
   */
  createParameters(referenceConfigFile) {
    const paramObj = {
      nodeId: referenceConfigFile.nodeId,
      references: JSON.parse(referenceConfigFile.value),
    };

    const paramValue = new Variant({
      dataType: DataType.String,
      value: JSON.stringify(paramObj),
    });

    return { paramNames: [AddReferencesScriptParameterName], paramValues: [paramValue] };
  }


  /**
   * Handles the call script methods callback
   * @param {Array} results The result of the call
   * @param {AtviseFile} referenceConfigFile The referenceConfig file to process
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  handleCallback(results, referenceConfigFile, handleErrors) {
    const nodeId = referenceConfigFile.nodeId;
    const outputArguments = results[0].outputArguments;

    if (outputArguments[0].value.value !== StatusCodes.Good.value) {
      handleErrors(new Error(outputArguments[1].value));
    } else {
      const failedAttempts = outputArguments[3].value[0].value;

      if (failedAttempts) {
        if (failedAttempts.length > 0) {
          failedAttempts.map(targetNodeId => {
            Logger.error(`Adding reference from ${nodeId} to ${targetNodeId} failed`);
            return false;
          });
        } else {
          Logger.debug(`Successfully created references for ${nodeId}`);
        }
      }
      handleErrors(null, StatusCodes.Good, done => done());
    }
  }
}

import { ReferenceTypeIds, NodeClass } from 'node-opcua';
import checkType from '../../util/validation';
import AtviseFile from '../mapping/AtviseFile';
import ReverseReferenceTypeIds from '../ua/ReverseReferenceTypeIds';

/**
 * Type definition key for type definition files
 * @type {String}
 */
const TypeDefinitionKey = ReverseReferenceTypeIds[ReferenceTypeIds.HasTypeDefinition];


export default class CombinedNodeFile {

  /**
   * Creates a new CombinedNodeFile based on given atvise File.
   * @param {AtviseFile} file The file to add in first place
   */
  constructor(file, createNodes) {
    if (!checkType(file, AtviseFile) || !checkType(createNodes, Boolean)) {
      throw new Error('Class CombinedNodeFile: Can not parse given argument!');
    } else if (!CombinedNodeFile.hasValidType(file)) {
      throw new Error('Class CombinedNodeFile: File has wrong type!');
    }

    /**
     * The content atvise file
     * @type {AtviseFile}
     */
    this.contentFile = {};


    /**
     * Defines wether the stream works with {CombinedNodeFiles} or {AtviseFile}s.
     * @type {Boolean}
     */
    this.createNodes = createNodes;


    /**
     * The type definition atvise file
     * @type {AtviseFile}
     */
    this.typeDefinitionFile = {};

    if (file.isTypeDefinition) {
      this.typeDefinitionFile = file;
    } else {
      this.contentFile = file;
    }
  }

  /**
   * Checks if the given file has a valid type for combined files
   * @param {AtviseFile} file The file to check
   * @return {Boolean} file has valid type(=true) or not(=false)
   */
  static hasValidType(file) {
    return !file.isAtviseReferenceConfig && !file.isDirectory();
  }

  /**
   * `true` for files that already contain necessary information.
   * @type {Boolean}
   */
  get isComplete() {
    const typeDefFileComplete = checkType(this.typeDefinitionFile, AtviseFile);

    if (this.createNodes) {
      if (!typeDefFileComplete) {
        return false;
      }

      return this.isTypeDefOnlyFile ? typeDefFileComplete :
        checkType(this.contentFile, AtviseFile) && typeDefFileComplete;
    }

    return checkType(this.contentFile, AtviseFile);
  }

  /**
   * `true` for files that contain type definitions.
   * @type {Boolean}
   */
  get isTypeDefOnlyFile() {
    const typeDefinitionContent = JSON.parse(this.typeDefinitionFile.value);
    let typeDefinition = {};

    if (this.typeDefinitionFile.isBaseTypeDefinition) {
      typeDefinition = typeDefinitionContent;
    } else {
      typeDefinition = typeDefinitionContent[TypeDefinitionKey].items[0];
    }

    return NodeClass[typeDefinition.nodeClass].value !== NodeClass.Variable.value;
  }

  /**
   * Adds the given file to the combined file
   * @param{AtviseFile} file The file to add
   * @return {Bool} file type was already added(=true) or not(=false)
   */
  addFile(file) {
    if (this.fileTypeWasAlreadyAdded(file)) {
      return false;
    }

    if (file.isTypeDefinition) {
      this.typeDefinitionFile = file;
    } else {
      this.contentFile = file;
    }

    return true;
  }

  /**
   * checks if the given file type was already added
   * @param{AtviseFile} file The file to add
   * @return {Bool} file type was already added(=true) or not(=false)
   */
  fileTypeWasAlreadyAdded(file) {
    return file.isTypeDefinition ? checkType(this.typeDefinitionFile, AtviseFile) :
      checkType(this.contentFile, AtviseFile);
  }
}

import Logger from 'gulplog';
import {
  ReferenceTypeIds,
  StatusCodes,
  DataType,
  NodeClass,
  Variant,
} from 'node-opcua';
import CallScriptStream from '../script/CallScriptStream';
import NodeId from '../ua/NodeId';
import ReverseReferenceTypeIds from '../ua/ReverseReferenceTypeIds';


/**
 * Type definition key for type definition files
 * @type {String}
 */
const TypeDefinitionKey = ReverseReferenceTypeIds[ReferenceTypeIds.HasTypeDefinition];

/**
 * Modelling rule key for type definition files
 * @type {String}
 */
const ModellingRuleKey = ReverseReferenceTypeIds[ReferenceTypeIds.HasModellingRule];


/**
 * Definition for the parameter name of the CreateNode script
 * @type {Array}
 */
const CreateNodeScriptParameterName = 'paramObjString';


/**
 * A stream that creates atvise server nodes for the given{@link CombinedNodeFiles}s
 */
export default class CreateNodeStream extends CallScriptStream {

  /**
   * Creates a new CreateNodeStream
   */
  constructor() {
    super(new NodeId('ns=1;s=SYSTEM.LIBRARY.ATVISE.SERVERSCRIPTS.atscm.CreateNode'));
  }


  /**
   * Returns an error message specifically for the given combined file.
   * @param {CombinedNodeFile} combinedNodeFile The combined file to process
   * the error message for.
   * @return {String} The specific error message.
   */
  processErrorMessage(combinedNodeFile) {
    return `Error creating node:  ${combinedNodeFile.typeDefinitionFile.nodeId.toString()}`;
  }


  /**
   * Creates the parameter object for creating nodes
   * @param {CombinedNodeFile} combinedNodeFile The combined node file to process
   * the error message for.
   * @return {Object} The resulting parameter object.
   */
  createParameters(combinedNodeFile) {
    const typeDefinitionFile = combinedNodeFile.typeDefinitionFile;
    const nodeId = typeDefinitionFile.nodeId;
    const typeDefinitionConfig = JSON.parse(typeDefinitionFile.value);
    const paramObjString = new Variant({ dataType: DataType.String, value: '' });
    let typeDefinition = {};
    let modellingRuleRefs;

    if (typeDefinitionFile.isBaseTypeDefinition) {
      typeDefinition = typeDefinitionConfig;
    } else {
      typeDefinition = typeDefinitionConfig[TypeDefinitionKey].items[0];
      modellingRuleRefs = typeDefinitionConfig[ModellingRuleKey];
    }

    const configObj = {
      nodeId: nodeId.value,
      parentNodeId: nodeId.parentNodeId.value,
      browseName: nodeId.browseName,
      nodeClass: NodeClass[typeDefinition.nodeClass].value,
      typeDefinition: new NodeId(typeDefinition.refNodeId).value,
      modellingRule: modellingRuleRefs ? new NodeId(modellingRuleRefs.items[0].refNodeId).value :
        null,
    };

    if (!combinedNodeFile.isTypeDefOnlyFile) {
      const contentFile = combinedNodeFile.contentFile;
      const dataType = contentFile.dataType.value;

      configObj.dataType = dataType;
      configObj.valueRank = contentFile.arrayType.value;
      configObj.value = contentFile.createNodeValue;
    }

    paramObjString.value = JSON.stringify(configObj);

    return { paramNames: [CreateNodeScriptParameterName], paramValues: [paramObjString] };
  }

  /**
   * Handles the call script methods callback
   * @param {Array} results The result of the call
   * @param {CombinedNodeFile} combinedNodeFile The combined file to process
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  handleCallback(results, combinedNodeFile, handleErrors) {
    const outputArguments = results[0].outputArguments;

    if (outputArguments[0].value.value !== StatusCodes.Good.value) {
      handleErrors(new Error(outputArguments[1].value));
    } else {
      const createdNode = outputArguments[3].value[0].value;
      const creatingNodeFailed = outputArguments[3].value[1].value;

      if (creatingNodeFailed) {
        Logger.error(`Node ${
          combinedNodeFile.typeDefinitionFile.nodeId.toString()
          }: Creating node failed`);
      } else if (createdNode) {
        Logger.info(`Created node:  ${
          combinedNodeFile.typeDefinitionFile.nodeId.toString()
          }`);
      }
      handleErrors(null, StatusCodes.Good, done => done());
    }
  }
}

import { StatusCodes } from 'node-opcua';
import QueueStream from '../stream/QueueStream';
import CombinedNodeFile from './CombinedNodeFile';

/**
 * A stream that combines content {@link AtviseFile}s with type definition {@link AtviseFile}s
 * to {@link CombinedNodeFile}s.
 */
export default class NodeFileStream extends QueueStream {

  /**
   * Creates a new NodeFileStream.
   */
  constructor(options = {}) {
    super();

    /**
     * Defines wether the stream works with {CombinedNodeFiles} or {AtviseFile}s.
     * @type {Boolean}
     */
    this.createNodes = options.createNodes || false;


    /**
     * The file cache
     * @type {Map<node-opcua~NodeId, CombinedNodeFile>}
     */
    this.combinedFilesCache = {};
  }

  /**
   * Returns an error message specifically for the given atvise file
   * @param {AtviseFile} file The file to get
   * the error message for.
   * @return {String} The specific error message.
   */
  processErrorMessage(file) {
    return `Error processing file with node id: ${file.nodeId.toString()}`;
  }

  /**
   * Mapps the given content {@link AtviseFiles}s with the given type
   * definition{@link AtviseFiles}s.
   * @param {AtviseFile} file The file to process
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */

  processChunk(file, handleErrors) {
    const nodeId = file.nodeId;
    let combinedFile = {};

    if (!CombinedNodeFile.hasValidType(file)) {
      handleErrors(new Error(`NodeFileStream: File has invalid type:  ${nodeId.toString()}`));
    }

    if (this.combinedFilesCache[nodeId]) {
      combinedFile = this.combinedFilesCache[nodeId];

      if (!combinedFile.addFile(file)) {
        handleErrors(new Error(`NodeFileStream: Duplicate file:  ${nodeId.toString()}`));
      }
    } else {
      this.combinedFilesCache[nodeId] = new CombinedNodeFile(file, this.createNodes);
      combinedFile = this.combinedFilesCache[nodeId];
    }

    if (combinedFile.isComplete) {
      handleErrors(null, StatusCodes.Good, done => {
        this.push(combinedFile);
        delete this.combinedFilesCache[nodeId];
        done();
      });
    } else {
      handleErrors(null, StatusCodes.Good, done => done());
    }
  }
}

import Logger from 'gulplog';
import { StatusCodes } from 'node-opcua';
import QueueStream from '../stream/QueueStream';

/**
 * A stream that writes all read {@link CombinedNodeFiles}s to their corresponding
 * nodes on atvise server.
 */
export default class WriteStream extends QueueStream {


  /**
   * Creates a new WriteStream based on a source file stream.
   * @param {Object} options The stream configuration options.
   */
  constructor(options = {}) {
    super();

    /**
     * Defines wether the stream works with {CombinedNodeFiles} or {AtviseFile}s.
     * @type {Boolean}
     */
    this.createNodes = options.createNodes || false;
  }


  /**
   * Returns an error message specifically for the given combined file.
   * @param {CombinedNodeFile} combinedNodeFile The combined file to process
   * the error message for.
   * @return {String} The specific error message.
   */
  processErrorMessage(combinedNodeFile) {
    return `Error processing file:  ${combinedNodeFile.contentFile.nodeId.toString()}`;
  }

  /**
   * Writes {@link CombinedNodeFile.contentFile}'s values to the corresponding nodes on
   * the atvise server.
   * @param {CombinedNodeFile} combinedNodeFile The combined file to process.
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  processChunk(combinedNodeFile, handleErrors) {
    const contentFile = combinedNodeFile.contentFile;

    if (this.createNodes && combinedNodeFile.isTypeDefOnlyFile) {
      this.push(combinedNodeFile);
      handleErrors(null, StatusCodes.Good, done => done());
    } else {
      try {
        this.session.writeSingleNode(contentFile.nodeId.toString(), {
          dataType: contentFile.dataType,
          arrayType: contentFile.arrayType,
          value: contentFile.value,
        }, (err, statusCode) => {
          if (statusCode.value === StatusCodes.BadUserAccessDenied.value) {
            Logger.warn(`Error writing node ${
              contentFile.nodeId.toString()
            }: Make sure it is not opened in atvise builder`);

            handleErrors(err, StatusCodes.Good, done => done());
          } else if (statusCode === StatusCodes.BadNodeIdUnknown) {
            if (this.createNodes) {
              Logger.debug(`Node ${
                contentFile.nodeId.toString()
                }: does not exist and is pushed to create node stream`);

              this.push(combinedNodeFile);
            } else {
              Logger.info(`Node ${
                contentFile.nodeId.toString()
                }: does not exist in atvise server address space`);
            }

            handleErrors(err, StatusCodes.Good, done => done());
          } else {
            this.emit('write-successful', contentFile);
            handleErrors(err, StatusCodes.Good, done => done());
          }
        });
      } catch (e) {
        handleErrors(e);
      }
    }
  }
}

import { DataType, StatusCodes, VariantArrayType } from 'node-opcua';
import QueueStream from '../stream/QueueStream';
import NodeId from '../ua/NodeId';
import checkType from '../../util/validation';

/**
 * Call script node id
 * @type {node-opcua~NodeId}
 */
const CallScriptMethodId = new NodeId('ns=1;s=AGENT.SCRIPT.METHODS.callScript');

/**
 * Base node id for callscript node
 * @type {node-opcua~NodeId}
 */
const CallScriptMethodBaseNodeId = CallScriptMethodId.parentNodeId;


/**
 * A stream that processes atvise server requests in parallel.
 * @abstract
 */
export default class CallScriptStream extends QueueStream {

  constructor(targetScriptId) {
    if (!checkType(targetScriptId, NodeId)) {
      throw new Error('CallScriptStream#constructor: Given targetScriptId is undefined' +
        ' or has invalid type!');
    }

    super();
    this.targetScriptId = targetScriptId;
    this.targetScriptBaseId = targetScriptId.parentNodeId;
  }

  /**
   * Returns an error message specifically for the given combined file.
   * @param {CombinedNodeFile} combinedNodeFile The combined file to process
   * the error message for.
   * @return {Object} The resulting call script object.
   */
  createCallObject(combinedNodeFile) {
    const parameters = this.createParameters(combinedNodeFile);

    return {
      objectId: CallScriptMethodBaseNodeId.toString(),
      methodId: CallScriptMethodId.toString(),
      inputArguments: [
        {
          dataType: DataType.NodeId,
          value: this.targetScriptId,
        },
        {
          dataType: DataType.NodeId,
          value: this.targetScriptBaseId,
        },
        {
          dataType: DataType.String,
          arrayType: VariantArrayType.Array,
          value: parameters.paramNames,
        },
        {
          dataType: DataType.Variant,
          arrayType: VariantArrayType.Array,
          value: parameters.paramValues,
        },
      ],
    };
  }

  /**
   * Creates the script parameters for the given chunk
   * @param {*} chunk The chunk to create the parameter object for
   * the error message for.
   * @return {Object} The resulting script parameters. The object always needs to contain a
   * 'paramNames' and a 'paramValues' array as property
   */
  createParameters(chunk) { // eslint-disable-line no-unused-vars
    throw new Error('CallScriptStream#createParameters must be implemented by all subclasses');
  }

  /**
   * Handles the node-opcua call script method callback
   * @param {Error} err If the call throw an error or not
   * @param {Array} results The result of the call
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   * @abstract
   */
  handleCallback(err, results, handleErrors) { // eslint-disable-line no-unused-vars
    throw new Error('CallScriptStream#handleCallback must be implemented by all subclasses');
  }

  /**
   * Performs opcua method calls for the given call object configuration
   * @param {*} chunk The chunk being processed.
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  processChunk(chunk, handleErrors) {
    const callObj = this.createCallObject(chunk);

    try {
      this.session.call([callObj], (err, results) => {
        if (err) {
          handleErrors(err);
        } else if (results[0].statusCode.value !== StatusCodes.Good.value) {
          handleErrors(err, results[0].statusCode, done => done());
        } else {
          this.handleCallback(results, chunk, handleErrors);
        }
      });
    } catch (e) {
      handleErrors(e);
    }
  }
}

import { StatusCodes } from 'node-opcua';
import Stream from './Stream';

/**
 * A stream that process atvise server requests in parallel.
 * @abstract
 */
export default class QueueStream extends Stream {

  /**
   * Creates a new QueueStream with the given options.
   * @param {Object} [options] The options to use
   * @param {Number} [options.maxParallel] The maximum of parallel tasks to execute.
   */
  constructor(options = {}) {
    const maxParallel = options.maxParallel || 250;
    super(Object.assign(options, { highWaterMark: maxParallel }));

    /**
     * The number of running operations.
     * @type {Number}
     */
    this._processing = 0;

    /**
     * The number of chunks processed so far.
     * @type {Number}
     */
    this._processed = 0;

    /**
     * The queued chunks.
     * @type {*[]}
     */
    this._queued = [];

    /**
     * The maximum of parallel tasks to execute
     * @type {number}
     */
    this._maxParallel = maxParallel;

    /**
     * The timestamp of the date when the stream was created.
     * @type {Number}
     */
    this._start = (new Date()).getTime();

    this.on('processed-chunk', () => {
      if (!this.queueEmpty) {
        this._processChunk(this._queued.shift());
      } else if (this._processing === 0) {
        this.emit('drained');
      }
    });
  }

  /**
   * `true` if there are queued operations or an operation is running right now.
   * @type {Boolean}
   */
  get hasPending() {
    return this._processing > 0 || this._queued.length > 0;
  }

  /**
   * `true` if there are no queued operations
   * @type {Boolean}
   */
  get queueEmpty() {
    return this._queued.length === 0;
  }

  /**
   * The number of chunks already processed.
   * @type {Number}
   */
  get processed() {
    return this._processed;
  }

  /**
   * The number of processed chunks per second.
   * @type {Number}
   */
  get opsPerSecond() {
    return (this._processed / (((new Date()).getTime() - this._start) / 1000)) || 0;
  }

  /**
   * The error message to use when processing a chunk fails. **Must be overridden by all
   * subclasses!**
   * @param {*} chunk The chunk being processed.
   * @return {String} The error message to use.
   * @abstract
   */
  processErrorMessage(chunk) { // eslint-disable-line no-unused-vars
    throw new Error('QueueStream#processErrorMessage must be implemented by all subclasses');
  }

  /**
   * The function to call when a chunk is ready to be processed. **Must be overridden by all
   * subclasses.**
   * @param {*} chunk The chunk to process.
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors Call this function to handle errors and bad status codes. When no error occured
   * and the status code received is fine, `onSuccess` is called. Further processing of valid
   * chunks, e.g. recursions should happen in `onSuccess`. **Note that `onSuccess` is an
   * asynchronous function with a callback as an argument.**
   * @example <caption>Basic implementation</caption>
   * class MyQueueStream extends QueueStream {
   *   ...
   *   processChunk(chunk, handle) {
   *     client.session.doSomething((err, result, statusCode) => handle(err, statusCode, done => {
   *       // This is called if err is falsy and status code is node-opcua~StatusCodes.Good
   *       doSomethingWith(result);
   *       done();
   *     }));
   *   }
   *   ...
   * }
   * @example <caption>Implement a recursion</caption>
   * class RecursiveQueueStream extends QueueStream {
   *   ...
   *   processChunk(chunk, handle) {
   *     client.session.doSomething((err, result, statusCode) => handle(err, statusCode, done => {
   *       // Write the result back to the stream.
   *       // This means, that `result` will be queued and, as soon as possible, #processChunk will
   *       // be called with `result` as the `chunk` argument.
   *       this.write(result, null, done);
   *     }));
   *   }
   *   ...
   * }
   * @example <caption>Allowing some invalid status codes</caption>
   * import { StatusCodes } from 'node-opcua';
   *
   * class FriendlyQueueStream extends QueueStream {
   *   ...
   *   processChunk(chunk, handle) {
   *     client.session.doSomething((err, result, statusCode) => {
   *       if (statusCode.value === StatusCodes.BadUserAccessDenied.value) {
   *         Logger.warn(`Ignored invalid status: ${statusCode.description}`);
   *         handle(err, StatusCodes.Good, done => done());
   *       } else {
   *         handle(err, statusCode, done => done());
   *       }
   *     });
   *   }
   *   ...
   * }
   * @abstract
   */
  processChunk(chunk, handleErrors) { // eslint-disable-line no-unused-vars
    handleErrors(new Error('QueueStream#processChunk must be implemented by all subclasses'));
  }

  /**
   * Calls {@link QueueStream#processChunk} and handles errors and invalid status codes.
   * @param {*} chunk The chunk to process.
   * @emits {*} Emits a `processed-chunk` event once a chunk was processed.
   */
  _processNextChunk(chunk) {
    this._processing--;
    this._processed++;
    this.emit('processed-chunk', chunk);
  }

  /**
   * Calls {@link QueueStream#processChunk} and handles errors and invalid status codes.
   * @param {*} chunk The chunk to process.
   * @emits {*} Calls {@link QueueStream#_processNextChunk} event once a chunk was processed.
   */
  _processChunk(chunk) {
    this._processing++;

    this.processChunk(chunk, (err, statusCode, onSuccess) => {
      if (err) {
        this.emit('error', new Error(`${this.processErrorMessage(chunk)}: ${err.message}`));
      } else if (statusCode.value !== StatusCodes.Good.value) {
        this.emit('error', new Error(`${this.processErrorMessage(chunk)}:`,
          `${statusCode.description}`));
      } else {
        onSuccess(() => this._processNextChunk(chunk));
      }
    });
  }

  /**
   * Enqueues the given chunk for processing.
   * @param {*} chunk The chunk to enqueue.
   */
  _enqueueChunk(chunk) {
    if (this._processing < this._maxParallel) {
      this._processChunk(chunk);
    } else {
      this._queued.push(chunk);
    }
  }

  /**
   * Calls {@link QueueStream#_enqueueChunk} as soon as the stream's session is opened.
   * @param {*} chunk The chunk to transform.
   * @param {String} enc The encoding used.
   * @param {function} callback Called once the chunk has been enqueued.
   */
  _transform(chunk, enc, callback) {
    if (this.session) {
      this._enqueueChunk(chunk);
      callback();
    } else {
      this.once('session-open', () => {
        this._enqueueChunk(chunk);
        callback();
      });
    }
  }

  /**
   * Waits for pending operations to complete.
   * @param {function} callback Called once all queued chunks have been processed.
   */
  _flush(callback) {
    if (this.hasPending) {
      this.once('drained', () => {
        super._flush(callback);
      });
    } else {
      super._flush(callback);
    }
  }

}

import { ctor as throughStreamClass } from 'through2';
import Session from '../ua/Session';

/**
 * An object transform stream connected to atvise server.
 */
export default class Stream extends throughStreamClass({ objectMode: true }) {

  /**
   * Creates a new Stream and starts opening a new session to atvise server.
   * @param {Object} [options] The options to use. See
   * [through2 documentation](https://github.com/rvagg/through2#options) for details.
   * @param {Boolean} [options.keepSessionAlive=false] If the ativse server session should be closed
   * one the stream ends.
   * @emits {Session} Emits an `session-open` event once the session is open, passing the Session
   * instance.
   */
  constructor(options = {}) {
    super(options);

    /**
     * `true` if the stream's atvise server session should be kept alive once the stream ends.
     * @type {Boolean}
     */
    this._keepSessionAlive = options.keepSessionAlive || false;

    Session.create()
      .then(session => (this.session = session))
      .then(session => this.emit('session-open', session))
      .catch(err => this.emit('error', err));
  }

  /**
   * Called just before the stream is closed: Closes the open session.
   * @param {function(err: ?Error, data: Object)} callback Called once the session is closed.
   */
  _flush(callback) {
    if (this.session && !this._keepSessionAlive) {
      Session.close(this.session)
        .then(() => callback())
        .catch(err => callback(err));
    } else {
      callback();
    }
  }

}

import filter from 'gulp-filter';
import Transformer from './Transformer';

/**
 * A transformer that transforms only some of the files read.
 * @abstract
 */
export default class PartialTransformer extends Transformer {

  /**
   * Creates a new partial transformer with the specified options.
   * @param {Object} options The options to use. See {@link Transformer#constructor} for available
   * options.
   */
  constructor(options = {}) {
    super(options);

    /**
     * The filter stream used.
     * @type {Stream}
     */
    this.filter = filter(file => this.shouldBeTransformed(file), { restore: true });
  }

  /**
   * `true` if `file` should be transformed.
   * @param {AtviseFile} file The file to transform or not.
   * @abstract
   */
  shouldBeTransformed(file) { // eslint-disable-line no-unused-vars
    throw new Error('PartialTransformer#shouldBeTransformed must be implemented by all subclasses');
  }

  /**
   * Transforms a file if {@link PartialTransformer#shouldBeTransformed} returns `true`.
   * @param {AtviseFile} file The file to transform.
   * @param {String} enc The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error that occured
   * while transforming or (optionally) the transformed file.
   */
  _transform(file, enc, callback) {
    if (this.shouldBeTransformed(file)) {
      super._transform(file, enc, callback);
    } else {
      callback(null, file);
    }
  }

  /**
   * Applies the transformer to the given stream. It does so by running the following steps:
   *  - Pipe the {@link PartialTransformer#filter} stream
   *  - If PartialTransformer#applyToFilteredStream is overrridden, apply it.
   *  - Otherwise pipe {@link Transformer#withDirection}.
   *  - Restore the filter stream
   * @param {Stream} stream The stream to apply the transformer to.
   * @param {TransformDirection} direction The direction to use.
   * @return {Stream} The resulting stream.
   */
  applyToStream(stream, direction) {
    const filteredStream = stream.pipe(this.filter);

    return (this.applyToFilteredStream(filteredStream, direction) ||
      filteredStream.pipe(this.withDirection(direction)))
      .pipe(this.filter.restore);
  }

  /**
   * Applies a stream transformer to the given, already filtered stream. Override this method if you
   * want to pipe streams directly. Returning a falsy value (e.g. false, null, undefined) invokes
   * {@link Transformer#transformFromDB} or {@link Transformer#transformFromFilesystem} instead.
   * @param {Stream} stream The stream to apply the transformer to.
   * @param {TransformDirection} direction The direction to use.
   * @return {?Stream} The resulting stream.
   */
  applyToFilteredStream(stream, direction) { // eslint-disable-line no-unused-vars
    return false;
  }

}

import { readdir } from 'fs';
import { extname, basename, join } from 'path';
import Logger from 'gulplog';
import PartialTransformer from './PartialTransformer';
import AtviseFile from '../mapping/AtviseFile';

/**
 * Determines which files are needed to create a combined file and stores these files as long as
 * some of them are missing.
 */
export class CombineFilesCache {

  /**
   * Creates a new DisplayCache.
   */
  constructor() {
    /**
     * The files caches for the given path.
     * @type {Map<String, vinyl~File>}
     */
    this._files = {};

    /**
     * The extensions of the files required.
     * @type {String[]}
     */
    this._required = {};
  }

  /**
   * Returns the extensions of the missing files for the given `dirname`.
   * @param {String} dirname The cache key to look for.
   * @return {String[]} Extensions of the missing files.
   */
  missingExtensions(dirname) {
    const required = this._required[dirname];
    const files = this._files[dirname];

    return required.filter(ext => files[ext] === undefined);
  }

  /**
   * Checks if, when `file` is added, all required files are cached.
   * @param {vinyl~File} file The file to add before checking.
   * @param {function(err: ?Error, files: ?Map<String, vinyl~File>)} callback Called with the error
   * that occured while checking or all files related to `file` if all required files are already
   * cached.
   */
  gotAllFiles(file, callback) {
    const dirname = file.dirname;

    if (!this._required[dirname]) {
      readdir(dirname, (err, files) => {
        if (err) {
          callback(err);
        } else {
          this._files[dirname] = {};
          this._required[dirname] = files
            .filter(name => name[0] !== '.')
            .map(name => extname(name));

          this.gotAllFiles(file, callback);
        }
      });
    } else {
      this._files[dirname][file.extname] = file;

      if (this.missingExtensions(dirname).length === 0) {
        const files = this._files[dirname];
        callback(null, files);

        delete this._files[dirname];
        delete this._required[dirname];
      } else {
        callback(null);
      }
    }
  }

}

/**
 * A transformer that splits files into multiple others.
 * @abstract
 */
export default class SplittingTransformer extends PartialTransformer {

  /**
   * Creates a new SplittingTransformer.
   * @param {Object} options The options to apply.
   */
  constructor(options) {
    super(options);

    /**
     * The cache used when collecting files to combine.
     * @type {CombineFilesCache}
     */
    this._combineFilesCache = new CombineFilesCache();
  }

  /**
   * Creates a combined file from the cached split files.
   * @param {Map<String, AtviseFile>} files The cached files stored against their extensions.
   * @param {AtviseFile} lastFile The last file collected. This is the only file guaranteed to be
   * set, therefore us if for error messages, etc.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Should be called with any errors
   * that occur while combining the files, or optionally the resulting file.
   * @abstract
   */
  createCombinedFile(files, lastFile, callback) { // eslint-disable-line no-unused-vars
    throw new Error(
      'SplittingTransformer#createCombinedFile must be implemented by all subclasses'
    );
  }

  /**
   * Calls {@link SplittingTransformer#createCombinedFile} as soon as all dependencies are
   * required files are cached.
   * @param {AtviseFile} file The read file.
   * @param {String} enc The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error occured while
   * caching files or creating the combined file or optionally the resulting combined file.
   */
  transformFromFilesystem(file, enc, callback) {
    this._combineFilesCache.gotAllFiles(file, (err, allFiles) => {
      if (err) {
        callback(err);
      } else if (allFiles) {
        this.createCombinedFile(allFiles, file, callback);
      } else {
        callback(null);
      }
    });
  }

  /**
   * Splits a {@link vinyl~File}: The resulting is a clone of the input file, with a different path.
   * @param {vinyl~File} file The file to split.
   * @param {?String} newExtension The extension the resulting file gets.
   * @return {vinyl~File} The resulting file.
   * @example
   * // Assuming that `original` is a File with the path 'path/to/file.type.xml':
   * const result = SplittingTransformer.splitFile(original, '.another');
   * // `result` is a new File, with the contents of `original` and the path
   * // 'path/to/file.type/file.another'
   */
  static splitFile(file, newExtension) {
    const newFile = file.clone();

    newFile.basename = `${newFile.stem}/${newFile.stem}`;
    newFile.extname = newExtension;
    newFile.contents = Buffer.from('');

    return newFile;
  }

  /**
   * Combines split files to a single one.
   * @param {vinyl~File[]} files The files to combine.
   * @param {String} newExtension The extension the resulting file gets.
   * @return {vinyl~File} The resulting file.
   */
  static combineFiles(files, newExtension) {
    const newFile = files[0].clone();

    newFile.path = `${newFile.dirname}${newExtension}`;

    return newFile;
  }

  /**
   * If there are any missing files this method loads these files and calls
   * {SplittingTransformer#createCombinedFile} with them.
   * @param {function(err: ?Error)} callback Called with the error that occurred while loading
   * missing files.
   */
  _flush(callback) {
    const missingDirnames = Object.keys(this._combineFilesCache._files);

    if (missingDirnames.length > 0) {
      Promise.all(
        missingDirnames.map(dirname => {
          const files = this._combineFilesCache._files[dirname];
          const firstFile = files[Object.keys(files)[0]];

          const missing = this._combineFilesCache.missingExtensions(dirname);
          const stem = basename(dirname, extname(dirname));
          const paths = missing.map(ext => join(dirname, '/', `${stem}${ext}`));

          delete this._combineFilesCache._files[dirname];
          delete this._combineFilesCache._required[dirname];

          Logger.debug('Loading', paths.length, 'required file(s)');

          return Promise.all(
            paths.map((path, i) => AtviseFile.read({
              cwd: firstFile.cwd,
              base: firstFile.base,
              path,
            })
              .then(file => {
                files[missing[i]] = file;
              })
            )
          )
            .then(() => new Promise((resolve, reject) => {
              this.createCombinedFile(files, firstFile, (err, file) => {
                if (err) {
                  reject(err);
                } else {
                  this.push(file);

                  resolve(file);
                }
              });
            }));
        })
      )
        .then(files => Logger.debug('Created', files.length, 'additional file(s)'))
        .then(() => callback())
        .catch(err => callback(err));
    } else {
      callback();
    }
  }

}

