import { inspect } from 'util';
import { ctor as throughStreamClass } from 'through2';

/**
 * The directions a transformer can be run in.
 * @type {{FromDB: String, FromFilesystem: String}}
 */
export const TransformDirection = {
  FromDB: 'FromDB',
  FromFilesystem: 'FromFilesystem',
};

function isValidDirection(direction) {
  return [
    TransformDirection.FromDB,
    TransformDirection.FromFilesystem,
  ].includes(direction);
}

/**
 * A special kind of an object transform stream: It does apply different transformations based on
 * it's direction.
 * @abstract
 */
export default class Transformer extends throughStreamClass({ objectMode: true }) {

  /**
   * Creates a new Transformer with the specified options.
   * @param {Object} [options] The options to use.
   * @param {TransformDirection} [options.direction] The direction to use.
   * @throws {Error} Throws an error if the given direction is invalid.
   */
  constructor(options = {}) {
    super();

    /**
     * The options the transformer was created with. Used for printing description.
     * @type {Object}
     */
    this._options = options;

    if (options.direction) {
      if (isValidDirection(options.direction)) {
        /**
         * The transformer's direction
         * @type {TransformerDirection}
         */
        this.direction = options.direction;
      } else {
        throw new Error('Invalid direction');
      }
    }
  }

  /**
   * Returns the Transformer with the given direction
   * @param {TransformDirection} direction The direction to use.
   * @return {Transformer} Itself, to be chainable.
   * @throws {Error} Throws an error if the given direction is invalid.
   */
  withDirection(direction) {
    if (!isValidDirection(direction)) {
      throw new Error('Invalid direction');
    }

    this.direction = direction;
    return this;
  }

  /**
   * Calls {@link Transformer#transformFromDB} or {@link Transformer#transformFromFilesystem}
   * based on the transformer's direction.
   * @param {Object} chunk The chunk to transform.
   * @param {String} enc The encoding used.
   * @param {function(err: ?Error, obj: ?Object)} callback Called with the error that occured while
   * transforming or (optionally) the transformed object.
   * @throws {Error} Throws an error if the transformer has no valid direction.
   */
  _transform(chunk, enc, callback) {
    if (!this.direction) {
      callback(new Error('Transformer has no direction'));
    } else if (this.direction === TransformDirection.FromDB) {
      this.transformFromDB(chunk, enc, callback);
    } else {
      this.transformFromFilesystem(chunk, enc, callback);
    }
  }

  /**
   * **Must be overridden by all subclasses:** Transforms the given chunk when using
   * {@link TransformDirection.FromDB}.
   * @param {Object} chunk The chunk to transform.
   * @param {String} enc The encoding used.
   * @param {function(err: ?Error, obj: ?Object)} callback Called with the error that occured while
   * transforming or (optionally) the transformed object.
   */
  transformFromDB(chunk, enc, callback) {
    callback(new Error('Transformer#transformFromDB must be overridden by all subclasses'));
  }

  /**
   * **Must be overridden by all subclasses:** Transforms the given chunk when using
   * {@link TransformDirection.FromFilesystem}.
   * @param {Object} chunk The chunk to transform.
   * @param {String} enc The encoding used.
   * @param {function(err: ?Error, obj: ?Object)} callback Called with the error that occured while
   * transforming or (optionally) the transformed object.
   */
  transformFromFilesystem(chunk, enc, callback) {
    callback(new Error('Transformer#transformFromFilesystem must be overridden by all subclasses'));
  }

  /**
   * Applies the transformer to the given stream. By default this just invokes
   * {@link Transformer#transformFromDB} or {@link Transformer#transformFromFilesystem}. Override
   * this method if you want to pipe streams directly.
   * @param {Stream} stream The stream to apply the transformer to.
   * @param {TransformDirection} direction The direction to use.
   * @return {Stream} The resulting stream.
   */
  applyToStream(stream, direction) {
    return stream.pipe(this.withDirection(direction));
  }

  /**
   * Creates a stream with all transformers passed, with the given direction. Transformers are
   * reversed if using {@link TransformDirection.FromFilesystem}.
   * @param {Stream} stream The stream to apply the transformers to.
   * @param {Transformer[]} transformers The transformers to apply.
   * @param {TransformDirection} direction The direction to use.
   * @return {Transformer} The last transformer passed, piped to the previous.
   */
  static applyTransformers(stream, transformers, direction) {
    if (!isValidDirection(direction)) {
      throw new Error('Direction is invalid');
    }

    if (transformers.length === 0) {
      return stream;
    }

    return (direction === TransformDirection.FromDB ? transformers : transformers.reverse())
      .reduce((prev, curr) => curr.applyToStream(prev, direction), stream);
  }

  /**
   * Prints the transformer.
   * @param {?Number} depth The depth to inspect.
   * @param {Object} options See https://nodejs.org/api/util.html#util_util_inspect_object_options
   * for details
   * @return {String} A string representation of the transformer.
   */
  inspect(depth, options) {
    const newOptions = options;
    newOptions.depth = options.depth === null ? null : options.depth - 1;

    if (depth < 0) {
      return options.stylize(`[${this.constructor.name}]`, 'special');
    }

    return `${options.stylize(this.constructor.name, 'special')}${inspect(this._options, newOptions)
      .replace(/^{/, '<').replace(/}$/, '>')
      .replace(/\n/, `\n${' '.repeat(this.constructor.name.length)}`)
    }`;
  }

}

import SplittingTransformer from './SplittingTransformer';
import { parse as xmlStringToObj, serialize as objToXmlString, xml } from '../xml/xamel';

/**
 * Definition for default xml header
 * @type {String}
 */
const XmlHeader = '<?xml version="1.0" encoding="UTF-8" standalone="no"?>\n';

/**
 * List of XML Tags needed for relative node addresses
 * @type {String[]}
 */
const RelativePathElements = [
  'Elements',
  'RelativePathElement',
  'TargetName',
];

/**
 * A transformer used to transform XML documents.
 */
export default class XMLTransformer extends SplittingTransformer {
  /**
   * Creates a new {xamel~NodeSet}
   * @param {xamel~Tag[]} nodes The nodes to store in the created NodeSet
   */
  createNodeSet(nodes) {
    return new xml.NodeSet(nodes);
  }

  /**
   * Creates a new {xamel~Tag}
   * @param {String} tagName The xml tag name
   * @param {Object} attributes Object containing xml attributes
   * @param {xamel~Tag} parentTag The parent tag the created tag belongs to
   * @param {*} initialChildNode The tags child item
   */
  createTag(tagName, attributes, parentTag, initialChildNode) {
    const tag = new xml.Tag(tagName, attributes, parentTag);

    if (initialChildNode) {
      if (initialChildNode instanceof xml.Tag) {
        // eslint-disable-next-line no-param-reassign
        initialChildNode.parent = tag;
      }
      tag.append(initialChildNode);
    }

    return tag;
  }

  /**
   * Creates a new {xamel~CData}
   * @param {String} scriptCode The script code to store
   */
  createCData(scriptCode) {
    return new xml.CData(scriptCode);
  }

  /**
   * Creates a new <RelativePath> tag
   * @param {Object} configObj The relative path elements configuration object
   * @param {xamel~Tag} parentTag The parent tag the relative path tag belongs to
   */
  createRelPathTag(configObj, parentTag) {
    const relPathTag = this.createTag('RelativePath', {}, parentTag);

    if (configObj) {
      let currParentTag = relPathTag;

      // Build relative path element structure
      RelativePathElements.forEach(tagName => {
        const tag = this.createTag(tagName, {}, currParentTag);
        currParentTag.append(tag);
        currParentTag = tag;
      });

      // Append relative path information
      currParentTag
        .append(this.createTag('NamespaceIndex', {}, currParentTag, configObj.nameSpaceIndex))
        .append(this.createTag('Name', {}, currParentTag, configObj.nodePath));
    } else {
      relPathTag.append(this.createTag('Elements', {}, relPathTag));
    }

    return relPathTag;
  }

  /**
   * Parses XML in a file's contents.
   * @param {AtviseFile} file The file to process.
   * @param {function(err: ?Error, result: ?Object)} callback Called with the parsed document or the
   * parse error that occurred.
   */
  decodeContents(file, callback) {
    xmlStringToObj(
      file.contents.toString(),
      {
        cdata: true,
        strictEntities: true,
        normalize: true,
      },
      callback
    );
  }

  /**
   * Builds an XML string from an object.
   * @param {xml2js~Builder} xmlObj The object to encode.
   * @param {function(err: ?Error, result: ?String)} callback Called with the resulting string or
   * the error that occurred while building.
   */
  encodeContents(xmlObj, callback) {
    try {
      callback(null, `${XmlHeader}${objToXmlString(xmlObj,
        {
          pretty: true,
          header: false,
        })
      }`);
    } catch (e) {
      callback(e);
    }
  }
}

import { OPCUAClient } from 'node-opcua';
import ProjectConfig from '../../config/ProjectConfig';

/**
 * A wrapper around {@link node-opcua~OPCUAClient} used to connect to atvise server.
 */
export default class Client {

  /**
   * Creates and connects a new instance of {@link node-opcua~OPCUAClient}.
   * @return {Promise<node-opcua~OPCUAClient, Error>} Fulfilled with an already connected
   * {@link node-opcua~OPCUAClient} instance, rejected if an error occured.
   */
  static create() {
    const client = new OPCUAClient({
      requestedSessionTimeout: 600000,
      keepSessionAlive: true,
    });

    return new Promise((resolve, reject) => {
      const endpoint = `opc.tcp://${ProjectConfig.host}:${ProjectConfig.port.opc}`;

      setTimeout(() => reject(
        new Error(`Unable to connect to ${endpoint}: Connection timed out`)
      ), 5000);

      client.connect(endpoint, err => {
        if (err) {
          reject(new Error(`Unable to connect to ${endpoint}: ${err.message}`));
        } else {
          resolve(client);
        }
      });
    });
  }

}

import { sep, extname, dirname } from 'path';
import { NodeId as OpcNodeId } from 'node-opcua';

/**
 * OPC-UA node id types.
 * @type {Map<String, node-opcua~NodeIdType>}
 */
const Type = OpcNodeId.NodeIdType;

/**
 * File extensions to remove
 * @type {String[]}
 */
const ExtensionsToRemove = ['.script', '.display', '.qd'];

/**
 * OPC-UA node id types mapped against node-id identifiers (e.g. i, s ...).
 * @type {Map<String, node-opcua~NodeIdType>}
 */
const TypeForIdentifier = {
  i: Type.NUMERIC,
  s: Type.STRING,
  g: Type.GUID,
  b: Type.BYTESTRING,
};

/**
 * A wrapper around {@link node-opcua~NodeId}.
 */
export default class NodeId extends OpcNodeId {

  /**
   * Creates a new NodeId. Can be called in multiple ways:
   *  - with a {@link node-opcua~NodeIdType}, a value and a namespace (defaults to 0),
   *  - with a value only (type will be taken from it, namespace defaults to 1) or
   *  - with a {@link NodeId}s string representation (e.g. `ns=1;s=AGENT.DISPLAYS`).
   * @param {node-opcua~NodeIdType|String|Number} typeOrValue The type or value to use.
   * @param {(Number|String)} [value] The value to use.
   * @param {Number} [namespace=1] The namespace to use.
   */
  constructor(typeOrValue, value, namespace = 1) {
    if (!(Type.get(typeOrValue))) {
      let m = null;

      if (typeof typeOrValue === 'string') {
        m = typeOrValue.match(/^ns=([0-9]+);(i|s|g|b)=(.*)$/);
      }

      if (m === null) {
        super(
          Number.isNaN(Number.parseInt(typeOrValue, 10)) ? Type.STRING : Type.NUMERIC,
          typeOrValue,
          1
        );
      } else {
        const n = Number.parseInt(m[1], 10);
        const t = TypeForIdentifier[m[2]];
        const v = t === Type.NUMERIC ? Number.parseInt(m[3], 10) : m[3];

        super(t, v, n);
      }
    } else {
      super(typeOrValue, value, namespace);
    }
  }

  /**
   * Creates a new NodeId based on a file path.
   * @param {String} path The file path to use.
   * @return {NodeId} The resulting NodeId.
   */
  static fromFilePath(path) {
    let separator = '.';
    const extension = extname(path);

    // step one directory outside for split files
    if (ExtensionsToRemove.indexOf(extension) > -1) {
      // eslint-disable-next-line no-param-reassign
      path = dirname(path);
    }

    const value = path.split(sep)
      .reduce((result, current) => {
        const next = `${result}${separator}${current}`;

        if (current === 'RESOURCES') {
          separator = '/';
        }

        return next;
      });

    return new NodeId(NodeId.NodeIdType.STRING, value, 1);
  }

  /**
   * The node id's value, encoded to a file path.
   * @type {String}
   */
  get filePath() {
    const parts = this.value.split('RESOURCES');
    parts[0] = parts[0].split('.').join('/');

    return parts.join('RESOURCES');
  }

  /**
   * The node id's browsename as string
   * @type {String}
   */
  get browseName() {
    const lastSeperator = this.value.indexOf('/') > -1 ?
      '/' : '.';

    return this.value.substr(this.value.lastIndexOf(lastSeperator) + 1);
  }

  /**
   * The parent node id
   * @type {NodeId}
   */
  get parentNodeId() {
    const lastSeperator = this.value.indexOf('/') > -1 ?
      '/' : '.';

    return new NodeId(this.value.substr(0, this.value.lastIndexOf(lastSeperator)));
  }


  /**
   * Returns a string in the format 'namespace value' that is printed when inspecting the NodeId
   * using node.js's *util.inspect*.
   * @see https://nodejs.org/api/util.html#util_util_inspect_object_options
   * @param {Number} depth The depth to inspect.
   * @param {Object} options The options to use.
   * @return {String} A string in the format 'namespace value'.
   */
  inspect(depth, options) {
    return [
      options.stylize(this.namespace, 'number'),
      options.stylize(this.value, this.identifierType === Type.NUMERIC ? 'number' : 'string'),
    ].join(' ');
  }
}

import { ReferenceTypeIds } from 'node-opcua';

/**
 * Reverse map of {node-opcua~ReferenceTypeId}'s
 * @type {Object}
 */
const ReverseReferenceTypeIds = (function() {
  // eslint-disable-next-line no-return-assign
  Object.entries(ReferenceTypeIds).map(keyValuePair => this[keyValuePair[1]] = keyValuePair[0]);
  return this;
}).bind({})();


export default ReverseReferenceTypeIds;

import Emitter from 'events';
import { StatusCodes, ClientSession } from 'node-opcua';
import Logger from 'gulplog';
import Client from './Client';
import ProjectConfig from '../../config/ProjectConfig';

/**
 * The global EventEmitter used to emit events.
 * @type {events~Emitter}
 */
const emitter = new Emitter();

/**
 * The currently open sessions.
 * @type {node-opcua~ClientSession[]}
 */
const openSessions = [];

/**
 * The number of session currently being opened.
 * @type {Number}
 */
let openingSessions = 0;

/**
 * A wrapper around {@link node-opcua~ClientSession} used to connect to atvise server.
 */
export default class Session {

  /**
   * An {@link events~Emitter} that emits events when creating / closing sessions.
   * @type {events~Emitter}
   */
  static get emitter() {
    return emitter;
  }

  /**
   * Creates and opens a new {@link node-opcua~ClientSession}.
   * @return {Promise<node-opcua~ClientSession, Error>} Fulfilled with an already opened
   * {@link node-opcua~ClientSession}.
   * @emits {undefined} Emits an `all-open` event once all opening sessions are open.
   */
  static create() {
    openingSessions++;

    return Client.create()
      .then(client => new Promise((resolve, reject) => {
        client.createSession({
          userName: ProjectConfig.login.username,
          password: ProjectConfig.login.password,
        }, (err, session) => {
          if (err) {
            if (
              [
                'userName === null || typeof userName === "string"',
                'password === null || typeof password === "string"',
              ].includes(err.message) ||
              (err.response &&
              err.response.responseHeader.serviceResult === StatusCodes.BadUserAccessDenied)
            ) {
              reject(new Error('Unable to create session: Invalid login'));
            } else {
              reject(new Error(`Unable to create session: ${err.message}`));
            }
          } else {
            Object.assign(session, { _emitter: new Emitter() });

            openSessions.push(session);
            resolve(session);
          }

          openingSessions--;
          if (openingSessions === 0) {
            emitter.emit('all-open');
          }
        });
      }));
  }

  /**
   * Closes the given session.
   * @param {node-opcua~ClientSession} session The session to close.
   * @param {Boolean} [deleteSubscriptions=true] If active subscriptions should be closed as well.
   * @return {Promise<node-opcua~ClientSession, Error>} Fulfilled with the (now closed!) session or
   * rejected with the error that occured while closing.
   */
  static close(session, deleteSubscriptions = true) {
    if (!session || !(session instanceof ClientSession)) {
      return Promise.reject(new Error('session is required'));
    }

    if (session._closed) {
      return Promise.resolve();
    }

    if (session._closing) {
      return new Promise(resolve => {
        session._emitter.on('fully-closed', () => resolve(session));
      });
    }

    Object.assign(session, { _closing: true });

    return new Promise((resolve, reject) => {
      function markAsClosed() {
        openSessions.splice(openSessions.indexOf(session), 1);
        Object.assign(session, { _closed: true });

        resolve(session);
        session._emitter.emit('fully-closed');
      }

      session.close(deleteSubscriptions, err => {
        if (err) {
          if (err.response &&
            err.response.responseHeader.serviceResult === StatusCodes.BadSessionIdInvalid
          ) {
            Logger.debug('Attempted to close a session that does not exist');
            markAsClosed(session);
          } else if (err.message === 'no channel') {
            // Client already disconnected
            markAsClosed(session);
          } else {
            reject(new Error(`Unable to close session: ${err.message}`));
          }
        } else {
          session._client.disconnect(clientErr => {
            if (clientErr) {
              reject(new Error(`Unable to disconnect client: ${clientErr.message}`));
            } else {
              markAsClosed(session);
            }
          });
        }
      });
    });
  }

  /**
   * The sessions currently open.
   * @type {Session[]}
   */
  static get open() {
    return openSessions;
  }

  /**
   * Closes all open sessions.
   * @return {Promise<Error, Session[]>} Rejected with the error that occurred while closing the
   * sessions or fulfilled with the (now closed) sessions affected.
   */
  static closeOpen() {
    function closeSessions(sessions) {
      return Promise.all(
        sessions.map(session => Session.close(session))
      );
    }

    if (openingSessions === 0) {
      if (openSessions.length === 0) {
        return Promise.resolve([]);
      }

      return closeSessions(openSessions);
    }

    return new Promise((resolve, reject) => {
      emitter.once('all-open', () => {
        closeSessions(openSessions)
          .then(resolve, reject);
      });
    });
  }

}

import Emitter from 'events';
import {
  ClientSubscription,
  AttributeIds,
  subscription_service as SubscriptionService,
  StatusCodes,
} from 'node-opcua';
import ProjectConfig from '../../config/ProjectConfig';
import BrowseStream from '../pull/BrowseStream';
import QueueStream from '../stream/QueueStream';
import { Session } from '../ua/Session';

/**
 * A stream that monitors changes in the read nodes.
 */
export class SubscribeStream extends QueueStream {

  /**
   * Creates a new SubscribeStream based on some options.
   * @param {Object} options The stream options to apply.
   */
  constructor(options = {}) {
    super(Object.assign(options, { keepSessionAlive: true })); // FIXME: Option not handled.

    /**
     * Set to true once all nodes are monitored.
     * @type {Boolean}
     */
    this._trackChanges = false;

    this.once('session-open', () => this.createSubscription());
  }

  /**
   * Creates the server subscription to monitor nodes with.
   * @emit {node-opcua~ClientSubscription} Emits a `subscription-started` event once the
   * subscription started.
   */
  createSubscription() {
    const subscription = new ClientSubscription(this.session, {
      requestedPublishingInterval: 100,
      requestedLifetimeCount: 1000,
      requestedMaxKeepAliveCount: 12,
      maxNotificationsPerPublish: 10,
      publishingEnabled: true,
      priority: 10,
    });

    subscription.on('started', () => {
      /**
       * The {@link node-opcua~ClientSubscription} to use to monitor nodes.
       * @type {node-opcua~ClientSubscription}
       */
      this.subscription = subscription;
      this.emit('subscription-started', subscription);
    });

    subscription.on('failure', err => this.emit('error', err));
  }

  /**
   * Returns an error message specifically for the given reference description.
   * @param {MappingItem} mappingItem The mappingItem to process
   * the error message for.
   * @return {String} The specific error message.
   */
  processErrorMessage(mappingItem) {
    return `Error reading node ${mappingItem.sourceNodeId.toString()}`;
  }

  /**
   * Monitors the nodes specified by the given {MappingItem}s.
   * @param {MappingItem} mappingItem The mappingItem to process
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  processChunk(mappingItem, handleErrors) {
    const nodeId = mappingItem.nodeId;

    if (!mappingItem.shouldBeRead) {
      handleErrors(null, StatusCodes.Good, done => done());
      return;
    }

    const item = this.subscription.monitor({
      nodeId,
      attributeId: AttributeIds.Value,
    }, {
      clientHandle: 13,
      samplingInterval: 250,
      queueSize: 1,
      discardOldest: true,
    }, SubscriptionService.TimestampsToReturn.Both);

    item.on('changed', dataValue => {
      if (!this._trackChanges) {
        handleErrors(null, StatusCodes.Good, done => done()); // Ignore first notification
      } else {
        mappingItem.createConfigItemFromDataValue(dataValue);
        this.emit('change', mappingItem);
      }
    });

    item.on('err', err => {
      /*
       This works around a bug in node-opcua:
       Instead of a error a string is emitted
       FIXME: Remove once bug is fixed
       */
      if (err instanceof Error) {
        handleErrors(err);
      } else {
        handleErrors(new Error(err));
      }
    });
  }

  /**
   * Buffers all read node descriptions until the subscription started, then calls
   * {@link QueueStream#_enqueueChunk} with them.
   * @param {node-opcua~ReferenceDescription} desc Reference description of the node to transform.
   * @param {String} enc The encoding used.
   * @param {function(err: ?Error)} callback Called with the error that occured while trying to
   * monitor the given node.
   */
  _transform(desc, enc, callback) {
    if (this.subscription) {
      this._enqueueChunk(desc);
      callback();
    } else {
      this.once('subscription-started', () => {
        this._enqueueChunk(desc);
        callback();
      });
    }
  }

  /**
   * Starts tracking node changes.
   * @param {function(err: ?Error)} callback Called once flushing is complete.
   */
  _flush(callback) {
    super._flush(err => {
      this._trackChanges = true;
      callback(err);
    });
  }

}

/**
 * Watches the given nodes for value changes.
 * @emit {ReadStream.ReadResult} Emits `change` events when a watched node changes.
 */
export default class Watcher extends Emitter {

  /**
   * Creates a new Watcher with the given nodes.
   * @param {NodeId[]} nodes The nodes to watch (recursively).
   */
  constructor(nodes = ProjectConfig.nodes) {
    super();

    /**
     * The browse stream that discovers the nodes to watch.
     * @type {BrowseStream}
     */
    this._browseStream = new BrowseStream(nodes)
      .on('error', err => this.emit('error', err));

    /**
     * The stream that starts monitoring the nodes to watch.
     * @type {SubscribeStream}
     */
    this._subscribeStream = new SubscribeStream()
      .on('error', err => this.emit('error', err));

    this._browseStream.pipe(this._subscribeStream);

    this._subscribeStream.on('finish', () => this.emit('ready'));
    this._subscribeStream.on('change', event => this.emit('change', event));
  }

  /**
   * Ends monitoring nodes.
   */
  close() {
    if (this._subscribeStream.session) {
      Session.close(this._subscribeStream.session)
        .catch(err => this.emit('error', err));
    }
  }

}

