import DeleteFsStream from '../lib/gulp/DeleteFsStream';
import ProjectConfig from '../config/ProjectConfig';

/**
 * Deletes listed files from the file system.
 */
export default function deleteFs(callback) {
  return new DeleteFsStream({
    deleteFileName: ProjectConfig.DeleteFileNames.fs,
  })
    .on('close', callback);
}

deleteFs.description = 'Deletes listed files from file system';

import DeleteServerStream from '../lib/gulp/DeleteServerStream';
import ProjectConfig from '../config/ProjectConfig';

/**
 * Deletes listed atvise server nodes.
 */
export default function deleteServer(callback) {
  return new DeleteServerStream({
    deleteFileName: ProjectConfig.DeleteFileNames.server,
  })
    .on('finish', callback);
}

deleteServer.description = 'Deletes listed nodes from atvise server';

import DiffStream from '../lib/gulp/DiffStream';
