  });
}

push.description = 'Push all stored nodes to atvise server';

import { dirname } from 'path';
import sane from 'sane';
import browserSync from 'browser-sync';
import Logger from 'gulplog';
import { obj as createStream } from 'through2';
import PushStream from '../lib/gulp/PushStream';
import PullStream from '../lib/gulp/PullStream';
import AtviseFile from '../lib/mapping/AtviseFile';
import ServerWatcher from '../lib/watch/Watcher';
import ProjectConfig from '../config/ProjectConfig';
import NodeId from '../lib/ua/NodeId';
import { validateDirectoryExists } from '../util/fs';

/**
 * The task executed when running `atscm watch`.
 */
export class WatchTask {

  /**
   * Creates a new watch task instance. Also creates a new Browsersync instance.
   */
  constructor() {
    /**
     * The Browsersync instance used.
     * @type {events~Emitter}
     */
    this.browserSyncInstance = browserSync.create();

    /**
     * If the task is currently pulling.
     * @type {boolean}
     */
    this._pulling = false;

    /**
     * If the task is currently pushing.
     * @type {boolean}
     */
    this._pushing = false;

    /**
     * Timestamp of the last pull
     * @type {number}
     */
    this._lastPull = 0;

    /**
     * The {@link NodeId} of the last push.
     * @type {?NodeId}
     */
    this._lastPushed = null;
  }

  /**
   * Waits for a watcher (which can actually be any kind of {@link events~Emitter}) to emit a
   * 'ready' event.
   * @param {events~Emitter} watcher The watcher to wait for.
   * @return {Promise<events~Emitter, Error>} Fulfilled with the set up watcher or rejected with the
   * watcher error that occurred while waiting for it to get ready.
   */
  _waitForWatcher(watcher) {
    return new Promise((resolve, reject) => {
      watcher.on('error', err => reject(err));
      watcher.on('ready', () => resolve(watcher));
    });
  }

  /**
   * Starts a file watcher for the directory {@link WatchTask#directoryToWatch}.
   * @return {Promise<sane~Watcher, Error>} Fulfilled with the file watcher once it is ready or
   * rejected with the error that occurred while starting the watcher.
   */
  startFileWatcher() {
    return validateDirectoryExists(ProjectConfig.RelativeSourceDirectoryPath)
      .catch(err => {
        if (err.code === 'ENOENT') {
          Logger.info(
            `Create a directory at ${ProjectConfig.RelativeSourceDirectoryPath}`,
            'or run "atscm pull" first'
          );

          Object.assign(err, {
            message: `Directory ${ProjectConfig.RelativeSourceDirectoryPath} does not exist`,
          });
        }

        throw err;
      })
      .then(() => this._waitForWatcher(sane(ProjectConfig.RelativeSourceDirectoryPath, {
        glob: '**/*.*',
        watchman: process.platform === 'darwin',
      })));
  }

  /**
   * Starts a watcher that watches the atvise server for changes.
   * @return {Promise<Watcher, Error>} Fulfilled with the server watcher once it is ready or
   * rejected with the error that occurred while starting the watcher.
   */
  startServerWatcher() {
    return this._waitForWatcher(new ServerWatcher());
  }

  /**
   * Initializes {@link WatchTask#browserSyncInstance}.
   */
  initBrowserSync() {
    this.browserSyncInstance.init({
      proxy: {
        target: `${ProjectConfig.host}:${ProjectConfig.port.http}`,
        ws: true,
      },
      // logLevel: 'debug', FIXME: Use log level specified in cli options
      // logPrefix: '',
    });

    /* bs.logger.logOne = function(args, msg, level, unprefixed) {
      args = args.slice(2);

      if (this.config.useLevelPrefixes && !unprefixed) {
        msg = this.config.prefixes[level] + msg;
      }

      msg = this.compiler.compile(msg, unprefixed);

      args.unshift(msg);

      Logger[level](format(...args));

      this.resetTemps();

      return this;
    }; */
  }

  /**
   * Handles a file change.
   * @param {string} path The path of the file that changed.
   * @param {string} root The root of the file that changed.
   * @param {fs~Stats} stats The stats of the file that changed.
   * @return {Promise<boolean>} Resolved with `true` if the change triggered a push operation,
   * with `false` otherwise.
   */
  handleFileChange(path, root, stats) {
    return new Promise(resolve => {
      if (!this._pulling && AtviseFile.normalizeMtime(stats.mtime) > this._lastPull) {
        const nodePath = dirname(path);
        let nodeId = {};

        nodeId = NodeId.fromFilePath(nodePath);

        if (nodeId.toString() !== this._lastPulled) {
          const pushStream = new PushStream({
            nodesToPush: [nodeId],
            createNodes: false,
          });

          this._pushing = true;
          Logger.info('File change:', path, 'changed');

          pushStream
            .on('write-successful', file => {
              this._lastPushed = file.nodeId.toString();
            })
            .on('finish', () => {
              this._pushing = false;
              this.browserSyncInstance.reload();

              resolve(true);
            });
        }
      } else {
        resolve(false);
      }
    });
  }
  /**
   * Handles an atvise server change.
   * @param {ReadNodeItem} readNodeMappingItem The resultung rad node mapping
   * item of the modification.
   * @return {Promise<boolean>} Resolved with `true` if the change triggered a pull operation,
   * with `false` otherwise.
   */
  handleServerChange(readNodeMappingItem) {
    return new Promise(resolve => {
      if (!this._pushing) {
        const nodeId = readNodeMappingItem.nodeId.toString();

        if (nodeId !== this._lastPushed) {
          const readStream = createStream();
          this._pulling = true;

          Logger.info('Server change:', nodeId, 'changed');
          readStream.write(readNodeMappingItem);
          readStream.end();

          const pullStream = new PullStream({
            useInputStream: true,
            inputStream: readStream,
          });

          pullStream
            .on('end', () => {
              this._pulling = false;
              this._lastPull = AtviseFile.normalizeMtime(readNodeMappingItem.configObj.mtime);
              this.browserSyncInstance.reload();

              resolve(true);
            });
        } else {
          this._lastPushed = null;

          resolve(false);
        }
      } else {
        resolve(false);
      }
    });
  }

  /**
   * Starts the file and server watchers, initializes Browsersync and registers change event
   * handlers.
   * @return {Promise<undefined, Error>} Fulfilled once all watchers are set up and Browsersync was
   * initialized.
   */
  run() {
    return Promise.all([
      this.startFileWatcher(),
      this.startServerWatcher(),
    ])
      .then(([fileWatcher, serverWatcher]) => {
        this.browserSyncInstance.emitter.on('service:running', () => {
          Logger.info('Watching for changes...');
          Logger.info('Press Ctrl-C to exit');
        });

        fileWatcher.on('change', this.handleFileChange.bind(this));
        // Rename von Files, Anlegen von neuen Files usw hier hinzufgen
        serverWatcher.on('change', this.handleServerChange.bind(this));

        this.initBrowserSync();
      });
  }

}

/**
 * The gulp task invoced when running `atscm watch`.
 * @return {Promise<undefined, Error>} Fulfilled once all watchers are set up and Browsersync was
 * initialized.
 */
export default function watch() {
  return (new WatchTask()).run();
}

watch.description = 'Watch local files and atvise server nodes to trigger pull/push on change';

import { Buffer } from 'buffer';
import Logger from 'gulplog';
import XMLTransformer from '../lib/transform/XMLTransformer';

/**
 * Splits read atvise display XML nodes into their SVG and JavaScript sources,
 * alongside with a .json file containing the display's parameters.
 */
export default class DisplayTransformer extends XMLTransformer {

  /**
   * Returns true for all files containing atvise displays.
   * @param {AtviseFile} file The file to check.
   * @return {Boolean} `true` for all atvise display files.
   */
  shouldBeTransformed(file) {
    return file.isDisplay;
  }

  /**
   * Splits any read files containing atvise displays into their SVG and JavaScript sources,
   * alongside with a .json file containing the display's parameters.
   * @param {AtviseFile} file The display file to split.
   * @param {String} enc The encoding used.
   * @param {function(err: Error, file: AtviseFile)} callback Called with the error that occured
   * while transforming the display, or the file passed through.
   */
  transformFromDB(file, enc, callback) {
    this.decodeContents(file, (err, xmlObj) => {
      if (err) {
        Logger.error(`Display ${file.nodeId}: Error parsing display content.`,
          'Check if display content is broken');
        callback(null);
      } else if (xmlObj.children.length === 0 || xmlObj.children[0].name !== 'svg') {
        Logger.error(`Display ${file.nodeId}: Can not decode display. Missing 'svg' tag`);
        callback(null);
      } else {
        try {
          let scriptFileAdded = false;
          const config = { parameters: [], dependencies: [] };
          const displayContent = xmlObj.children[0].children;

          const scriptFile = DisplayTransformer.splitFile(file, '.js');
          const configFile = DisplayTransformer.splitFile(file, '.json');
          const svgFile = DisplayTransformer.splitFile(file, '.svg');

          // Filter for script tags in display
          const scripts = displayContent.filter((tag, index) => {
            if (tag.name === 'script') {
              delete displayContent[index];
              return true;
            }
            return false;
          });

          // Filter for metadata tags in display
          const metadata = xmlObj.find('*/metadata').children;

          // Extract JavaScript
          scripts.map((script) => {
            const attributes = script.attrs;

            if (attributes.src || attributes['xlink:href']) {
              config.dependencies.push(attributes.src || attributes['xlink:href']);
            } else if (scriptFileAdded) {
              Logger.warn(`Display ${file.nodeId}:`,
                'atscm only supports one inline script per display');
            } else {
              scriptFileAdded = true;
              scriptFile.contents = Buffer.from(script.toString());
            }

            return false;
          });


          // Extract display parameters
          if (metadata.length > 0) {
            const meta = metadata[0].children;
            const nonParameterTags = [];

            if (metadata.length > 1) {
              Logger.warn(`Display ${file.nodeId}:`,
                'atscm only supports one metadata tag per display');
              metadata.splice(1, metadata.length);
            }

            meta.forEach(tag => {
              if (tag.name === 'atv:parameter') {
                config.parameters.push(tag.attrs);
              } else {
                nonParameterTags.push(tag);
              }
            });

            // overwrite meta data tag items, deleting items directly in metadata
            // tag made serialize function ignore
            // the remaining entries
            metadata[0].children = nonParameterTags;
          }

          configFile.contents = Buffer.from(JSON.stringify(config, null, '  '));

          this.encodeContents(xmlObj, (encodeError, xmlString) => {
            if (encodeError) {
              Logger.error(`Display ${file.nodeId}: Could not encode svg file`);
            } else {
              svgFile.contents = Buffer.from(xmlString);
              this.push(svgFile);
              this.push(configFile);
              this.push(scriptFile);
            }

            callback(null);
          });
        } catch (e) {
          callback(e);
        }
      }
    });
  }

  /**
   * Creates a display from the collected files.
   * @param {Map<String, vinyl~File>} files The collected files, stored against their extension.
   * @param {vinyl~File} lastFile The last file read. *Used for error messages only*.
   * @param {function(err: ?Error, data: vinyl~File)} callback Called with the error that occured
   * while creating the display or the resulting file.
   */
  createCombinedFile(files, lastFile, callback) {
    const svgFile = files['.svg'];
    const configFile = files['.json'];
    const scriptFile = files['.js'];

    let config = {};
    let inlineScript = '';

    if (!svgFile) {
      callback(new Error(`No display SVG in ${lastFile.dirname}`));
      return;
    }

    if (configFile) {
      try {
        config = JSON.parse(configFile.contents.toString());
      } catch (e) {
        callback(new Error(`Error parsing JSON in ${configFile.relative}: ${e.message}`));
        return;
      }
    }

    if (scriptFile) {
      inlineScript = scriptFile.contents.toString();
    }

    this.decodeContents(svgFile, (err, xmlObj) => {
      if (err) {
        Logger.error(`Display ${svgFile.nodeId}: Error parsing display content.
          Message: ${err.message}`);
        callback(null);
      } else {
        try {
          const displayContent = xmlObj.children[0];
          const metadata = xmlObj.find('*/metadata');
          const parameters = config.parameters.reverse();
          const dependencies = config.dependencies.reverse();
          const display = DisplayTransformer.combineFiles(
            Object.keys(files).map(ext => files[ext]),
            '.xml'
          );

          // Insert parameters
          if (parameters && parameters.length > 0) {
            const meta = metadata.children[0].children;

            parameters.forEach(param => meta.unshift(this.createTag('atv:parameter',
              param, metadata)));
          }

          // Insert dependencies
          if (dependencies && dependencies.length > 0) {
            dependencies.forEach(dependency => displayContent.children
              .push(this.createTag('script', { 'xlink:href': dependency, type: 'text/ecmascript' },
                metadata)));
          }

          // Insert script
          if (scriptFile) {
            const script = this.createTag('script', { type: 'text/ecmascript' }, displayContent);

            script.append(this.createCData(inlineScript));
            displayContent.children.push(script);
          }

          this.encodeContents(xmlObj, (encodeError, xmlString) => {
            if (encodeError) {
              Logger.error(`Display ${svgFile.nodeId}: Could not encode svg file`);
              callback(null);
            } else {
              display.contents = Buffer.from(xmlString);
              callback(null, display);
            }
          });
        } catch (e) {
          callback(e);
        }
      }
    });
  }

}

import { src } from 'gulp';
import CombinedStream from 'combined-stream';
import ProjectConfig from '../config/ProjectConfig';
import Transformer, { TransformDirection } from '../lib/transform/Transformer';
import MappingTransformer from './Mapping';

/**
 * A transformer that transforms mapped file system files to {@link AtviseFiles}'s
 */
export default class FileToAtviseFileTransformer {

  /**
   * Creates a new FileToAtviseFileTransformer
   * @param {Object} options The options to use. See
   * {@link FileToAtviseFileTransformer#constructor} for available
   * options.
   * @param {Boolean} [options.applyTransformers] Defines whether transformer
   * should be applied or not
   * @param {NodeId[]} [options.nodesToTransform] The nodes to transform.
   */
  constructor(options = {}) {
    /**
     * Combined stream instance.
     * @type {CombinedStream}
     */
    const combinedSrcStream = CombinedStream.create();

    /**
     * Stream that creates atvise files.
     * @type {MappingTransformer}
     */
    const mappingStream = new MappingTransformer(
      { direction: TransformDirection.FromFilesystem }
    );

    /**
     * Stream containing all type definition files.
     * @type {NodeId[]}
     */
    const nodesToTransform = options.nodesToTransform || [];

    nodesToTransform.map(nodeId => combinedSrcStream
      .append(src(`./src/${nodeId.filePath}/**/*.*`)));

    if (options.applyTransformers !== undefined && options.applyTransformers === false) {
      return combinedSrcStream
        .pipe(mappingStream);
    }

    return Transformer.applyTransformers(
      combinedSrcStream
        .pipe(mappingStream),
      ProjectConfig.useTransformers,
      TransformDirection.FromFilesystem
    );
  }
}

import Logger from 'gulplog';
import Transformer from '../lib/transform/Transformer';
import AtviseFile from '../lib/mapping/AtviseFile';

/**
 * A Transformer that maps {@link ReadStream.ReadResult}s to {@link AtviseFile}s.
 */
export default class MappingTransformer extends Transformer {

  /**
   * Writes an {@link AtviseFile} for each given {@link MappingItem}.
   * @param {MappingItem} mappingItem The mapping item to create the file for.
   * @param {String} encoding The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error that occurred
   * while transforming the read result or the resulting file.
   */
  transformFromDB(mappingItem, encoding, callback) {
    try {
      const file = AtviseFile.fromMappingItem(mappingItem);

      callback(null, file);
    } catch (e) {
      Logger.error(`Unable to map ${mappingItem.nodeId.toString()}: ${e.message}`);
      Logger.debug(e);
      callback(null);
    }
  }

  /**
   * Writes an {@link AtviseFile} for each {@link vinyl~File} read.
   * @param {vinyl~File} file The raw file.
   * @param {String} encoding The encoding used.
   * @param {function(err: ?Error, data: ?AtviseFile)} callback Called with the error that occurred
   * while transforming the read result or the resulting file.
   */
  transformFromFilesystem(file, encoding, callback) {
    if (file.isDirectory()) {
      callback(null);
    } else {
      const atFile = new AtviseFile({
        cwd: file.cwd,
        base: file.base,
        path: file.path,
        contents: file.contents,
      });

      callback(null, atFile);
    }
  }

}

import Logger from 'gulplog';
import XMLTransformer from '../lib/transform/XMLTransformer';

/**
 * A transformer that splits atvise scripts and quick dynamics into a code file and a .json file
 * containing parameters and metadata.
 */
export default class ScriptTransformer extends XMLTransformer {

  /**
   * Returns `true` for all files containing script code or quick dynamics.
   * @param {AtviseFile} file The file to check.
   * @return {Boolean} `true` for all files containing script code or quick dynamics.
   */
  shouldBeTransformed(file) {
    return file.isScript || file.isQuickDynamic;
  }

  /**
   * Splits any read files containing scripts or quick dynamics into their JavaScript sources,
   * alongside with a .json file containing parameters and metadata.
   * @param {AtviseFile} file The script file to split.
   * @param {String} enc The encoding used.
   * @param {function(err: Error, file: AtviseFile)} callback Called with the error that occured
   * while transforming the script, or the file passed through.
   */
  transformFromDB(file, enc, callback) {
    this.decodeContents(file, (err, xmlObj) => {
      if (err) {
        Logger.error(`Display ${file.nodeId}: Error parsing script content.`,
          'Check if script content is empty or broken');
        callback(null);
      } else if (xmlObj.children.length === 0 || xmlObj.children[0].name !== 'script') {
        Logger.error(`Script ${file.nodeId}: Can not decode script. Missing 'script' tag`);
        callback(null);
      } else {
        const configFile = ScriptTransformer.splitFile(file, '.json');
        const scriptFile = ScriptTransformer.splitFile(file, '.js');

        const config = { parameters: [] };

        // Filter for metadata tags in script
        const metadata = xmlObj.find('*/metadata').children;

        // Filter for parameters in script
        const parameters = xmlObj.find('*/parameter').children;

        // Filter for code tags
        let code = xmlObj.find('*/code').children;


        // Extract metadata
        if (metadata.length > 0) {
          const meta = metadata[0];
          config.metadata = [];

          if (metadata.length > 1) {
            Logger.warn(`Script ${file.nodeId}: `,
              'atscm only supports one metadata tag per script');
          }

          meta.forEach(tag => config.metadata
            .push({ name: tag.name, attrs: tag.attrs, value: tag.text() }));
        }

        // Extract Parameters
        if (parameters.length > 0) {
          parameters.forEach(param => {
            const paramObj = param.attrs;

            if (paramObj.relative === 'true') {
              const targetName = param.find('*/*/*/TargetName');

              if (targetName.children.length > 0) {
                const nameSpaceIndex = targetName.find('*/NamespaceIndex').eq(0);
                const nodePath = targetName.find('*/Name').eq(0);

                // add relative path information
                paramObj.relPath = {
                  nameSpaceIndex: nameSpaceIndex.text(),
                  nodePath: nodePath.text(),
                };
              }
            }
            config.parameters.push(paramObj);
          });
        }


        if (code.length === 0) {
          Logger.warn(`Script ${file.nodeId}: No script content defined`);
        }

        // Extract JavaScript
        code = code.toString();

        // Write config and script file
        configFile.contents = Buffer.from(JSON.stringify(config, null, '  '));
        scriptFile.contents = Buffer.from(code);

        this.push(configFile);
        this.push(scriptFile);
        callback(null);
      }
    });
  }

  /**
   * Creates a script from the collected files.
   * @param {Map<String, AtviseFile>} files The collected files, stored against their extension.
   * @param {AtviseFile} lastFile The last file read. *Used for error messages only*.
   * @param {function(err: ?Error, data: vinyl~File)} callback Called with the error that occured
   * while creating the script or the resulting file.
   */
  createCombinedFile(files, lastFile, callback) {
    const configFile = files['.json'];
    const scriptFile = files['.js'];

    let config = {};
    const xmlObj = {};

    const script = this.createTag('script', {}, xmlObj);
    const metadata = this.createTag('metadata', {}, script);
    const code = this.createTag('code', {}, script);
    const combinedFile = ScriptTransformer.combineFiles(
      Object.keys(files).map(ext => files[ext]),
      '.xml'
    );

    if (configFile) {
      try {
        config = JSON.parse(configFile.contents.toString());
      } catch (e) {
        Logger.warn(`Script ${configFile.nodeId}: Error paring config file`);
        callback(null);
        return;
      }
    }

    // Add metadata to script
    if (config.metadata) {
      config.metadata.forEach(tag => metadata.append(
        this.createTag(tag.name, tag.attrs, metadata, tag.value)
      ));
      script.append(metadata);
    }

    // Add parameters to script
    if (config.parameters) {
      config.parameters.forEach(param => {
        let relPath;

        if (param.relative === 'true') {
          relPath = this.createRelPathTag(param.relPath);
          // eslint-disable-next-line no-param-reassign
          delete param.relPath;
        }

        script.append(this.createTag('parameter', param, script, relPath));
      });
    }

    // Add CData content to script
    if (scriptFile) {
      code.append(this.createCData(scriptFile.contents.toString()));
      script.append(code);
    }


    this.encodeContents(this.createNodeSet(script), (encodeErr, xmlString) => {
      if (encodeErr) {
        Logger.error(`Script ${combinedFile.nodeId}: Could not encode script file`);
        callback(null);
      } else {
        combinedFile.contents = Buffer.from(xmlString);

        callback(null, combinedFile);
      }
    });
  }

}

import ProjectConfig from '../config/ProjectConfig';
import Transformer, { TransformDirection } from '../lib/transform/Transformer';
import MappingTransformer from './Mapping';
import BrowseStream from '../lib/pull/BrowseStream';
import ReadStream from '../lib/pull/ReadStream';

/**
 * A transformer that transforms mapped file system files to {@link AtviseFiles}'s
 */
export default class UaNodeToAtviseFileTransformer {
  /**
   * Creates a new FileToAtviseFileTransformer
   * @param {Object} options The options to use. See
   * {@link FileToAtviseFileTransformer#constructor} for available options.
   * @param {NodeId[]} [options.nodesToTransform] The nodes to transform.
   * @param {Boolean} [options.useInputStream] Defines if the given input stream should be
   * used for mapping.
   * @param {Stream} [options.inputStream] The input stream to use.
   */
  constructor(options = {}) {
    /**
     * Stream containing all type definition files.
     * @type {NodeId[]}
     */
    const nodesToTransform = options.nodesToTransform || [];

    /**
     * Stream that creates atvise files.
     * @type {MappingTransformer}
     */
    const mappingStream = new MappingTransformer({ direction: TransformDirection.FromDB });

    /**
     * Defines if the given input stream should be used for mapping
     * @type {MappingTransformer}
     */
    const useInputStream = options.useInputStream || false;

    /**
     * Stream to use as input for mapping stream.
     * @type {Stream}
     */
    let inputStream = null;

    /**
     * Stream that reads atvise server nodes.
     * @type {ReadStream}
     */
    this.readStream = new ReadStream();


    if (useInputStream) {
      if (!options.inputStream) {
        throw new Error('UaNodeToAtviseFileTransformer#constructor: Input stream is missing');
      } else {
        inputStream = options.inputStream;
      }
    } else {
      inputStream = (new BrowseStream(nodesToTransform))
        .pipe(this.readStream);
    }

    /**
     * Stream that creates {AtviseFiles} from browses {node-opcua~ReferenceDescriptions}.
     * @type {Stream}
     */
    this.stream = Transformer.applyTransformers(
      inputStream.pipe(mappingStream),
      ProjectConfig.useTransformers,
      TransformDirection.FromDB
    );
  }
}

