  /** @test {AtivseFile#value} */
  describe('#value', function() {
    const val = new Buffer('test');

    before(() => {
      stub(AtviseFile, 'decodeValue').callsFake(() => true);
      stub(AtviseFile, 'encodeValue').callsFake(() => val);
