    it('should forward change events', function(done) {
      const watcher = new Watcher([resolveNodeId('ns=1;s=AGENT.DISPLAYS.Main')]);

      watcher.on('ready', () => {
        const event = {};

        watcher.on('change', e => {
          expect(e, 'to be', event);
          watcher.close();
          done();
        });

        watcher._subscribeStream.emit('change', event);
      });
