      watcher.on('error', err => {
        expect(err, 'to have message', 'Test');
        done();
      });

      watcher.on('ready', () => watcher._subscribeStream.emit('error', new Error('Test')));
    });
  });

  /** @test {Watcher#close} */
  describe('#close', function() {
    it('should forward errors', function(done) {
