/**
 * The path to the project's configuration file.
 * @type {string}
 */
export const path = process.env.ATSCM_CONFIG_PATH;

/**
 * The current project's configuration.
 * @type {Atviseproject}
 */
const Config = require(path).default;

/**
 * The current project's configuration, with overrides (through `ATSCM_PROJECT__` env vars) already
 * handled.
 */
export default class ProjectConfig extends Config {
  /**
   * Return the project configuration override for the given name if available.
   * @param {string} name The variable to return.
   * @type {string|undefined} The variables's value or `undefined`.
   */
  static _env(name) {
    return process.env[`ATSCM_PROJECT__${name}`];
  }

  /**
   * The atvise server's host. Can be overridden with the `ATSCM_PROJECT__HOST` env variable.
   * @type {string}
   */
  static get host() {
    return this._env('HOST') || super.host;
  }

  /**
   * The atvise server ports to use.
   * @type {Object}
   * @property {number} opc The OPC-UA port the atvise server runs on. Can be overridden with the
   * `ATSCM_PROJECT__PORT__OPC` env variable.
   * @property {number} http The HTTP port the atvise server can be reached at. Can be overridden
   * with the `ATSCM_PROJECT__PORT__HTTP` env variable.
   */
  static get port() {
    return {
      opc: parseInt(this._env('PORT__OPC'), 10) || super.port.opc,
      http: parseInt(this._env('PORT__HTTP'), 10) || super.port.http,
    };
  }

  /**
   * The login to use. Return false if no login is required (default).
   * @type {Object}
   * @property {string} username The username to log in with. Can be overridden with the
   * `ATSCM_PROJECT__LOGIN__USERNAME` env variable.
   * @property {string} password The password to log in with. Can be overridden with the
   * `ATSCM_PROJECT__LOGIN__PASSWORD` env variable.
   */
  static get login() {
    if (this._env('LOGIN__USERNAME') || this._env('LOGIN__PASSWORD')) {
      return {
        username: this._env('LOGIN__USERNAME') || super.login.username,
        password: this._env('LOGIN__PASSWORD') || super.login.password,
      };
    }

    return super.login;
  }

  /**
   * A regular expression matching all source nodes.
   * @type {RegExp};
   */
  static get sourceNodeRegExp() {
    if (!this._sourceNodeRegExp) {
      this._sourceNodesRegExp = new RegExp(
        `^(${this.nodes.map(({ value }) => `${value.replace(/\./g, '\\.')}`).join('|')})`
      );
    }

    return this._sourceNodesRegExp;
  }

  /**
   * A regular expression matching all ignored nodes.
   * @type {RegExp};
   */
  static get ignoredNodesRegExp() {
    if (!this._ignoredNodesRegExp) {
      this._ignoredNodesRegExp = new RegExp(
        `^(${this.ignoreNodes.map(({ value }) => `${value.replace(/\./g, '\\.')}`).join('|')})`
      );
    }

    return this._ignoredNodesRegExp;
  }

  /**
   * Returns `true` for all external (not in {@link Atviseproject.nodes} or ignored by
   * {@link Atviseproject.ignoreNodes}).
   * @param {string} id The node id to check.
   * @return {boolean} If the node is external.
   */
  static isExternal(id) {
    return !id.match(this.sourceNodeRegExp) || !!id.match(this.ignoredNodesRegExp);
  }

  /**
   * The connection timeout, in milliseconds. Can be overridden with the `ATSCM_PROJECT__TIMEOUT`
   * env variable.
   * @type {number}
   */
  static get timeout() {
    const env = this._env('TIMEOUT');
    return env ? parseInt(env, 10) : super.timeout;
  }
}

import { VariableIds } from 'node-opcua/lib/opcua_node_ids';
import { readJson } from 'fs-extra';
import { coerce, satisfies, gtr, minVersion } from 'semver';
import Logger from 'gulplog';
import prompts from 'prompts';
import { red, yellow } from 'chalk';
import { readNode } from '../api';
import { engines } from '../../package.json';
import { updateJson } from '../lib/helpers/fs';
import NodeId from '../lib/model/opcua/NodeId';
import { HookContext } from './hooks';

const atserverVersionNodeId = new NodeId(
  `ns=0;i=${VariableIds.Server_ServerStatus_BuildInfo_SoftwareVersion}`
);

export async function loadProjectRequirement(): Promise<string> {
  const packageManifest = await readJson('./package.json');

  return packageManifest.engines && packageManifest.engines.atserver;
}

export async function loadRemoteVersion(): Promise<string> {
  const raw = (await readNode(atserverVersionNodeId)).value;

  return coerce(raw).version;
}

export async function askForConfirmation({
  onAsk,
  ...options
}: {
  message: string;
  onAsk?: () => void;
}): Promise<boolean> {
  if (!process.stdin.isTTY) return false;

  if (onAsk) onAsk();

  return (await prompts({
    type: 'confirm',
    name: 'confirmed',
    ...options,
  })).confirmed;
}

export async function approveToContinue(
  { log, continueOnError }: HookContext,
  error: Error
): Promise<void> {
  if (continueOnError) {
    log.warn(red(error.message));
    log.warn(`Using --continue, skipping...`);
    return;
  }

  const shouldContinue = await askForConfirmation({
    onAsk: () => Logger.error(red(error.message)),
    message: 'Do you want to continue anyway?',
  });

  if (!shouldContinue) {
    throw error;
  }
}

export default async function checkAtserver(context: HookContext): Promise<void> {
  const { log } = context;

  log.debug('Checking atserver version');

  const atscmRequirement = engines.atserver;

  const [projectRequirement, remoteVersion] = await Promise.all([
    loadProjectRequirement(),
    loadRemoteVersion(),
  ]);

  if (!satisfies(remoteVersion, atscmRequirement)) {
    log.debug(`Version ${remoteVersion} does not satisfy requirement ${atscmRequirement}`);
    log.warn(
      yellow(
        `Your atvise server version (${remoteVersion}) is not supported, it may or may not work.`
      )
    );

    if (gtr(remoteVersion, atscmRequirement)) {
      log.info(
        `You're running a newer version of atvise server. Please run 'atscm update' to check for updates.`
      );
    } else {
      log.info(`Please upgrade to atserver ${minVersion(atscmRequirement)} or above.`);
    }
  }

  let updatePackage = false;
  if (!projectRequirement) {
    log.info(`Your package.json file doesn't specify an atserver version, adding it...`);

    updatePackage = true;
  } else if (!satisfies(remoteVersion, projectRequirement)) {
    await approveToContinue(
      context,
      new Error(
        `Your project is setup with atserver ${projectRequirement} but you're using ${remoteVersion}`
      )
    );

    updatePackage = await askForConfirmation({
      message: `Use atvise server ${remoteVersion} as new default?`,
    });
  } else {
    log.debug(`Running against atserver ${remoteVersion}`);
  }

  if (updatePackage) {
    await updateJson<{ engines?: { atserver?: string } }>('./package.json', current => {
      /* eslint-disable no-param-reassign */
      if (!current.engines) current.engines = {};
      current.engines.atserver = remoteVersion;
      /* eslint-enable no-param-reassign */

      return current;
    });
  }
}

import { satisfies as validVersion } from 'semver';
import { readNode } from '../api';
import { versionNode } from '../lib/server/scripts/version';
import { dependencies } from '../../package.json';
import { HookContext } from './hooks';

export default async function checkServerscripts({ log }: HookContext): Promise<void> {
  log.debug('Checking installed serverscripts');

  const required = dependencies['@atscm/server-scripts'];
  let version;

  try {
    version = (await readNode(versionNode)).value;

    const [release] = version.split('-beta');
    const valid = validVersion(release, required);

    if (!valid) throw new Error('Invalid version');

    log.debug(`Serverscripts ${version} installed (${required} required)`);
  } catch (error) {
    throw Object.assign(
      new Error(`Invalid server script version: ${version || 'not installed'} (${required} required)
- Please run 'atscm import' again to update`),
      { originalError: error }
    );
  }
}

/* eslint-disable import/prefer-default-export */

import Logger from 'gulplog';

export interface HookContext {
  continueOnError: boolean;
  log: typeof Logger;
}

export function setupContext({ log, continueOnError }: Partial<HookContext> = {}): HookContext {
  return {
    log: log || Logger,
    continueOnError:
      continueOnError === undefined ? process.env.CONTINUE_ON_FAILURE === 'true' : continueOnError,
  };
}

export { default as Atviseproject } from './lib/config/Atviseproject';
export { default as ProjectConfig } from './config/ProjectConfig';
export { default as NodeId } from './lib/model/opcua/NodeId';
export { default as Transformer, TransformDirection } from './lib/transform/Transformer';
export { default as PartialTransformer } from './lib/transform/PartialTransformer';
export { default as SplittingTransformer } from './lib/transform/SplittingTransformer';
export { default as DisplayTransformer } from './transform/DisplayTransformer';
export * from './transform/ScriptTransformer';
export { default as NewlinesTransformer } from './transform/Newlines';

import { join } from 'path';
import { src, dest } from 'gulp';
import handlebars from 'gulp-compile-handlebars';
import helpers from 'handlebars-helpers';
import streamToPromise from 'stream-to-promise';
import deps from '../../res/init/templates/dependencies.json';

/**
 * The action run when running "atscm init".
 */
export default class InitTask {
  /**
   * Returns the globs of the processed files for the given config lanugage.
   * @param {string} langId The configuration language used.
   * @return {string[]} Globs of the files to handle.
   */
  static filesToHandle(langId) {
    return [
      './general/**/*',
      './general/**/.*',
      `./lang/${langId}/**/*.*`,
      `./lang/${langId}/**/.*`,
    ].map(p => join(__dirname, '../../res/init/templates', p));
  }

  /**
   * Runs the task with the given options.
   * @param {Object} options The options to use.
   * @return {Promise<{ install: string[] }, Error>} Resolved with information on further actions
   * to run or rejected if the task failed.
   */
  static run(options) {
    const langId = options.configLang;

    const install = deps.lang[langId];

    const stream = src(this.filesToHandle(langId))
      .pipe(
        handlebars(options, {
          helpers: helpers(),
        })
      )
      .pipe(dest('./'));

    return streamToPromise(stream).then(() => ({ install }));
  }
}

import { basename } from 'path';
import InitOption from '../lib/init/Option';
import Atviseproject from '../lib/config/Atviseproject';
import Validator from './OptionsValidator';

/**
 * A map providing dirnames for config langs
 * @type {Map<String, String>}
 */
export const ConfigLangs = {
  'ES2015 JavaScript': 'es2015',
  'Regular JavaScript': 'es5',
  TypeScript: 'ts',
};

/**
 * Suggests a project name based on the name of the directory 'atscm init' was called in.
 * @param {string} dirname The current working directory's name.
 * @return {string} The suggested name.
 */
function projectName(dirname) {
  if (dirname === 'atscm') {
    return 'atscm-project';
  }

  return dirname;
}

/**
 * Options available for the "atscm init" command.
 * @type {Object}
 * @property {InitOption<input>} name The new project's name.
 * @property {InitOption<input>} description The new project's description.
 * @property {InitOption<input>} author The new project's author.
 * @property {InitOption<input>} atviseHost The new project's atvise server host.
 * @property {InitOption<input>} atvisePortOpc The new project's atvise server OPC port.
 * @property {InitOption<input>} atvisePortHttp The new project's atvise server HTTP port.
 * @property {InitOption<confirm>} useLogin If the new porject's atvise server requires login.
 * @property {InitOption<input>} atviseUsername The new project's atvise server login name.
 * @property {InitOption<input>} atvisePassword The new project's atvise server login password.
 * @property {InitOption<list>} configLang The language to use for the new project's configuration.
 */
export const InitOptions = {
  name: new InitOption({
    message: 'Project name',
    default: projectName(basename(process.cwd())),
    validate: Validator.name,
  }),
  description: new InitOption('Project description'),
  author: new InitOption('Project author'),
  atviseHost: new InitOption('Atvise server host', Atviseproject.host),
  atvisePortOpc: new InitOption('Atvise OPC port', Atviseproject.port.opc),
  atvisePortHttp: new InitOption('Atvise HTTP port', Atviseproject.port.http),
  useLogin: new InitOption({
    message: 'Does your atvise server require login',
    type: 'confirm',
    default: false,
  }),
  atviseUser: new InitOption({
    message: ' - Username',
    when: answers => answers.useLogin,
  }),
  atvisePassword: new InitOption({
    message: ' - Password',
    when: answers => answers.useLogin,
  }),
  configLang: new InitOption({
    type: 'list',
    message: 'Configuration language to use',
    choices: Object.keys(ConfigLangs).map(name => ({ name, value: ConfigLangs[name] })),
  }),
};

/**
 * {@link InitOptions}, exported as an array. Required to run {@link Inquirer}.
 * @type {InitOption[]}
 */
const InitOptionsAsArray = Object.keys(InitOptions).map(name =>
  Object.assign({ name }, InitOptions[name])
);

export default InitOptionsAsArray;

import validatePackageName from 'validate-npm-package-name';

/**
 * A static class containing validators for the options used when running "atscm init".
 */
export default class InitOptionsValidator {
  /**
   * Validates a project name to be a valid npm package name.
   * @param {string} value The name to validate.
   * @return {boolean|string} Returns true if `value` is a valid npm package name, or an error
   * message otherwise.
   */
  static name(value) {
    const result = validatePackageName(value);

    if (result.errors) {
      return result.errors[0];
    }

    // First letter must be a letter
    if (value.match(/^@?[a-z]+/i) === null) {
      return 'name must start with a letter';
    }

    if (value === 'atscm') {
      return "'atscm' is not allowed";
    }

    return result.validForNewPackages ? true : result.warnings[0];
  }
}

import { DataType, VariantArrayType, Variant } from 'node-opcua/lib/datamodel/variant';
import { resolveNodeId } from 'node-opcua/lib/datamodel/nodeid';
import { LocalizedText } from 'node-opcua/lib/datamodel/localized_text';
import { StatusCodes } from 'node-opcua/lib/datamodel/opcua_status_code';
import { QualifiedName } from 'node-opcua/lib/datamodel/qualified_name';
import { DataValue } from 'node-opcua/lib/datamodel/datavalue';
import { ExpandedNodeId } from 'node-opcua/lib/datamodel/expanded_nodeid';
import { DiagnosticInfo } from 'node-opcua/lib/datamodel/diagnostic_info';
import { pick } from './helpers/Object';

/**
 * Function that returns the passed argument as is.
 * @param {*} b The input argument.
 * @return {*} The value passed.
 */
const asIs = b => b;

/**
 * Maps a single property of an object using the the mapper defined in *map* for the given
 * *dataType*.
 * @param {Map<node-opcua~DataType, function} map The mappings to use.
 * @param {Object} obj The object to process.
 * @param {string} key Name of the property to map.
 * @param {node-opcua~DataType} dataType The data type to map the property to.
 */
const mapPropertyAs = (map, obj, key, dataType) => {
  if (obj[key]) {
    return Object.assign(obj, {
      [key]: map[dataType](obj[key]),
    });
  }

  return obj;
};

/**
 * A set of functions that return raw values from {@link node-opcua~Variant} for specific
 * {@link node-opcua~DataType}s.
 * @type {Map<node-opcua~DataType, function(value: any): any>}
 */
const toRawValue = {
  [DataType.Null]: () => null,
  [DataType.StatusCode]: ({ name }) => name,
  [DataType.QualifiedName]: ({ namespaceIndex, name }) => ({ namespaceIndex, name }),
  [DataType.LocalizedText]: ({ text, locale }) => ({ text: text || null, locale }),
  [DataType.DataValue]: value => {
    const options = pick(value, [
      'value',
      'statusCode',
      'sourceTimestamp',
      'sourcePicoseconds',
      'serverTimestamp',
      'serverPicoseconds',
    ]);

    mapPropertyAs(toRawValue, options, 'value', DataType.Variant);
    mapPropertyAs(toRawValue, options, 'statusCode', DataType.StatusCode);
    // NOTE: server- and sourceTimstamps get mapped as dates

    return options;
  },
  [DataType.Variant]: ({ dataType, arrayType, value, dimensions }) => ({
    dataType,
    arrayType,
    // eslint-disable-next-line no-use-before-define
    value: getRawValue({ value, dataType, arrayType }),
    dimensions,
  }),
  [DataType.DiagnosticInfo]: info => {
    const options = pick(info, [
      'namespaceUri',
      'symbolicId',
      'locale',
      'localizedText',
      'additionalInfo',
      'innerStatusCode',
      'innerDiagnosticInfo',
    ]);

    mapPropertyAs(toRawValue, options, 'innerStatusCode', DataType.StatusCode);
    mapPropertyAs(toRawValue, options, 'innerDiagnosticInfo', DataType.DiagnosticInfo);

    return options;
  },
};

/**
 * Returns the raw value for a {@link node-opcua~Variant}.
 * @param {node-opcua~Variant} variant The variant to convert.
 */
function getRawValue({ value, dataType, arrayType }) {
  if (arrayType !== VariantArrayType.Scalar) {
    return (Array.isArray(value) ? value : Array.from(value)).map(val =>
      getRawValue({
        value: val,
        dataType,
        arrayType: VariantArrayType[arrayType.value - 1],
      })
    );
  }

  return (toRawValue[dataType] || asIs)(value);
}

/**
 * Returns a buffer containing a {@link node-opcua~Variant}s encoded value.
 * @param {node-opcua~Variant} variant The variant to encode.
 * @return {Buffer} A buffer containing the encoded value.
 */
export function encodeVariant({ value, dataType, arrayType }) {
  if (value === null) {
    return Buffer.from([]);
  }

  const rawValue = getRawValue({ value, dataType, arrayType });

  if (rawValue instanceof Buffer) {
    return rawValue;
  }

  const stringify = a => (a.toJSON ? a.toJSON() : JSON.stringify(a, null, '  '));

  const stringified =
    typeof rawValue === 'object' ? stringify(rawValue) : rawValue.toString().trim();

  return Buffer.from(stringified);
}

/**
 * Decodes a buffer to a string.
 * @param {Buffer} b The buffer to decode from.
 * @return {string} The buffer's string representation.
 */
const decodeAsString = b => b.toString().trim();

/**
 * Decodes a buffer to an integer value.
 * @param {Buffer} b The buffer to decode from.
 * @return {number} The decoded integer.
 */
const decodeAsInt = b => parseInt(decodeAsString(b), 10);

/**
 * Decodes a buffer to a float value.
 * @param {Buffer} b The buffer to decode from.
 * @return {number} The decoded float.
 */
const decodeAsFloat = b => parseFloat(decodeAsString(b));

/**
 * Decodes a buffer using JSON.
 * @param {Buffer} b The buffer to decode from.
 * @return {*} The decoded value, most likely an Object.
 */
const decodeAsJson = b => JSON.parse(b.toString());

/**
 * Mapping functions that return raw values for a stored value of the given type.
 * @type {Map<node-opcua~DataType, function>}
 */
const decodeRawValue = {
  [DataType.Null]: () => null,
  [DataType.Boolean]: b => decodeAsString(b) === 'true',
  [DataType.SByte]: decodeAsInt,
  [DataType.Byte]: decodeAsInt,
  [DataType.Int16]: decodeAsInt,
  [DataType.UInt16]: decodeAsInt,
  [DataType.Int32]: decodeAsInt,
  [DataType.UInt32]: decodeAsInt,
  [DataType.Int64]: decodeAsJson,
  [DataType.UInt64]: decodeAsJson,
  [DataType.Float]: decodeAsFloat,
  [DataType.Double]: decodeAsFloat,
  [DataType.String]: decodeAsString,
  [DataType.DateTime]: decodeAsString,
  [DataType.Guid]: decodeAsString,
  // ByteString maps to Buffer
  [DataType.XmlElement]: decodeAsString,
  [DataType.NodeId]: decodeAsString,
  [DataType.ExpandedNodeId]: decodeAsString,
  [DataType.StatusCode]: decodeAsString,
  [DataType.QualifiedName]: decodeAsJson,
  [DataType.LocalizedText]: decodeAsJson,
  // FIXME: Add ExtensionObject
  [DataType.DataValue]: decodeAsJson,
  [DataType.Variant]: decodeAsJson,
  [DataType.DiagnosticInfo]: decodeAsJson,
};

/**
 * Mapping functions that return OPC-UA node values for raw values.
 * @type {Map<node-opcua~DataType, function>}
 */
const toNodeValue = {
  [DataType.DateTime]: s => new Date(s),
  [DataType.ByteString]: b => {
    if (b instanceof Buffer) {
      return b;
    }

    return Buffer.from(b.data, 'binary');
  },
  [DataType.NodeId]: s => resolveNodeId(s),

  // Jep, node-opcua does not provide a resolve function for expanded nodeids
  [DataType.ExpandedNodeId]: s => {
    const nodeId = resolveNodeId(s);
    const [value, ...defs] = nodeId.value.split(';');

    const { identifierType, namespace, namespaceUri, serverIndex } = defs.reduce((opts, def) => {
      const match = def.match(/^([^:]+):(.*)/);
      if (!match) {
        return opts;
      }

      let [key, val] = match.slice(1); // eslint-disable-line prefer-const

      if (key === 'serverIndex') {
        val = parseInt(val, 10);
      }

      return Object.assign(opts, { [key]: val });
    }, Object.assign({}, nodeId));

    return new ExpandedNodeId(identifierType, value, namespace, namespaceUri, serverIndex);
  },

  [DataType.StatusCode]: name => StatusCodes[name],
  [DataType.QualifiedName]: options => new QualifiedName(options),
  [DataType.LocalizedText]: options => new LocalizedText(options),
  [DataType.DataValue]: options => {
    const opts = options;

    mapPropertyAs(toNodeValue, opts, 'value', DataType.Variant);
    mapPropertyAs(toNodeValue, opts, 'statusCode', DataType.StatusCode);
    mapPropertyAs(toNodeValue, opts, 'sourceTimestamp', DataType.DateTime);
    mapPropertyAs(toNodeValue, opts, 'serverTimestamp', DataType.DateTime);

    return new DataValue(opts);
  },
  [DataType.Variant]: ({ dataType, arrayType, value, dimensions }) =>
    new Variant({
      dataType,
      arrayType: VariantArrayType[arrayType],
      value,
      dimensions,
    }),
  [DataType.DiagnosticInfo]: options => {
    const opts = options;

    mapPropertyAs(toNodeValue, opts, 'innerStatusCode', DataType.StatusCode);
    mapPropertyAs(toNodeValue, opts, 'innerDiagnosticInfo', DataType.DiagnosticInfo);

    return new DiagnosticInfo(opts);
  },
};

/**
 * Returns a node's OPC-UA value based on it's raw value and type.
 * @param {*} rawValue A node's raw value.
 * @param {node-opcua~DataType} dataType A node's data type.
 * @param {node-opcua~VariantArrayType} arrayType A node's array type.
 */
const getNodeValue = (rawValue, dataType, arrayType) => {
  if (arrayType.value !== VariantArrayType.Scalar.value) {
    if (!Array.isArray(rawValue)) {
      throw new Error('Value is not an array');
    }

    return rawValue.map(raw => getNodeValue(raw, dataType, VariantArrayType[arrayType.value - 1]));
  }

  return (toNodeValue[dataType] || asIs)(rawValue);
};

/**
 * Returns a {@link node-opcua~Variant} from a Buffer with the given *dataType* and *arrayType*.
 * @param {Buffer} buffer The buffer to decode from.
 * @param {Object} options The options to use.
 * @param {node-opcua~DataType} options.dataType The data type to decode to.
 * @param {node-opcua~VariantArrayType} options.arrayType The array type to decode to.
 */
export function decodeVariant(buffer, { dataType, arrayType }) {
  if (buffer === null || buffer.length === 0) {
    return null;
  }

  if (dataType === DataType.ByteString && arrayType === VariantArrayType.Scalar) {
    return buffer;
  }

  const rawValue =
    arrayType === VariantArrayType.Scalar
      ? (decodeRawValue[dataType] || asIs)(buffer)
      : JSON.parse(buffer.toString());

  return getNodeValue(rawValue, dataType, arrayType);
}

