import Logger from 'gulplog';
import ProjectConfig from '../../config/ProjectConfig';
import QueueStream from './QueueStream';

/**
 * A mixin that transforms a regular stream into a {@link WaitingStream}, which makes it wait for a
 * node's dependencies to be transformed.
 * @param {QueueStream} Stream The node stream to inherit from.
 * @return {Class<WaitingStream>} The resulting stream class.
 * @example <caption>Basic usage</caption>
 * import QueueStream from 'atscm/src/lib/server/QueueStream';
 * import { waitForDependencies } from 'atscm/src/lib/server/WaitingStream';
 *
 * class MyWaitingStream extends waitForDependencies(QueueStream) {
 *
 *   dependenciesFor(file) {
 *     return [
 *       // An array of node ids
 *     ];
 *   }
 *
 * }
 */
export const waitForDependencies = Stream =>
  class Waiting extends Stream {
    /**
     * Creates a new WaitingStream.
     * @param {Object} options Options passed to the underlying {@link Stream} subclass.
     */
    constructor(options = {}) {
      super(options);

      this._dependencies = {};
      this._waitingFor = {};
      this._finishedProcessing = {};

      this.on('processed-chunk', file => {
        const key = file.nodeId.toString();
        const dependents = this._waitingFor[key];
        delete this._waitingFor[key];
        this._finishedProcessing[key] = true;

        if (dependents) {
          dependents.forEach(d => {
            const k = d.nodeId.toString();
            this._dependencies[k] -= 1;

            if (this._dependencies[k] === 0) {
              super._enqueueChunk(d);
              delete this._dependencies[k];
            }
          });
        }

        this.emit('finished-chunk');
      });
    }

    /**
     * **Must be implemented by all subclasses:** Returns the dependencies for a given file.
     * @typedef {function(file: AtviseFile): NodeId[]} WWaiting#dependenciesFor
     * @param {AtviseFile} file The file to get the dependencies for.
     * @return {NodeId[]} The file's dependencies.
     * @abstract
     */
    // eslint-disable-next-line no-unused-vars
    dependenciesFor(file) {
      throw new Error('#dependenciesFor must be implemented by all subclasses');
    }

    /**
     * Enqueues a file after it's dependencies.
     * @param {AtviseFile} file The file to process.
     */
    _enqueueChunk(file) {
      const dependencies = this.dependenciesFor(file)
        .filter(id => {
          if (id.namespace === 0 || !id.value || ProjectConfig.nodes.includes(id)) {
            return false;
          }

          return (
            !id.value.match(/^(Object|Variable)Types$/) &&
            !id.value.match(/^(Object|Variable)Types\.ATVISE/) &&
            !id.value.match(/^SYSTEM\.LIBRARY\.ATVISE/)
          );
        })
        .map(id => id.toString());

      if (dependencies && dependencies.length) {
        const needToWait = dependencies.reduce((wait, dependency) => {
          if (!this._finishedProcessing[dependency]) {
            const name = file.nodeId.toString();

            this._waitingFor[dependency] = (this._waitingFor[dependency] || []).concat(file);
            this._dependencies[name] = (this._dependencies[name] || 0) + 1;

            return true;
          }
          return wait;
        }, false);

        if (needToWait) {
          return;
        }
      }

      super._enqueueChunk(file);
    }

    /**
     * Delays the streams end until all chunks have been processed.
     * @param {function(err: Error)} callback Called once all chunks have been processed.
     */
    _flush(callback) {
      const checkProcessing = () => {
        if (this._processing || this.hasPending) {
          this.once('finished-chunk', () => checkProcessing());
        } else if (Object.keys(this._waitingFor).length > 0) {
          const first = Object.keys(this._waitingFor)[0];

          Logger.debug(`Missing dependency. Trying to process dependents of ${first}`);

          this.once('finished-chunk', () => checkProcessing());
          this.emit('processed-chunk', { nodeId: first });
        } else {
          super._flush(callback);
        }
      };

      checkProcessing();
    }
  };

/**
 * A {@link QueueStream} that waits for a file's dependencies to be processed before the file is
 * processed itself.
 * @abstract
 */
export default class WaitingStream extends waitForDependencies(QueueStream) {}

/* eslint-plugin-jsdoc does not recognize the "emits" tag */
/* eslint-disable jsdoc/check-tag-names */

/* Needed as long as https://github.com/gajus/eslint-plugin-jsdoc/issues/56 is open */
/* eslint-disable jsdoc/check-param-names */

import Emitter from 'events';
import { ClientSubscription } from 'node-opcua/lib/client/client_subscription';
import { AttributeIds } from 'node-opcua/lib/services/read_service';
import { NodeClass } from 'node-opcua/lib/datamodel/nodeclass';
import Logger from 'gulplog';
import ProjectConfig from '../../config/ProjectConfig';
import { reportProgress } from '../helpers/log';
import Session from './Session';
import NodeBrowser from './NodeBrowser';

/**
 * Watches the given nodes for value changes.
 * @emit {ReadStream.ReadResult} Emits `change` events when a watched node changes.
 */
export default class Watcher extends Emitter {
  /**
   * Creates a new Watcher with the given nodes.
   * @param {NodeId[]} nodes The nodes to watch (recursively).
   */
  constructor(nodes = ProjectConfig.nodesToWatch) {
    super();

    /**
     * The browser used to subscribe to server nodes.
     * @type {NodeBrowser}
     */
    this._nodeBrowser = new NodeBrowser({
      handleNode: this._subscribe.bind(this),
    });

    reportProgress(this._nodeBrowser.browse(nodes), {
      getter: () => this._nodeBrowser._pushed,
      formatter: count => `Subscribed to ${count} nodes`,
    })
      .then(() => this.emit('ready'))
      .catch(err => this.emit('error', err));

    /**
     * Resolved once the server subscription is set up.
     * @type {Promise<any>}
     */
    this.subscriptionStarted = this._setupSubscription().catch(err => this.emit('error', err));
  }

  /**
   * Initializes a server subscription.
   * @return {Promise<node-opcua~ClientSubscription>} A setup subscription.
   */
  _setupSubscription() {
    return Session.create().then(
      session =>
        new Promise((resolve, reject) => {
          /** The current session, if connected @type {Session} */
          this._session = session;

          const subscription = new ClientSubscription(session, {
            requestedPublishingInterval: 100,
            requestedLifetimeCount: 1000,
            requestedMaxKeepAliveCount: 12,
            maxNotificationsPerPublish: 10,
            publishingEnabled: true,
            priority: 10,
          });

          subscription.on('started', () => resolve(subscription));
          subscription.on('failure', err => reject(err));
        })
    );
  }

  /**
   * Subscribes to value changes of a single node.
   * @param {BrowsedNode} node A browsed node.
   */
  async _subscribe(node) {
    if (node.nodeClass !== NodeClass.Variable) {
      return undefined;
    }
    const subscription = await this.subscriptionStarted;

    const nodeId = node.id;

    const item = subscription.monitor(
      {
        nodeId,
        attributeId: AttributeIds.Value,
      },
      {
        clientHandle: 13,
        samplingInterval: 250,
        queueSize: 123,
        discardOldest: true,
      }
    );

    return new Promise((resolve, reject) => {
      // Sometimes the changed event is not emitted...
      // Fixes #202
      const timeout = setTimeout(() => {
        Logger.debug(`Error monitoring '${nodeId.value}': Did not receive initial value. Retry...`);

        try {
          item.terminate();
        } catch (e) {
          Logger.warn('Failed to terminate subscription', e);
        }

        return this._subscribe(node).then(resolve, reject);
      }, 1000);

      item.once('changed', () => {
        clearTimeout(timeout);
        item.on(
          'changed',
          this._handleChange.bind(this, {
            nodeId,
          })
        );

        resolve();
      });
      item.on('err', err => {
        clearTimeout(timeout);
        reject(err instanceof Error ? err : new Error(err));
      });
    }).catch(err => {
      throw Object.assign(err, { node });
    });
  }

  /**
   * Called once a change has been detected and emits a 'change' or 'delete' event.
   * @param {Object} node The node that changed.
   * @param {?node-opcua~Variant} dataValue The current value.
   */
  _handleChange({ nodeId }, dataValue) {
    this.emit(dataValue.value ? 'change' : 'delete', {
      // nodeClass,
      nodeId,
      value: dataValue.value,
      mtime: dataValue.serverTimestamp,
    });
  }

  /**
   * Ends monitoring nodes.
   */
  close() {
    if (this._session) {
      Session.close(this._session).catch(err => this.emit('error', err));
    }
  }
}

/* Needed as long as https://github.com/gajus/eslint-plugin-jsdoc/issues/56 is open */
/* eslint-disable jsdoc/check-param-names */

import Logger from 'gulplog';
import { StatusCodes } from 'node-opcua/lib/datamodel/opcua_status_code';
import { NodeClass } from 'node-opcua/lib/datamodel/nodeclass';
import WaitingStream from './WaitingStream';

// FIXME: Extend QueueStream directly

/**
 * A stream that writes all read {@link AtviseFile}s to their corresponding nodes on atvise server.
 * The underlying {@link TreeStream} ensures the nodes are processed in an order that respects the
 * parent-child relations between nodes. Nodes are created (if needed) before their children are
 * processed.
 */
export default class WriteStream extends WaitingStream {
  /**
   * Creates a new write stream with the given {@link CreateNodeStream} and
   * {@link AddReferencesStream}. Implementer have to ensure this create stream is actually piped.
   * @param {CreateNodeStream} createStream The stream that handles node creations.
   * @param {AddReferencesStream} addReferencesStream The stream that adds missing node references.
   * @param {Object} options The options passed to the underlying {@link TreeStream}.
   */
  constructor(createStream, addReferencesStream, options) {
    super(options);

    /**
     * If a node has to be created first, it's callback is added to this map.
     * @type {Map<String, function(err: Error)}
     */
    this._createCallbacks = {};

    createStream.on('processed-chunk', ({ nodeId }) => {
      const key = nodeId.toString();

      if (this._createCallbacks[key]) {
        this._createCallbacks[key](null);
      }
    });

    /**
     * The stream responsible for adding additional references.
     * @type {AddReferencesStream}
     */
    this._addReferencesStream = addReferencesStream;
  }

  /**
   * The error message to use when writing a file fails.
   * @param {AtviseFile} file The file being processed.
   * @return {string} The error message to use.
   */
  processErrorMessage(file) {
    return `Error writing ${file.nodeId}`;
  }

  /**
   * Pushes a node to the piped create stream and waits for the node to be created.
   * @param {AtviseFile} file The file create the node for.
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  _createNode(file, handleErrors) {
    this._createCallbacks[file.nodeId.toString()] = err => {
      handleErrors(err, StatusCodes.Good, done => done());
    };

    this.push(file);
  }

  /**
   * Returns a files parent node and type definition.
   * @param {AtviseFile} file The file to check.
   * @return {NodeId[]} The files dependencies.
   */
  dependenciesFor() {
    return [];
  }

  /**
   * Writes an {@link AtviseFile} to it's corresponding node on atvise server.
   * @param {AtviseFile} file The file to write.
   * @param {function(err: Error, statusCode: node-opcua~StatusCodes, onSuccess: function)}
   * handleErrors The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  processChunk(file, handleErrors) {
    if (file.nodeClass.value !== NodeClass.Variable.value) {
      // Non-variable nodes are just pushed
      this._createNode(file, handleErrors);
      return;
    }

    try {
      this.session.writeSingleNode(
        `ns=1;s=${file.nodeId}`,
        file.variantValue,
        (err, statusCode) => {
          if (
            statusCode === StatusCodes.BadUserAccessDenied ||
            statusCode === StatusCodes.BadNotWritable
          ) {
            Logger.warn(`Error writing node ${file.nodeId}
  - Make sure it is not opened in atvise builder
  - Make sure the corresponding datasource is connected`);
            handleErrors(err, StatusCodes.Good, done => done());
          } else if (statusCode === StatusCodes.BadNodeIdUnknown) {
            Logger.debug(`Node ${file.nodeId} does not exist: Attempting to create it...`);

            this._createNode(file, handleErrors);
          } else {
            handleErrors(err, statusCode, done => {
              // Push to add references stream
              this._addReferencesStream.push(file);

              done();
            });
          }
        }
      );
    } catch (e) {
      handleErrors(e);
    }
  }
}

import { StatusCodes } from 'node-opcua/lib/datamodel/opcua_status_code';
import QueueStream from '../QueueStream';

/**
 * A stream that calls an OPC-UA method for all input files.
 * @abstract
 */
export default class CallMethodStream extends QueueStream {
  /**
   * **Must be implemented in all subclasses:** The {@link NodeId} of the method to call.
   * @type {NodeId} The method's id.
   */
  get methodId() {
    throw new Error('Must be implemented in all subclasses');
  }

  /**
   * The {@link NodeId} of the object from which the method should get called. Defaults to the value
   * of {@link NodeId#parent} of {@link CallMethodStream#methodId}.
   * @type {NodeId} The call-object's id.
   */
  get methodBaseId() {
    return this.methodId.parent;
  }

  /**
   * The input arguments the method should be called with for a file. Needs to be overridden by
   * subclasses in most cases. Returning `null` indicates no method call is needed.
   * @param {vinyl~File} file The file beeing processed.
   * @return {?node-opcua~Variant[]} The resulting input arguments.
   */
  // eslint-disable-next-line no-unused-vars
  inputArguments(file) {
    return [];
  }

  /**
   * Creates a call method request object for a file.
   * @param {vinyl~File} file The file beeing processed.
   * @return {?node-opcua~CallMethodRequest} The resulting call request.
   */
  callRequest(file) {
    const args = this.inputArguments(file);

    if (args === null) {
      return null;
    }

    return {
      objectId: this.methodBaseId,
      methodId: this.methodId,
      inputArguments: args,
    };
  }

  /**
   * **Must be implemented by all subclasses:** If the method call returns a status code of
   * *{@link node-opcua~StatusCodes}.Good*, this method decides if the output matches the expected
   * results.
   * @param {vinyl~File} file The file beeing processed.
   * @param {node-opcua~Variant[]} outputArgs The output arguments.
   * @param {function(err: Error)} callback Call this method with an error to indicate the method
   * call didn't work as expected.
   */
  // eslint-disable-next-line no-unused-vars
  handleOutputArguments(file, outputArgs, callback) {
    throw new Error('Must be implemented in all subclasses');
  }

  /**
   * Returns an error message specifically for the given file.
   * @param {vinyl~File} file The file to generate the error message for.
   * @return {string} The specific error message.
   */
  processErrorMessage(file) {
    return `Error running ${this.methodId.toString()} for ${file.relative}`;
  }

  /**
   * Performs an opcua method call for the given file.
   * @param {vinyl~File} file The file being processed.
   * @param {function(err: Error, status: node-opcua~StatusCodes, success: function)} handleErrors
   * The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  processChunk(file, handleErrors) {
    try {
      const request = this.callRequest(file);

      if (!request) {
        handleErrors(null, StatusCodes.Good, done => done());
        return;
      }

      this.session.call([request], (err, [result] = []) => {
        if (err) {
          handleErrors(err);
        } else if (result.statusCode.value !== StatusCodes.Good.value) {
          handleErrors(err, result.statusCode, done => done());
        } else {
          this.handleOutputArguments(file, result.outputArguments, outputError => {
            handleErrors(outputError, StatusCodes.Good, done => done());
          });
        }
      });
    } catch (e) {
      handleErrors(e);
    }
  }
}

import { DataType, VariantArrayType } from 'node-opcua/lib/datamodel/variant';
import { StatusCodes } from 'node-opcua/lib/datamodel/opcua_status_code';
import NodeId from '../../model/opcua/NodeId';
import CallMethodStream from './CallMethodStream';

/**
 * A stream that calls atvise server scripts for all passed nodes.
 * @abstract
 */
export default class CallScriptStream extends CallMethodStream {
  /**
   * The id of the *callScript* method.
   * @type {NodeId}
   */
  get methodId() {
    return new NodeId(NodeId.NodeIdType.STRING, 'AGENT.SCRIPT.METHODS.callScript', 1);
  }

  /**
   * **Must be implemented by all subclasses:** The id of the script to call.
   * @type {NodeId}
   * @abstract
   */
  get scriptId() {
    throw new Error('Must be implemented by all subclasses');
  }

  /**
   * Id of the script's base object.
   * @type {NodeId}
   */
  get scriptBaseId() {
    return this.scriptId.parent;
  }

  /**
   * Returns the parameters to call the script with for the given file.
   * @param {AtviseFile} file The processed file.
   * @return {Object} The parameters passed to the script.
   */
  // eslint-disable-next-line no-unused-vars
  scriptParameters(file) {
    return {};
  }

  /**
   * Creates the raw method input arguments for the given file.
   * @param {AtviseFile} file The processed file.
   * @return {?node-opcua~Variant[]} Input arguments for the *callScript* method.
   */
  inputArguments(file) {
    const params = this.scriptParameters(file);

    if (params === null) {
      return null;
    }

    const paramNames = Object.keys(params);

    return [
      {
        dataType: DataType.NodeId,
        value: this.scriptId,
      },
      {
        dataType: DataType.NodeId,
        value: this.scriptBaseId,
      },
      {
        dataType: DataType.String,
        arrayType: VariantArrayType.Array,
        value: paramNames,
      },
      {
        dataType: DataType.Variant,
        arrayType: VariantArrayType.Array,
        value: paramNames.map(key => params[key]),
      },
    ];
  }

  /**
   * Returns the error message logged if running the script fails.
   * @param {AtviseFile} file The processed file.
   * @return {string} The resulting error message.
   */
  processErrorMessage(file) {
    return `Error running script ${this.scriptId} for ${file.relative}`;
  }

  /**
   * Calls the script specified in {@link CallScriptStream#scriptId}. If the script does not exist
   * but could be imported by running `atscm import` a special status description is returned.
   * @param {vinyl~File} file The file being processed.
   * @param {function(err: Error, status: node-opcua~StatusCodes, success: function)} handleErrors
   * The error handler to call. See {@link QueueStream#processChunk} for details.
   */
  processChunk(file, handleErrors) {
    super.processChunk(file, (err, status, success) => {
      const processedStatus = status;

      const atscmScript = this.scriptId.value.match(/SERVERSCRIPTS\.atscm\.(.*)/);
      if (status === StatusCodes.BadMethodInvalid && atscmScript) {
        processedStatus.description = `The '${atscmScript[1]}' script does not exist.
- Did you forget to run 'atscm import'?`;
      }

      handleErrors(err, processedStatus, success);
    });
  }
}

