      const lines = ['first line', 'second'];

      return expect(
        cb =>
          transformer.transformFromFilesystem(
            new File({
              path: './src/test/Test.script/Test.js',
              contents: Buffer.from(lines.join(EOL)),
            }),
            'utf8',
